css:
@-moz-document domain("youtube.com") {
:root {
    --theme-color-dark: #004a3f;
    --theme-color: #007769;
    --theme-color-light: #48a999;
}

/* Volume slider */
.ytp-big-mode,
.ytp-volume-slider-handle:before,
.ytp-volume-slider-track {
    background: var(--theme-color);
    box-shadow: 0px 0px 5px rgba(93, 255, 44, .055);
}

/*Volume slider background track*/
.ytp-volume-slider-handle:after {
    left: 4px;
    background: rgba(255, 255, 255, .05);
}

/*Volume slider handle */
.ytp-volume-slider-handle {
    background: var(--theme-color);
}

/*Player quality badges*/
.ytp-settings-button.ytp-hd-quality-badge:after,
.ytp-settings-button.ytp-4k-quality-badge:after,
.ytp-settings-button.ytp-5k-quality-badge:after,
.ytp-settings-button.ytp-8k-quality-badge:after {
    background-color: var(--theme-color) !important;
}

/*Live Now badge, Player settings quality text */
.badge-style-type-live-now.style-scope.ytd-badge-supported-renderer,
.ytp-swatch-color {
    color: var(--theme-color) !important;
    border-color: var(--theme-color);
}

/*Live icon*/
.yt-icon {
    color: var(--theme-color) !important;
}

/*Sub-button text*/
paper-button.ytd-subscribe-button-renderer {
    color: var(--theme-color-light) !important;
    background-color: var(--theme-color-dark) !important;
}

/*Menu Buttons */
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
    fill: rgba(221, 81, 51, .5) !important;
}

.ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after,
.ytp-settings-button.ytp-3d-badge::after {
    background-color: rgba(221, 81, 51, .4)!important;
}

.ytp-svg-fill {
    fill: #222;
}

/*youtube logo color*/
path.ytd-topbar-logo-renderer {
    fill: var(--theme-color) !important;
}

/*subtitle and other right side player buttons*/
button.ytp-button > svg > path {
    fill: var(--theme-color) !important;
}

/*play button and other left side player buttons*/
path.ytp-svg-fill {
    fill: var(--theme-color) !important;
}

/* HD(720p..), (if available)*/
.ytp-settings-button.ytp-hd-quality-badge:after,
.ytp-settings-button.ytp-4k-quality-badge:after,
.ytp-settings-button.ytp-5k-quality-badge:after,
.ytp-settings-button.ytp-8k-quality-badge:after {
    background-color: #af0000;
    fill: var(--theme-color);
}

/* Background -> offer, notes, subtitles, etc. */
.ytp-panelpopup {
    background: none repeat scroll 0% 0% var(--theme-color);
    color: #aaa;
}

/*TEXT z panel pop-up*/
.ytp-menu-label-secondary {
    color: #aaa;
}

/*autoplay*/
paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
    background: none repeat scroll 0% 0% var(--theme-color);
}
}
