css:
@-moz-document url-prefix("https://www.exclusivelygames.com/forum/"),
url-prefix("http://www.exclusivelygames.com/forum/") {
    /* Put your wallpaper in the body tag if you want, site doesn't do much with this section. */
    body {
        background: #111;
    }

    .topic-list-container .topic-list .topic-item .cell-topic .topic-info {
        color: #fff;
    }

    .page-title-widget .module-title,
    .page-title-widget .module-title h1 {
        color: #ee2461;
    }

    .b-top-menu__background {
        background: none 0 0, #444;
    }

    .b-top-menu__item a.b-top-menu__item-link {
        color: #fff;
    }

    .b-comp-menu-dropdown__trigger--headerbar {
        color: #fff;
    }

    /* thread details in topics section */
    .posts-count,
    .topics-count,
    .views-count,
    .votes-count,
    .lastpost-by {
        color: #0797e7;
    }

    .topic-list-container .topic-list .post-date {
        color: #0797e7;
    }

    .forum-list-container .category-header .category {
        color: #0797e7 !important;
    }
    .forum-list-container .category-header {
        background: none 0 0, #444;
    }

    .list-footer {
        background: none 0 0, #444;
        font-weight: bold;
    }

    .forum-list-container .forum-item .post-date,
    .forum-list-container .subforum-list .post-date,
    .blogmember-list .list-container .post-date,
    .sg-groups-list .sg-groups-list-container .post-date {
        color: #0797e7;
    }

    .b-post-sm__date {
        color: #0797e7;
    }

    /*post stylings*/
    .view-mode .widget-tabs .widget-tabs-nav {
        height: 30px;
        min-height: 30px !important;
        line-height: 30px !important;
        border: 1px solid #9bc4db !important;
        margin: 0 0 -1px 0 !important;
        padding: 0 !important;
        position: relative !important;
        background: none 0 0, #444 !important;
        text-transform: uppercase;
        border-radius: 3px 3px 0 0 !important;
    }

    .forum-list-container .forum-item,
    .forum-list-container .subforum-list,
    .blogmember-list .list-container .list-item,
    .sg-groups-list .sg-groups-list-container .list-item {
        background: #222;
    }

    .b-post {
        background: #222;
        color: #fff;
    }
    .b-userinfo__details {
        color: #fff;
    }
    .b-post__body {
        background: #333;
    }

    .b-post__arrow {
        border-right: 10px solid #333;
        border-top: 6px solid transparent;
        border-bottom: 6px solid transparent;
    }

    .b-post--first .b-post__arrow {
        border-right-color: #333;
    }

    .b-post--first .b-post__body {
        background: none 0 0, #333;
    }

    .bbcode_container .bbcode_quote {
        background: #444;
    }

    .quote_container {

        background: #444;
    }

    /* commenting */
    .b-comment-entry {
        background: #444;
        border: none;
    }

    .b-comment-entry__textbox {
        background: #333;
    }

    .b-comments__header,
    .b-comments__footer {
        background: #444;
        border: none;
    }

    /* Latest Activity */
    .conversation-content-widget .list-container.stream-view .list-item.conversation-starter,
    .conversation-content-widget .list-container .list-item.conversation-starter .list-item-body-wrapper {
        background: #333;
    }
    .widget-content,
    .post-content {
        color: #fff;
    }

    .conversation-list.stream-view .list-item {
        background: #333;
        color: #fff;
    }

    .conversation-content-widget .list-container .list-item .list-item-body-wrapper {
        background: #333;
    }

    /* breadcrumbs section */
    #breadcrumbs .crumb-link {
        color: #0797e7;
        font-weight: bold;
    }

    /* convo toolbar */
    .conversation-toolbar-wrapper .conversation-toolbar,
    .forum-list-container .forum-list-header {
        background: #333;
        color: #fff;
    }

    /*post navigation*/
    .widget-tabs .widget-tabs-nav li.ui-tabs-active {
        background: none 0 0, #ee2461;
    }
    .widget-tabs .widget-tabs-nav li {
        background: none 0 0, #0797e7;
        margin: .3px;
        font-weight: bold;
    }
    .widget-tabs .widget-tabs-nav li a {
        color: #fff;
    }
    .activity-stream-widget .ui-tabs-nav li a:hover,
    .profileTabs .ui-tabs-nav li a:hover,
    .conversation-content-widget .ui-tabs-nav li a:hover,
    .widget-tabs.channel-conversation-list-wrapper .ui-tabs-nav li a:hover {
        color: #fff;
        background: none 0 0, #ee2461;
    }

    .widget-tabs .widget-tabs-nav .ui-tabs-nav {
        background: none 0 0, #444 !important;
    }


    /*Widget Modules*/
    .module-title h1 {
        color: #0797e7;
    }
    .canvas-layout-container .canvas-widget {
        background: #222;
    }
    
    #widget_2, #widget_12, #widget_21 {
        background: transparent;
    }
    /*post editor*/
    body {
        color: #fff;
    }

    .b-content-entry-panel--autosave,
    .b-toolbar {
        background: none 0 0, #444;
    }
}
