css:
@-moz-document url-prefix("http://codepen.io"), url-prefix("https://codepen.io"), url-prefix("http://www.codepen.io"), url-prefix("https://www.codepen.io") {
:root {
    --bg1: #26282c;
    --bg2: #191B1F;
    --bg3: #0A0C10;
}

/* Editor */
body.editor #react-pen-footer {
    display: none;
}
body.editor .ItemTitle_text-2ho1f {
    display: flex;
}
body.editor .ItemTitle_by-24dz8 {
    margin-left: 15px;
}
body.editor #editable-title-span {
    font-size: 14px
}
.editor button:active {
    transform: translate(0);
}
body.editor .UserMenu_userMenuButton-38sVN {
    margin: 0;
    width: 30px;
    height: 30px;
    border-radius: 0;
}
body.editor .UserMenu_userMenuButton-38sVN::after {
    margin-top: -4px;
}
body.editor .UserMenu_userMenu-3E8E- {
    top: 30px;
}
body.editor #main-header {
    height: 30px;
    background: var(--bg1);
}
body.editor #main-header .header-router-wrap {
    box-shadow: none;
}
body.editor #main-header .item-title-text {
    display: flex;
}
body.editor #main-header .item-title-text h1 {
    font-size: 14px;
    margin: 0;
}
body.editor #main-header .by {
    margin-left: 15px;
}
body.editor #main-header .Logo_small-3i6l6 {
    width: 20px;
}
body.editor .Logo_root-3XNFn {
    height: 20px;
}
body.editor #main-header .editor-actions button {
    border: none;
    height: 30px;
    padding: 0 15px;
    margin: 0;
    border-radius: 0;
    background: transparent;
}
body.editor #main-header .editor-actions button:hover {
    background: rgba(255,255,255,0.1)
}
body.editor #main-header .multi-button {
    margin-right: 0;
}
body.editor #resizer {
    background: var(--bg1);
    width: 8px;
}
body.editor .CodeMirror-simplescroll-vertical {
    width: 4px;
}
body.editor .CodeMirror-simplescroll-vertical div {
    border: none;
    background: rgba(255,255,255,0.1);
}
body.editor .CodeMirror-sizer {
    border-right-width: 4 !important;
    padding-right: 0 !important;
}
body.editor .top-boxes.editor-parent .html-editor-resizer {
    display: none;
}
body.editor .powers {
    padding: 0;
    height: 20px;
    border: none;
}
body.editor .code-wrap {
    height: calc(100% - 20px);
}
body.editor .powers .editor-actions-left {
    padding: 0;
}
body.editor .powers .editor-actions-left {
    display: flex;
    align-items: center;
}
body.editor .powers .editor-actions-left .button {
    border-radius: 0;
    background: var(--bg2);
    height: 19px;
    margin-right: 5px;
}
body.editor .powers h2 {
    font-size: 10px;
}
body.editor .powers .editor-dropdown-button {
    background: var(--bg2);
    height: 19px;
    position: relative;
    top: -1px;
    border-radius: 0;
}
body.editor .top-boxes .editor-resizer {
    background: var(--bg1) !important;
}
body.editor .css-editor-title.scss::after {
    font-size: 10px;
    bottom: 0;
}
body.editor {
    padding-bottom: 0;
}
#item-settings-modal {
    padding: 0;
    color: #fff;
}
#item-settings-modal {
    background: var(--bg2);
}
#item-settings-modal h4, #item-settings-modal p, #item-settings-modal label {
    color: #fff;
}
#item-settings-modal input:not(.green), #item-settings-modal textarea, #item-settings-modal select {
    background: var(--bg2) !important;
    border: 3px solid var(--bg3) !important;
    color: #fff;
}
#item-settings-modal .resource-search-bar {
    background: transparent;
}
#item-settings-modal .resource-search-bar .icon.icon-mag {
    position: absolute;
    z-index: 1;
}
#item-settings-modal .resource-search-bar input {
    padding-left: 40px
}
#item-settings-modal .button.green {
    color: #fff !important;
    font-weight: bold !important;
    text-shadow: 0 2px 3px rgba(0,0,0,0.3);
}
body.editor .external-resource-url-row .external-url-option, body.editor .npm-row .external-url-option {
    background: var(--bg3)
}
body.editor .external-resource-url-row .external-url-option svg, body.editor .npm-row .external-url-option svg {
    fill: #fff;
}
}

style_setting_options.value:
.CodeMirror-simplescroll-horizontal div, .CodeMirror-simplescroll-vertical div {
    background: #45474B;
}
.CodeMirror-simplescroll-vertical div {
    border-right: none;
}
.CodeMirror-simplescroll-vertical {
    width: 3px;
}
body.editor .box, .editor .top-boxes, body.editor .CodeMirror-gutter-wrapper, body.project .editor-pane, body.project .editor {
    background: var(--bg1);
}
