/* ===== Chats ===== */

.ya-chat-widget_theme_dark.ya-chat-widget_desktop, .ya-chat-widget_theme_light.ya-chat-widget_desktop {box-shadow: 0 -2px 6px rgba(0,0,0,.03), 0 6px 18px rgba(0,0,0,.13);}
.ya-chat-widget_theme_dark.ya-chat-widget_desktop.ya-chat-widget_theme_light, .ya-chat-widget_theme_light.ya-chat-widget_desktop.ya-chat-widget_theme_light {background-color: hsl(210,6%,17%);}
.ya-chat-button_theme_light .ya-chat-button__container {background-color: hsl(210,6%,17%);}
.ya-chat-badge__count {background-color: #ff3838;}

.ya-chat-icon {color: hsl(210,100%,98%);}
.ya-chat-button_theme_light .ya-chat-button__text {color: hsl(210,100%,98%);}

.ya-chat-popup:before {background: hsl(210,6%,17%);}
.ya-chat-close__button-box:hover {background-color: hsl(210,6%,24%);}
.ya-chat-close__button {filter: invert(1);}

.ya-chat-error {color: #eee;}
.ya-chat-error__button {color: hsl(210,75%,70%);}

.ya-chat-loader__spinner {border-color: hsl(180,50%,55%) transparent transparent hsl(180,50%,55%);}

/* ===== Keyboard ===== */

.keyboard {font-family: 'YS Text', Arial, Helvetica, sans-serif;}
body .keyboard__popup {background: #333;}
body .keyboard__popup .keyboard__lang {color: #eee;}
.keyboard-popup {background: #222; color: #eee;}
.keyboard__popup:after {background-color: #333;}
.keyboard__key {background-color: #333;}
.keyboard-popup__close, .keyboard__key-image, .keyboard__lang-tick {filter: invert(1);}
.keyboard__key_state_marked {background-color: #383838;}
.keyboard__key_state_suppressed {background-color: #444;}
.keyboard__switcher {color: #eee;}
.keyboard__switcher:hover {color: #e85253;}
.dropdown-menu_action_open .keyboard__switcher {background: #333;}

/* ===== Footer ===== */

.serp-footer__search-settings {border-top-color: #333;}
.serp-footer .serp-footer__search-settings .link {color: #eee;}
.serp-footer__search-settings .lang-switcher .link:before, .serp-footer__search-settings .serp-footer__link:before, .serp-footer__search-settings .serp-footer__region .link:before {filter: invert(1);}

.serp-footer__main {background-color: #222; background-image: linear-gradient(0deg,#383e4d 0,#222 100%);}
.serp-footer__promo_gradient_blue::after {background-image: linear-gradient(-134deg,#00138f 0,#65128f 100%);}
.serp-footer__promo_gradient_red::after {background-image: linear-gradient(-134deg,#4d00af 0,#af3232 100%);}

.serp-footer__main_theme_Light {color: #eee;}
.serp-footer__main_theme_Light .link.link.link {color: #eee;}
.serp-footer__main_theme_Light .copyright.copyright {color: #eee !important;}
