css:
@-moz-document domain("egosoft.com") {
body {
  padding-top: 0; }

blockquote {
  border-left: 2px solid #7DBD51; }

a {
  color: #93ABC4; }

hr.divider {
  background: transparent; }

#wrap {
  background-color: #252525;
  border-radius: 0; }

#page-header {
  margin: -15px; }

.headerbar,
.forumbg {
  border-radius: 0; }

.headerbar {
  margin-bottom: .5em;
  border-bottom: 2px solid #59839E;
  background-size: cover;
  background-attachment: fixed;
  background-position-y: -112px;
  background-image: url(https://www.egosoft.com/games/x4/screenshots/x4_screen_074.png); }
  .headerbar .banner-header {
    height: 230px;
    overflow: hidden; }
    .headerbar .banner-header .header-banner {
      opacity: .5;
      position: relative;
      left: 20%;
      border-radius: 0; }

.navbar {
  background-color: transparent; }

.action-bar {
  margin: 0 0 1em; }

[title='Game Registration'] {
  color: #ff7c7c; }

.dropdown .pointer {
  border-color: #3390AD transparent; }
  .dropdown .pointer .pointer-inner {
    border-color: #101010 transparent; }

.dropdown .dropdown-contents {
  border-color: #3390AD; }

.postprofile {
  border: 0;
  padding: .5em;
  margin-top: 2em;
  background-color: #1f1f1f;
  -webkit-transform: perspective(1000px) rotateY(30deg);
          transform: perspective(1000px) rotateY(30deg);
  -webkit-transition: -webkit-transform 0.4s ease;
  transition: -webkit-transform 0.4s ease;
  -o-transition: transform 0.4s ease;
  transition: transform 0.4s ease;
  transition: transform 0.4s ease, -webkit-transform 0.4s ease;
  font-size: 1.4em; }
  .postprofile:hover {
    -webkit-transform: perspective(1000px) rotateY(0deg);
            transform: perspective(1000px) rotateY(0deg); }
    .postprofile:hover dd.profile-rank::after {
      width: 100%; }
  .postprofile::after {
    content: '';
    position: absolute;
    height: 2px;
    width: 1em;
    background-color: #7d7d7d;
    top: 0;
    left: 0; }
  .postprofile::before {
    content: '';
    position: absolute;
    height: 2em;
    width: 2px;
    background-color: #7d7d7d;
    top: 0;
    left: 0; }
  .postprofile a {
    color: #d4a38c; }
  .postprofile dd {
    line-height: 2em;
    padding: 8px;
    margin-left: 0; }
    .postprofile dd.profile-posts, .postprofile dd.profile-rank, .postprofile dd.profile-joined {
      background-color: transparent;
      -webkit-box-sizing: border-box;
              box-sizing: border-box;
      border: 1px solid;
      border-color: transparent;
      -webkit-transition: background-color 0.4s ease, border-color 0.4s ease;
      -o-transition: background-color 0.4s ease, border-color 0.4s ease;
      transition: background-color 0.4s ease, border-color 0.4s ease; }
    .postprofile dd.profile-posts:hover, .postprofile dd.profile-joined:hover {
      border-color: rgba(51, 144, 173, 0.7);
      background-color: rgba(51, 144, 173, 0.4); }
    .postprofile dd.profile-rank {
      position: relative;
      -webkit-transition-delay: .2s;
           -o-transition-delay: .2s;
              transition-delay: .2s; }
      .postprofile dd.profile-rank::after {
        content: '';
        position: absolute;
        height: 100%;
        z-index: -1;
        width: 0;
        border: 1px solid #BB5020;
        background-color: transparent;
        -webkit-box-shadow: 5px 0 20px 0 #f13737;
                box-shadow: 5px 0 20px 0 #f13737;
        -webkit-box-sizing: border-box;
                box-sizing: border-box;
        top: 0;
        left: 0;
        -webkit-transition: width 0.4s ease;
        -o-transition: width 0.4s ease;
        transition: width 0.4s ease; }
    .postprofile dd.profile-joined:hover {
      color: white; }
  .postprofile dt {
    padding-bottom: 8px; }
    .postprofile dt.no-profile-rank {
      border-bottom: 2px solid #313131; }

.postbody h3 > a {
  padding: .2em;
  -webkit-transition: opacity 0.4s ease;
  -o-transition: opacity 0.4s ease;
  transition: opacity 0.4s ease;
  border-radius: .2em;
  border: 1px solid;
  opacity: .5; }
  .postbody h3 > a:hover {
    text-decoration: none;
    opacity: 1; }

.postbody p.author {
  border-bottom: 1px dashed; }

.content {
  color: #b9bdc1; }

.post {
  border-top: 2px solid rgba(125, 189, 81, 0.8);
  border-radius: 0; }
  .post .content {
    font-size: 1.5em; }

.post.bg2 {
  background-color: rgba(15, 17, 18, 0.6); }

h2.topic-title,
h2.forum-title {
  background-image: -webkit-gradient(linear, left top, right top, from(rgba(89, 131, 158, 0.3)), color-stop(25%, rgba(89, 131, 158, 0.8)), color-stop(50%, rgba(89, 131, 158, 0.5)), color-stop(75%, rgba(89, 131, 158, 0.8)), to(rgba(89, 131, 158, 0.3)));
  background-image: -webkit-linear-gradient(left, rgba(89, 131, 158, 0.3) 0%, rgba(89, 131, 158, 0.8) 25%, rgba(89, 131, 158, 0.5) 50%, rgba(89, 131, 158, 0.8) 75%, rgba(89, 131, 158, 0.3) 100%);
  background-image: -o-linear-gradient(left, rgba(89, 131, 158, 0.3) 0%, rgba(89, 131, 158, 0.8) 25%, rgba(89, 131, 158, 0.5) 50%, rgba(89, 131, 158, 0.8) 75%, rgba(89, 131, 158, 0.3) 100%);
  background-image: linear-gradient(to right, rgba(89, 131, 158, 0.3) 0%, rgba(89, 131, 158, 0.8) 25%, rgba(89, 131, 158, 0.5) 50%, rgba(89, 131, 158, 0.8) 75%, rgba(89, 131, 158, 0.3) 100%);
  margin: .5em auto;
  text-align: center;
  padding: .5em;
  margin-left: -15px;
  margin-right: -15px;
  border-top: 1px solid #59839E;
  border-bottom: 1px solid #59839E; }

.topic-title > a,
.forum-title > a {
  color: #a8d4f0; }
  .topic-title > a:hover,
  .forum-title > a:hover {
    text-decoration: none; }

ul.topiclist .row:hover {
  background-color: #1d2617; }

.jumpbox-return > a {
  color: #EFF3F7; }

.search-box .inputbox {
  border-color: transparent;
  -webkit-transition: opacity 0.4s ease;
  -o-transition: opacity 0.4s ease;
  transition: opacity 0.4s ease;
  opacity: .5; }
  .search-box .inputbox:hover, .search-box .inputbox:focus {
    opacity: 1;
    border-color: #59839E; }

.button {
  border-color: transparent;
  -webkit-box-shadow: none;
          box-shadow: none;
  background: rgba(53, 53, 53, 0.75);
  color: #EFF3F7;
  -webkit-transition: border-color 0.4s ease;
  -o-transition: border-color 0.4s ease;
  transition: border-color 0.4s ease; }
  .button:hover {
    border-color: #59839E;
    background: rgba(53, 53, 53, 0.75); }
  .button:focus {
    border-color: #59839E;
    background: rgba(53, 53, 53, 0.75); }
  .button .icon {
    color: #EFF3F7; }

.pagination li.active span {
  background: transparent;
  border-color: #4692BF; }
}
