css:
@-moz-document domain(mises.org) {
.main-container{
  background-image: url(https://i.imgur.com/78ruuXA.png);
  background-size: 100vw;
  background-color: black;
  background-repeat: no-repeat;
}

.navbar-header{
  background-color: #111;
}

.main-content{
  background-color: #222;
  color: #eee
}

.main-content a, .main-content span{
  color: #50a2e5 !important;
}

/*fix for some random tags having background-color set to the background*/
.main-content span span, .main-content a em, #profile-accordion .panel{
  background-color: #222 !important;
}

.page-header{
  color: #50a2e5;
}

ul.pagination > li > a{
  background-color: #333;
}

ul.pagination > li.active > a{
  background-color: #151515;
}

ul.pagination > li.active > a:hover{
  background-color: #151515;
}

.main-content blockquote{
  background-color: #333;
  color: #50a2e5;
  border-left: 5px solid #50a2e5
}

.gsc-results-wrapper-overlay.gsc-results-wrapper-visible,
.gsc-webResult.gsc-result, .gsc-webResult.gsc-result:hover{
  background-color: #111;
  border: none;
}

.gs-bidi-start-align.gs-snippet{
  color: #aaa
}

.sidebar .panel:not([class*="ds-1col"]){
  background-color: #222 !important;
  color: white !important;
}

.region[class*=region-bottom]{
  background-color: #333;
}

.region[class*=region-bottom] a{
  color: #50a2e5 !important;
}

.webform-component-fieldset, .view-mode-block_teaser_term .panel-body, .view-mode-block_teaser_term .panel-footer, .bg-gray, .resource-item.odd, .main-content .view-mode-video_block_teaser, .group-footnotes{
  background-color: #333;
}

.group-teaser.panel-body p{
  color: white;
}

.content-bottom .panel-body p, .bottom-blocks .panel-body p{
  color: #aaa !important;
}

.gsc-modal-background-image.gsc-modal-background-image-visible{
  background-color: #222;
}

#causeview_iframe iframe{
  color: white !important;
  background-color: #fff ;
}

.list-group-item{
  background-color: #333;
  border: 1px solid #111;
}

.bg-gray div h5{
  color: #eee;
}

.bg-gray h2.teaser-title{
  color: #50a2e5;
}

#block-views-blog-block-5 div.panel-body{
  background-color: #333;
}

.view-homepage-hero{
  background: #333;
}

.bef-checkboxes{
  color: #eee;
}


}
