css:
@-moz-document domain(alluc.uno) {
body,#startpage,#navmenuicon #popmenu,.blockx,.block-source,
#resultlist.trends,#searchrow,#resultlist,fieldset,#resultlistdetails {background:#111;color:#ccc}

img#menuicon  {
  width: 40px;height: 40px;
  background: url(https://raw.githubusercontent.com/satoru8/Icons/master/menu24.png) center/cover no-repeat;
  padding-left: 40px; /* Equal to width of new image */
    
}

#navmenuicon #popmenu {border-color:purple}
.block-source,#player-and-details-2 {border:none}

#searchrow {border:none;border-bottom:1px solid purple;padding-bottom:10px}
#searchmenu {border-color:purple;padding-bottom:40px}
#searchmenu li a{color:#eee}
#resultlist.trends {border:1px solid purple;}
#search_btn{top:7px !important}
input.search_field {
  color:#fff; padding: 20px;border-radius: 20px;
  border:none;box-shadow: 0 0 15px 1px purple
}
.typeahead {background-color: #222;color:#ccc}
.typeahead:focus {border: none}
#startpage .btn { margin-top:30px;padding: .6em 2em .6em}
body .btn {
  background: #222;color:#fff;text-shadow: none;
  border:none;box-shadow: 0 0 5px purple;
  border-radius: 20px;margin-top:30px;
  padding: .6em 2em .6em
}
.btn:hover {color: #fff;box-shadow:0 0 10px purple}
.btn:active {transform: scale(.95)}

.mgline,#sidebar,.mgbox {display:none}
.pagination ul {box-shadow: none}
.headline {text-align: center}

#resultlist {width:83%}
#resultlist > div div.resblock {
  background: #000;border:none;transition: .1s ease;
  box-shadow: 0 0 4px purple;border-radius:4px
}
#resultlist > div div.resblock:active {transform: scale(.95);transition: .1s ease}
#resultlist > div div.resblock .clickable:hover {
  box-shadow:0 0 10px 1px purple
}
.imgshadow,.blockx > div.i img {border-radius:6px;box-shadow: 0 0 5px purple}
#resultfilter fieldset{border:1px solid purple;left:4px;text-align: center}
legend{left:4.5em;right:auto}

#resultlist h1{text-align: center}

span.beforetrends {color:#eee}

#bookmark-this:hover {
    left:-80px;transition:all .6s ease;
    background: #000;box-shadow:0 0 10px 1px purple;
    background-image: none;color:purple;
    text-decoration: none;
}
#bookmark-this {
    left:-112px;transition:all .6s ease;
    background: #222;box-shadow:0 0 5px pink;
    background-image: none;border-radius:8px;
    color: #bbb;z-index:1;
    padding: 15px 15px 10px;
}
}
