css:
@-moz-document domain("4channel.org"), domain("boards.4channel.org") {
body,

div.opContainer {
    min-height: 100px !important;
    margin-bottom: 3px !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.2);
}

div.op {
    background: rgb(37, 38, 42);
    margin-top: 0px !important;
    margin-bottom: 5px !important;
    padding-bottom: 3px !important;
}

.boxcontent,
.top-box,
.left-box,
.right-box,
#recent-images a {
    background: rgb(37, 38, 42);
}

div.reply,
.catalog-mode:root .board,
.inline .post {
    background: rgb(37, 38, 42);
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.2);
}

#ft li,
.right-box,
.boxcontent,
.top-box,
.left-box,
#recent-images .boxcontent,
#qr select,
#archive-board-select,
select {
    border: none !important;
}




img:hover {
    opacity: 0.3 !important;
    transition: 0.4s ease-in-out !important;
}

/* unscrollable horizontally, fixes stupid header issues */
body {
    overflow-x: hidden !important;
}


div.thread {
    max-width: 1200px !important;
    margin: 0 auto !important;
}




img {
    border: 0px !important;
    border-color: var(--alt-bg) !important;
    border-style: solid !important;
}




body,
.post.reply,
.opContainer,
#board-list,
#custom-board-list,
#menu.dialog,
.entry,
.yotsuba-b:root .suboption-list > div:last-of-type,
option,
#qr textarea.field,
.field,
#index-search.field,
#updater.dialog,
#thread-stats.dialog,
#thread-stats,
.boardSubtitle,
#thread-watcher,
div.post div.postInfo span.nameBlock span.postertrip,
div.post div.postInfo span.nameBlock span.name,
div.post div.postInfo span.subject,
#qr:not(.captcha-open) .captcha-counter > a,
#file-n-submit > input,
#watcher-link,
.settings-link,
.expand-all-shortcut,
.qr-shortcut,
div#boardNavDesktop,
.yotsuba_b_new .backlink,
.quotelink,
div#boardNavDesktop a,
table.postForm > tbody > tr > td:first-child,
input[type="text"],
input[type="password"],
table.postForm > tbody textarea,
#recaptcha_response_field,
div#boardNavDesktopFoot a,
#qr select {
    font-family: "Open Sans", "Segoe UI" !important;
    font-weight: 600 !important;
    font-size: 13px !important;
}


hr {
    margin-top: 0px !important;
    padding-top: 0px !important;
    border: 0 !important;
}




#index-size,
#index-sort,
#index-mode {
    border: none !important;
}



div.sideArrows,
.qr-link-container,
.boardSubtitle,
.bottomCtrl.desktop,
#absbot.absBotText,
.navLinks.desktop,
.navLinks.navLinksBot.desktop {
    display: none !important;
}

div.center:nth-child(16),
div.center:nth-child(6) {
    display: none !important;
}

div.center:nth-child(4) {
    border: none !important;
}

.yotsuba:root .hide-post-button,
.yotsuba-b:root .hide-post-button {
    display: none !important;
}

#unread-line {
    border: 0!important;
    height: 1px!important;
    margin-top: -10px!important;
}

.inline {
    padding-left: 15px !important;
    padding-right: 15px !important;
    border: 0 !important;
}

#overlay {
    background: rgb(37, 38, 42);
}



:root a.backlink,
:root .backlink-container > a.backlink.deadlink,
.warning,
:root a.replylink:hover,
:root a:not(.entry):hover {
    /* background: url(http://i.imgur.com/lFuGnSO.png) center no-repeat !important; */
    /* content: url('http://i.imgur.com/lFuGnSO.png') !important;*/
}

:root a.backlink {
    margin-left: 5px !important;
}

.postInfo > input,
a.hide-reply-button > span {
    display: none !important;
}

.fileText a {
    text-decoration: none;
}

#qp,
kbd {
    border: 0 !important;
    box-shadow: none !important;
}

.close.fa.fa-times {
    display: none !important;
}

#qr.dialog.has-captcha.autohide {
    border: 0px solid #6b454f !important;
}

#qr.autohide:not(:hover):not(.has-focus) {
    max-height: 21px;
    transition: max-height 0.7s ease 1s;
}

#qr > form {
    padding-top: 4px!important;
}

#qr {
    background: var(--header-bg) !important;
    box-shadow: 6px 6px 5px rgba(0, 0, 0, 0.3);
    border-radius: 0px !important;
    transition: all 0.2s ease 0s;
    transition: max-height 0.4s ease;
    z-index: 1!important;
}

#qr > div,
#qr {
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.2);
}

#qr label input[type="checkbox"] {
    opacity: 0.4 !important;
}

#qr label input[type="checkbox"]:hover {
    opacity: 0.7 !important;
}

#qr .textarea textarea {
    max-height: 184px;
    transition: none!important;
}

.field,
#qr option:hover,
#dump-button:hover,
:root .field:hover,
#menu .entry.focused,
:root .field:focus,
#file-n-submit > input {
    background: var(--header-bg) !important;
    border-color: var(--header-bg) !important;
    color: var(--main-text) !important;
}

#qr form {
    bottom: 10px !important;
    right: 0% !important;
    width: 300px !important;
    border: 1px var(--header-bg) !important;
    padding: 0px !important;
    padding-top: 0px !important;
    padding-bottom: 0px !important;
    background: rgb(37, 38, 42);
    border-color: var(--post-bg) !important;
    border-radius: 0px !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.2);
}

#dump-button {
    background: rgb(37, 38, 42);
    border-color: var(--post-bg) !important;
    color: var(--main-text) !important;
}

#qr .field,
.field {
    background: rgb(37, 38, 42);
    border: 1px var(--header-bg) !important;
    color: var(--main-text) !important;
}

.replyContainer {
    padding: 0px !important;
}



.file:hover .fileText,
.file.image-expanded .fileText {
    opacity: 1 !important;
    max-height: 22px !important;
}


.fileText {
    margin-top: 0px !important;
    margin-left: -3px !important;
    padding: 4px !important;
    max-width: 440px !important;
}



div.post div.postInfo span.nameBlock span.name,
div.post div.postInfo span.nameBlock span.postertrip,
.nameBlock {
    padding: 4px !important;
    margin-left: -3px !important;
}

.warning {
    padding: 4px !important;
    margin-left: -3px !important;
}


.container {
    padding: 4px !important;
    margin-left: -3px !important;
}





.opContainer {
    background: rgb(37, 38, 42);
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
}

a.fileThumb,
div.post div.file .fileThumb img {
    float: left !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.2);
}


.board {
    margin: 0 auto !important;
    max-width: 1500px !important;
    margin-top: 20px !important;
}

.catalog-mode:root .board {
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.2);
}

.navLinks {
    background: rgb(37, 38, 42);
    padding: 0px !important;
    margin: 0 auto !important;
    max-width: 1540px !important;
    margin-top: 60px !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.2);
}

.catalog-mode:root .board {
    padding: 25px !important;
}

.catalog-stats {
    background: rgb(37, 38, 42);
    padding: 5px !important;
}
.catalog-thread > .subject {
    background: var(--header-bg) !important;
    padding: 5px !important;
}
.catalog-thread > .comment {
    background: var(--header-bg) !important;
    padding: 5px !important;
}



.catalog-reply,
.catalog-post.catalog-post:hover {
    background: var(--alt-bg) !important;
    border: none !important;
}

.catalog-post.catalog-post:hover {
    box-shadow: 2px 2px 6px 8px rgba(0, 0, 0, 0.2) !important;
}

div.navLinks {
    padding: 5px !important;
}

.bottomlink,
.archlistlink,
.indexlink {
    display: none !important;
}

.brackets-wrap::after,
.brackets-wrap::before {
    content: "" !important;
}
#index-search,
#index-size,
#index-sort,
#index-mode {
    background: rgb(37, 38, 42);
}

span.postNum.desktop a[title="Link to this post"] {
    font-size: 0px !important;
}

span.postNum.desktop a[title="Link to this post"]:after {
    content: "#" !important;
    font-size: 12px !important;
}

.centered-links:root #custom-board-list {
    top: 13px !important;
    padding: 10px
}
}
