css:
@-moz-document regexp("https://blog.csdn.net/\\w+?/article/details/\\d+/?(?:\\?.+)?") {
#csdn-toolbar,
.article-info-box,
.hide-article-box,
.blog-expert-recommend-box,
.recommend-item-box.type_hot_word,
.recommend-ad-box,
.isGreatIcon,
.tool-box,
.meau-gotop-box,
.recommend-end-box {
    display: none!important;
}

body {
    min-width: unset;
}

#article_content {
    height: auto!important;
}

#mainBox {
    margin-left: auto;
    margin-right: auto;
    width: calc(100% - 4rem);
}

#mainBox > main {
    display: block!important;
    float: none;
    width: 100%;
}

#mainBox > aside {
    position: unset!important;
    float: none;
    display: none!important;
}

.pulllog-box {
    display: none!important;
}

.recommend-box {
    display: flex;
    flex-wrap: wrap;
    background: white;
    padding-top: 3rem;
    position: relative;
}

.recommend-box::before {
    content: "相关文章";
    display: block;
    position: absolute;
    top: 1rem;
    font-size: 1.3rem;
    left: 1rem;
}

.recommend-item-box {
    width: 50%;
    max-width: 25rem;
}

.recommend-item-box::before {
    display: none;
}

.recommend-item-box .content,
.recommend-item-box h4 {
    width: 100%!important;
}

.blog_title_box.oneline {
    display: none;
}
}
