@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.org.ru")
{

body { cursor: default; color: #999999;}
img {display: none;}
table {font-family: tahoma; font-size: 12px;}
p.vbig { font-family: arial, verdana; font-weight: normal; font-size: 3em; margin: 1em; color: #C25025; }
a { text-decoration: none; cursor: pointer; color: #0033ff; }
a:hover { color: #ff3300; }
td.dat { border: 1px #E2DEB1 dashed; padding: 5px; background-color: #FFFBD2; font-family: verdana, tahoma; font-size: 11px; padding: 1.3em; color: #000000; }
td.dat:hover { border: 1px #E2DEB1 solid; }
span.small { display: none; }
textarea { width: 100%; }
hr { display: none; }

}
