css:
@-moz-document domain("somtoday.nl") {
.stpanel {
    background: url(https://wallpapercave.com/wp/8hIYog1.jpg) no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
.logo {
    display: block;
    background-image: url(https://www.clker.com/cliparts/8/e/4/0/15440163891054267158galaxy%20goed.med.png);
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    width: 140px;
    height: 28px;
    margin: 10px 20px 10px 0;
}
#header-wrapper {
    background: url(http://jmd.im/wp-content/uploads/2017/06/blackiii_thumbnail.jpg) no-repeat center center fixed;
    background-image: url(http://jmd.im/wp-content/uploads/2017/06/blackiii_thumbnail.jpg);
    background-position-x: center;
    background-position-y: center;
    background-size: cover;
    background-attachment: fixed;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
}
#header-wrapper, #main-menu-wrapper, #content-wrapper {
    padding-left: 28px;
}
body {
    line-height: 1;
    color: #2b2b2b;
    font-size: 12px;
    font-family: Open Sans,Arial,Helvetica,sans-serif;
    overflow-y: scroll;
    overflow-x: hidden;
    background: url(https://wallpapercave.com/wp/8hIYog1.jpg) no-repeat center center fixed;
    background-image: url(https://wallpapercave.com/wp/8hIYog1.jpg);
    background-position-x: center;
    background-position-y: center;
    background-size: cover;
    background-attachment: fixed;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
}
#detail-panel-wrapper {
    display: table-cell;
    padding: 0 15px;
    background: url(https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/J9H9WF0/galaxy-night-sky-animation-with-shining-light-particle-stars-flickering-on-colorful-blue-teal-pink-gradient-background_ssco1vgxe_thumbnail-full01.png) no-repeat center center fixed;
    background-image: url(https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/J9H9WF0/galaxy-night-sky-animation-with-shining-light-particle-stars-flickering-on-colorful-blue-teal-pink-gradient-background_ssco1vgxe_thumbnail-full01.png);
    background-position-x: center;
    background-position-y: center;
    background-size: cover;
    background-attachment: fixed;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
    vertical-align: top;
    min-width: 250px;
    -moz-transition: .2s;
    -webkit-transition: .2s;
    -o-transition: .2s;
    -ms-transition: .2s;
    transition: .2s;
}
.section .block {
    background: url(https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/J9H9WF0/galaxy-night-sky-animation-with-shining-light-particle-stars-flickering-on-colorful-blue-teal-pink-gradient-background_ssco1vgxe_thumbnail-full01.png) no-repeat center center fixed;
    background-image: url(https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/J9H9WF0/galaxy-night-sky-animation-with-shining-light-particle-stars-flickering-on-colorful-blue-teal-pink-gradient-background_ssco1vgxe_thumbnail-full01.png);
    background-position-x: center;
    background-position-y: center;
    background-size: cover;
    background-attachment: fixed;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
    border: 1px solid #1789db00;
    margin-bottom: 2px;
    padding: 5px;
}
a {
    text-decoration: none;
    outline: none;
    color: #fff;
    cursor: pointer;
}
.section {
    clear: both;
    overflow: hidden;
    line-height: 15px;
    color: #dedede;
}
#main-menu-bottom {
    height: 5px;
    background-color: #8730ff;
}
#main-menu a.active, #main-menu a.active:hover {
    color: #ffffff;
    background: #8533f5;
    text-decoration: none;
    cursor: pointer;
}
#main-menu a:hover {
    color: #fffffe;
    background: #c200ff;
}
#main-menu a {
    display: block;
    float: left;
    height: 30px;
    padding: 7px 21px 0 11px;
    margin-right: 4px;
    font-size: 13.5px;
    color: #fff;
    background-color: #6b00ff73;
}
#master-panel {
    display: table-cell;
    background: url(https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/J9H9WF0/galaxy-night-sky-animation-with-shining-light-particle-stars-flickering-on-colorful-blue-teal-pink-gradient-background_ssco1vgxe_thumbnail-full01.png) no-repeat center center fixed;
    background-image: url(https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/J9H9WF0/galaxy-night-sky-animation-with-shining-light-particle-stars-flickering-on-colorful-blue-teal-pink-gradient-background_ssco1vgxe_thumbnail-full01.png);
    background-position-x: center;
    background-position-y: center;
    background-size: cover;
    background-attachment: fixed;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
    position: relative;
    vertical-align: top;
    padding-top: 30px;
    -moz-transition: width .3s;
    -webkit-transition: width .3s;
    -o-transition: width .3s;
    -ms-transition: width .3s;
    transition: width .3s;
}
.m-element, div.m-wrapperTable table tr td {
    padding: 14px 7px;
    margin-top: -1px;
    border: 1px solid #ececec00;
    border-left-width: 0;
    border-right-width: 0;
    overflow: hidden;
    cursor: pointer;
}
.m-wrapper.active {
    margin-right: -5px;
    padding-right: 5px;
    background: #f1f1f1;
    background: -moz-linear-gradient(left, #fff 0, #f1f1f1 100%);
    background: -webkit-gradient(linear, left top, right top, color-stop(0, #ff0a0a), color-stop(100%, #f1f1f1));
    background: -webkit-linear-gradient(left, #fff 0, #f1f1f1 100%);
    background: -o-linear-gradient(left, #fff 0, #f1f1f1 100%);
    background: -ms-linear-gradient(left, #fff 0, #f1f1f1 100%);
    background: linear-gradient(to right, #ff052a 0, #5440ff 100%);
}
.afspraak {
    position: absolute;
    left: 5px;
    right: 5px;
    background-color: #c5073d00;
    margin: 2px;
    text-align: left;
}
.afspraak .toekenning {
    background-color: #c5073d00;
    margin-left: 3px;
    height: 100%;
}
.dayTitle {
    padding: 5px;
    border-bottom: solid 3px #c5073d;
    background: #c5073d;
}
.roster-table-header .day .huiswerk-items {
    background-color: #c5073d;
    min-height: 100px;
    height: 100%;
    padding-left: 3px;
}
.pauze {
    width: 960px;
    height: 18px !important;
    color: #009dff;
    background-color: #c5073d;
    line-height: 26px;
    vertical-align: middle;
}
.roster-table-content .hours .hour {
    width: 960px;
    position: relative;
    height: 98px;
    border-top: solid 1px #c5073d;
    border-bottom: solid 1px #c5073d;
}
.afspraakVakNaam {
    color: #fff;
    font-size: 13px;
    height: 14px;
    width: auto;
    word-break: break-all;
}
.day {
    display: table-cell;
    position: relative;
    border-left: dotted 1px #c5073d;
    overflow: hidden;
}
.stpanel header figure.figurelogo {
    background-image: url(https://www.clker.com/cliparts/8/e/4/0/15440163891054267158galaxy%20goed.med.png);
}
h3 {
    padding-bottom: 4px;
    height: 17px;
    font-size: 14px;
    color: #ffffff;
    text-transform: none;
}
h2 {
    font-size: 19px;
    color: #ffffff;
    line-height: 30px;
}
.stpanel header figure {
    display: block;
    width: 48rem;
    margin: 0 auto;
}
.stpanel section form {
    background: #ce30ff00;
    visibility: visible;
    -webkit-backface-visibility: hidden;
    -webkit-animation: slide-up 500ms ease;
    -moz-animation: slide-up 500ms ease;
    animation: slide-up 500ms ease;
    margin: auto;
    padding: 1.5rem;
    max-width: 27rem;
    margin-bottom: 1.5rem;
}
.stpanel .form--input:hover i, .stpanel .form--input:focus i {
    color: #ce00ff;
}
.stpanel .form--input i {
    position: absolute;
    top: .35rem;
    width: 2rem;
    height: 2rem;
    right: .35rem;
    padding-left: .2rem;
    line-height: 2rem;
    font-size: 1rem;
    text-align: center;
    color: #ff00c6;
}
.stpanel {
    height: 100%;
    width: 100%;
    line-height: 1.5em;
    color: #ffffff;
}
.m-element:hover, .load-more:hover {
  background-color: #fb00ff4d;
}

}
