css:
@-moz-document domain("duelingnexus.com") {
	
/*change this link to change the background*/
html ,#game-rps-container{
	background: url(https://imgur.com/OMmsFjQ.png )!important;
}

/*this changes the image that indicates that you can special summon from Extra Deck or Graveyard*/
.game-field-zone-activate {
	content:url("https://i.imgur.com/rCcLieN.png");
}

/*general border style*/
* {
	border-radius:0 10px 10px 10px;
}

/*all borders except these*/
.game-field-card, .editor-card-small, .game-selection-card-image, .game-display-card-image{
	border-radius:0;
}

/*deck editor borders*/
#editor-main-deck, #editor-extra-deck, #editor-side-deck {
	border:1px solid #ffffff47;	
}

/*card search window*/
.game-selection-card {
	margin: 1vh 1vw 1vh 1vw;
	z-index: 7;
}

/*cards on field on top*/
.game-field-card {
	z-index: 10;
}

/*color of atk def lvl etc*/
.game-field-stats-text {
	color: #fff !important;
	z-index: 3;
}

.game-field-zone-content  {
	color: #fff;
	z-index: 5;
}

.game-card-negated {
	z-index: 5;
}

.game-field-zone-activate {
	z-index: 8;
	position: absolute;
}


#game-option-list {
	padding: 0.5vw
}

#game-option-list .engine-button {
	margin: 0.25vw;
	padding: 0;
	position: relative;
	bottom: 0;
}

/*description box background*/
#card-description-box {
	background: rgba(0,0,0,0.55);
}

/*general background*/
#game-alert-window,
#options-window,
#game-results-container,
#game-message-window,
#game-waiting-menu,
#game-selection-window,
#game-yesno-window,
#game-position-window,
#game-option-window,
#game-announce-window,
#game-announce-card-window
{
	background-color: #00000080;
	border-color: #fff;
	border-width: 15px 1px 1px 3px;
	border-radius: 15px;
	left:-15vw;
	top:40vh;
	
}

#game-end-window{
	background-color: #00000080;
	border-color: #fff;
	border-width: 15px 1px 1px 3px;
	border-radius: 15px;
}

#game-room-players {
	width: 40vw;
	min-width: 28px;
	padding: 2vh 2vw 2vh 1vw;
	margin-top: 20px;
}

#game-room-information {
	width: 16vw;
	min-width: 260px;
	padding: 2vh 2vw 2vh 1vw;
}

#game-selection-window {
	overflow-y: scroll;
	height: 25vw;
	width: 40vw;
	z-index: 6;
	left:15vw;
	top:30vh;
}

#game-siding-column-2{
	border: 1px;
	height: 37.67vw;
	width: 37.67vw;
}

#game-siding-done{
	height: 4vw;
	width: 100%;
	position: relative;
	top: -2.0vw;
}

#news-area {
	background:#000000b5;
}

/*body font size*/
body{
font-size: 14px;
}

/*more backgrounds*/
#game-field-extra-monster1:hover, #game-field-extra-monster2:hover, #game-field-player-monster1:hover, #game-field-player-monster2:hover, #game-field-player-monster3:hover, #game-field-player-monster4:hover, #game-field-player-monster5:hover, #game-field-extra-monster1:hover, #game-field-opponent-monster1:hover, #game-field-opponent-monster2:hover, #game-field-opponent-monster3:hover, #game-field-opponent-monster4:hover, #game-field-opponent-monster5:hover {
    
    background-color: #fff9;
}

#game-field-player-spell1:hover, #game-field-player-spell2:hover, #game-field-player-spell3:hover, #game-field-player-spell4:hover, #game-field-player-spell5:hover, #game-field-opponent-spell1:hover, #game-field-opponent-spell2:hover, #game-field-opponent-spell3:hover, #game-field-opponent-spell4:hover, #game-field-opponent-spell5:hover{
    background-color: #fff9;
}

#game-field-opponent-graveyard:hover,#game-field-player-graveyard:hover,#game-field-opponent-spell6:hover,#game-field-player-spell6:hover, #game-field-player-banished:hover,#game-field-opponent-banished:hover,#game-field-player-deck:hover, #game-field-opponent-deck:hover, #game-field-player-extra:hover, #game-field-opponent-extra:hover{
    background-color: #fff9;
}
	
#game-waiting-window{
	position: fixed;
	left : 26vw;
	z-index: 5;
}
	
#game-selection-window {
	position: fixed;
	top: 50vh;
	left: 25vw;
	perspective:0;
}

/*emri i kartave ne hover
bug:if the text box in displayed in the left there is pilsing-----------------------------------------------------------------------------------------*/
#game-tooltip    {
	color: #fff ;
	font-size: 1.105vw;
	background: radial-gradient(#00000070, #0d0d0d59);
	border: 1px dashed #00000061;
	padding: 1vh;
	z-index: 7;
}
}

@-moz-document domain("duelingnexus.com") {

/*delete this code section if you are having problems with the 3D game field */
#game-selection-window {
	transform:translate(-12vw , -31vh);
}

#game-yesno-window{
	transform:translate(-4vw)
}

#game-container {
	perspective:1100px;

}

#game-column{
	transform:rotatex(48deg) translatex(-30px) scale(1.1);		
}

#game-action-menu{
	transform:translate(-24.5vw)
}

#game-tooltip {
	transform:translatex(-27vw);
		background:rgba(0, 0, 0, .33);

}

.game-avatar-area {
	transform: scale(1.35) 
}

#game-rps-container , #game-rps-result{
	perspective:1700px;
}

}
