css:
@-moz-document url-prefix("https://bs.to") {
/* 
    Burning Series BlueGrey

    Creator:            vkay
    Theme-Version:      1.0.0
    Website-Version:    1.1.1 
    Date:               10.12.2018
*/
:root {
    --blue-grey-900: #263238;
    --blue-grey-800: #37474F;
    --blue-grey-700: #455A64;
    --blue-grey-600: #546E7A;
    --blue-grey-500: #607D8B;
    --blue-grey-400: #78909C;

    --text-color: #e0e0e0;

    --error-red: #a43d47;
    /*     --success-green: #429a44; */
    --info-blue: #236b77;
}

@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/2UX7WLTfW3W8TclTUvlFyQ.woff) format('woff');
}


html {
    background: #1d262a;
}

body,
body a {
    font-size: 14px;
}

body > #root > section {
    background-color: var(--blue-grey-900);
}

/* Header */
#root > header,
.home > #teaser > header,
.home > .column > section > header,
footer > ul,
nav > ul {
    background: #29353b;
}

body > #root > header a.banner {
    background: url(http://vkaygpe.000webhostapp.com/Stylus/BurningSeries/img/bs_banner_bw.jpg) no-repeat;
}

/* Navigation */
nav > ul > li {
    background: #212b30;
    border-right: 1px solid #2e3c43;
}

nav > ul {
    display: flex;
    box-shadow: none;
    height: 2.75em;
}

/* Innere Listen */
nav > ul,
nav > ul > li > ul {
    background: #314047;
    line-height: 2em;
    border-radius: 0;
}

nav > ul > li > ul > li:first-child {
    border-top: none;
}

nav > ul > li {
    padding: .5em 0;
}

nav > ul > li:hover,
nav > ul > li > ul > li:hover {
    background: #384a52 !important;
}

nav > ul > li > ul {
    left: 0px;
    top: 2.75em;
    border-radius: 0 0 2px 2px;
}

nav > ul > li > ul > li {
    padding: .25em .5em;
    background: transparent;
    border: none;
}

nav > ul > li > ul > li:last-child {
    border-radius: 0 0 2px 2px;
}

nav > ul > li:last-child {
    padding: .5em;
}

nav a {
    color: white;
}

.fb-like,
.fb_iframe_widget {
    font-family: Helvetica, Arial, sans-serif;
    display: none !important;
}

footer ul {
    background-color: #1d262a;
}

/* Login */
form#login {
    padding: 8px;
    border-radius: 4px;
    background: #475a64;
}

form#login input[type=submit] {
    background-color: #718d9c;
    color: var(--text-color);
    border: none;
}

/* Logged-In */
section.navigation > div:first-child {
    border: 1px solid #4c626d;
    /*     background: #425259; */
    background: var(--blue-grey-800);
}

section.navigation a,
section.navigation div {
    padding: 5px;
    border: 1px solid #586b76;
    /*     background: #42525a; */
    background: var(--blue-grey-700);
    color: var(--text-color);
}

section.navigation a:hover,
section.navigation div:hover {
    padding: 5px;
    border: 1px solid #647b88;
    background: #5d707a !important;
    color: var(--text-color);
}

/* Messagebox */
.messageBox {
    margin-bottom: 10px;
    padding: 10px 12px;
}

.messageBox.error {
    background-color: var(--error-red);
    border-color: #8d1d28;
    color: var(--text-color);
}

.messageBox.info {
    background-color: var(--info-blue);
    border-color: #14606c;
    color: var(--text-color);
}

/* Scrollbar */
::-webkit-scrollbar {
    width: 12px;
}

/* Track */
::-webkit-scrollbar-track {
    background: var(--blue-grey-800);
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: var(--blue-grey-600);
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background: var(--blue-grey-400);
}

/* 
############################################################################################ 
############################################################################################
*/

/* Startseite */
section.home h2 {
    color: var(--text-color);
}

.fas.fa-user-edit {
    filter: invert(1) brightness(0.8);
}

.fas.fa-user-edit:hover {
    filter: brightness(0) invert(1);
}

/* 
############################################################################################ 
############################################################################################
*/
/* Alle Serien */
.andere-serien h2,
.andere-serien p,
.genre span strong {
    color: var(--text-color) !important;
}

.genre {
    margin-bottom: 20px;
}

.genre span {
    background-color: #5a727e !important;
}

.andere-serien .genre ul {
    background-color: #324047 !important;
}

.andere-serien a {
    color: var(--text-color) !important;
}

/* Suche */
.andere-serien input[type=text] {
    background-color: var(--blue-grey-800);
    border: 2px solid var(--blue-grey-700);
    color: var(--text-color) !important;
}

.andere-serien input[type=text]::placeholder {
    color: #aaa;
}

.andere-serien p {
    border-color: var(--blue-grey-600)
}

/* 
############################################################################################ 
############################################################################################
*/

/* Serien */
.serie h2 {
    text-transform: uppercase;
    padding-bottom: 10px;
}

.serie h2 small {
    padding-left: 20px;
}

/* Tabelle */
.infos {
    padding: 8px 0px;
}

.serie .infos div {
    padding: 4px 0px;
    /* offset-x | offset-y | blur-radius | spread-radius | color */
    box-shadow: 0px 3px 2px -2px #606060;
}

.serie .infos div span {
    min-width: 22%;
    max-width: 35%;
}

.serie .infos div p {
    max-width: 60%;
}

.serie .infos div p span:hover {
    background: #5a727f;
    padding: 1px 4px 1px 4px;
}

.serie .infos div p a {
    color: #a0d0ea;
}

/* Cover */
#sp_right img {
    border-radius: 3px;
    margin: 15px 0px
}

#sp_right a {
    margin-bottom: 5px;
}

.fas.fa-fw {
    width: 1.5em
}

/* Language Select */
.nice-select.series-language {
    background-color: #455A64;
    border: none;
}

.current {
    font-weight: bold
}

/* Pfeil */
.nice-select:after {
    border-bottom: 2px solid #ccc;
    border-right: 2px solid #ccc;
}

.nice-select.series-language > .list > .option,
.nice-select.series-language > .list,
.nice-select.series-language > .list:hover {
    background-color: #40525b;
}

#sp_right div.series-language {
    box-shadow: 0 0 2px 2px #4267b270;
}

/* Episoden */
.serie,
.serie a {
    color: var(--text-color);
}

.serie div.episodes,
.serie div.seasons {
    width: 49%;
}

.serie .frame {
    height: auto;
}

.serie .clearfix {
    display: inline-block;
}

.serie .frame ul {
    width: 250px;
    overflow: auto;
}

.serie .frame ul li {
    background-color: #40525c;
    margin-right: 3px;
    border-radius: 5px;
    height: auto;
}

.serie .frame ul li.active {
    background-color: #718d9c;
}

.serie .frame ul li a:hover {
    background-color: #78909C;
}

.serie .frame ul li.watched {
    background-color: #5a727f !important;
}

/* Episoden-Hover bei Hover, Hoster-Auswahl */
.serie .epiInfo,
.serie .epiInfo strong {
    background-color: #40525c;
    box-shadow: 0 0 1px 1px rgba(0, 0, 0, .2);
    border-radius: 4px;
    padding: 4px;
}

.serie .epiInfo strong {
    margin-bottom: 2px;
}

/* ----- */
.serie .scrollbar {
    background: #4b5b62;
}

.scrollbar .handle {
    background-color: #839ca8 !important;
}

/* Liste */
.serie table.episodes {
    border-radius: 3px;
    box-shadow: 0px 0px 5px #455963;
}

.serie table.episodes tr {
    background-color: #263238;
    border-top: 1px solid #455963;
    padding: 3px 0px;
}

.serie table.episodes tr.watched {
    background-color: #455A64 !important;
    border-top: 1px solid #455963;
    padding: 3px 0px;
}

.serie table.episodes tr:hover,
.serie table.episodes tr.watched:hover {
    background-color: #536c78 !important;
}

.serie table.episodes td a {
    color: #ddd;
}

.serie table.episodes tr td:nth-child(1) a {
    background-color: #40525c;
}

.serie table.episodes tr.watched td:nth-child(1) a {
    background-color: #5a727f !important;
}

/* Hover */
.serie table.episodes tr td:nth-child(1) a:hover,
.serie table.episodes tr.watched td:nth-child(1) a:hover {
    background-color: #78909C;
}

.fa-eye-slash,
.fa-eye,
.fa-fw.fa-eye,
.fa-fw.fa-eye-slash {
    filter: brightness(0.9)
}

/* Hoster-Auswahl, Player */
.serie .hoster-tabs li a:hover {
    background-color: #37474F !important;
    color: var(--text-color);
    border-color: transparent !important;
    border-width: 2px 2px 0px 2px;
}

.hoster-tabs li.active a:focus,
.hoster-tabs li.active a:hover,
.serie .hoster-tabs li.active a {
    background-color: #40525c !important;
    border-color: #5a727f #5a727f transparent !important;
    border-width: 2px 2px 0px 2px;
    color: var(--text-color);
}

.serie .hoster-player {
    background: #40525c;
    border: 2px solid #40525c;
    border-radius: 2px;
}

.serie .hoster-tabs {
    border: none !important;
}

.hoster-player.deactivated h3 {
    color: var(--text-color);
    font-size: 24px;
}

/* 
############################################################################################ 
############################################################################################
*/

/* App Seite */
body {
    color: var(--text-color);
}

a {
    color: #8390e6;
}

a.download {
    background-color: var(--blue-grey-700);
    color: var(--text-color);
    border: 1px solid var(--blue-grey-800)
}

a.download:hover {
    color: var(--text-color);
    background-color: var(--blue-grey-600);
    border: 1px solid var(--blue-grey-700)
}
}
