css:
@-moz-document domain("prairielearn.engr.illinois.edu") {
:root {
    /*Switch*/
    --darkest: #292929;
    --darker: #323232;
    --lighter: #404040;
    --lightest: #505050;
    --highlight: #3efcbd;
    --text: white;
    
    /*Twitter
    --darkest: #0c161c;
    --darker: #111E27;
    --lighter: #172838;
    --lightest: #1F3449;
    --highlight: #42c6e5;
    --text: white;
    */
}

body {
    background: var(--darker);
}

code {
    background: var(--lighter) !important;
    color: var(--text) !important;
    padding: 3px;
    border-radius: calc(.25rem - 1px) !important;
}

.upload-dropzone {
    background-color: var(--lighter) !important;
}

.bg-dark code, .output code {
    background: none !important;
}

.bg-dark {
    background: var(--darkest) !important;
}

.btn {
    background: var(--highlight) !important;
    color: var(--darkest) !important;
    border: none;
}

.navbar {
    background: var(--darkest) !important;
    position: fixed;
    z-index: 500;
    width: 100%;
}

.container {
    top: 80px !important;
    position: absolute;
    width: 100%;
    max-width: 100%;
    padding: 0px 100px 0px 100px;
}

.card, .dropdown-menu, .dropdown-item, .modal-content, .login-container, .popover-body {
    background: var(--lightest) !important;
    color: var(--text);
}

.card-header, .modal-header, .list-group-item, .alert, .popover-header{
    color: var(--text) !important;
    background: var(--lighter) !important;
    border: none !important;
}

.nohighlight, a, h1 {
    color: var(--highlight) !important;
}

.badge {
    color: var(--darker) !important;
}

.modal-body, .modal-footer {
    border : none;
}

.close, tr {
    color: var(--text) !important;
}

td, tr, th {
    border-top: none !important;
    border-bottom: 1px solid var(--lighter) !important;
}

::selection {
    background: var(--highlight);
    color: var(--darker);
}


/*======================================== custom code themes ========================================*/

/*taller window and bigger font*/
.ace_editor {
    height: 480px !important;
    font-size: 14px !important;
}

/*whole editor*/
.ace-chrome {
    background-color: #212121;
    color: white;
}

/*sidebar*/
.ace-chrome .ace_gutter {
    background: #212121;
    color: #41413d;
}

/*selected line and sidebar*/
.ace-chrome .ace_gutter-active-line, .ace-chrome .ace_marker-layer .ace_active-line {
    background-color : #181818;
}

/*cursor*/
.ace-chrome .ace_cursor {
    color: #ffcc00;
}

/*t/f/null*/
.ace-chrome .ace_constant.ace_language {
    color: #ff6694;
}

/*private etc*/
.ace-chrome .ace_storage,.ace-chrome .ace_keyword,.ace-chrome .ace_meta.ace_tag, .hljs-keyword {
    color: #c792ea;
    font-style:italic;
}

/*object names*/
.ace-chrome .ace_support.ace_function, .hljs-title {
    color: #ffc55a;
}

/*numbers*/
.ace-chrome .ace_constant.ace_numeric, .hljs-number {
    color: #fb805b;
}

/*string*/
.ace-chrome .ace_string, .hljs-string {
    color: #ccf78d;
}

/*operators*/
.ace-chrome .ace_keyword.ace_operator, .hljs-operator {
    color: #7fdaff;
    font-style: normal;
}

/*comments*/
.ace-chrome .ace_comment, .ace-chrome .ace_comment.ace_doc {
    font-style: italic;
    color: #616161;
}

/*javadoc*/
.ace-chrome .ace_comment.ace_doc.ace_tag {
    font-style: italic;
    color: #8c8c8c;
}

/*text selection*/
.ace-chrome .ace_marker-layer .ace_selection {
    background: #353535;
}

/*show other bracket*/
.ace-chrome .ace_marker-layer .ace_bracket {
    background: #ffcc0055;
    margin: 1px 0px 0px 0px;
    padding: 4px;
    border: none;
    border-radius: 0px;
}

/*selected text in find*/
.ace-chrome .ace_marker-layer .ace_selected-word {
    background: #fff2;
    border: 1px solid #fff3;
}

/*indentation guide from Dracula*/
.ace-chrome .ace_indent-guide {
 background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAEklEQVQImWNgYGBgYHB3d/8PAAOIAdULw8qMAAAAAElFTkSuQmCC) right repeat-y
}
}
