/* DÃ‰BUT _header.scss */
header #headerFixedBar {
    background-color: #000;
}

header #headerFixedBar.faded {
    background-color: rgb(0, 0, 0);
}

header #headerFixedBar nav #container-user-recherche #parent-textbox-recherche {
    transition: transform 200ms;
    transition-timing-function: cubic-bezier(0.4, 0, 1, 1);
    background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, #3516A8 10%, #C41616 100%);
}

header #headerFixedBar nav #container-user-recherche #parent-textbox-recherche #headerSearchTextBox {
    background-color: #ACACAC;
    transition-property: background-color;
    transition-duration: 0.5s;
}

header #headerFixedBar nav #container-user-recherche #parent-textbox-recherche #headerSearchTextBox:hover {
    background-color: #e8eaed;
}

header #headerFixedBar nav #headerNavbarProfile #headerNavbarProfileText #headerNavbarProfileInstitution {
    color: #656565;
    transition-property: color;
    transition-duration: 0.2s;
}

header #headerFixedBar nav #headerNavbarProfile #headerNavbarProfileText #headerNavbarProfileInstitution:hover {
    color: #0277c8;
}

header #headerFixedBar nav a.headerNavbarLink {
    transition-property: background-color;
    transition-duration: 0.2s;
}

header #headerFixedBar nav a.headerNavbarLink:hover {
    background-color: #4A4A4A;
}

header #headerImage {
    background: none;
    height: 60px;
}

* {
    background-color: black;
}

a {
    color: #aaaaaa;
    transition-property: color;
    transition-duration: 0.4s;
}
a:hover {
    color: #2732D2;
}

