css:
@-moz-document domain("chessable.com")
{

body {
    background-color: #181818;
    border: 0px solid #000;
     color: #929292;
}

div.bookHome{
    background-color: #262626;
    border: 0px solid #000;       
}
.myButton {
    
    box-shadow: 0 2.5px 0 0 #565656,0 3.7px 2px rgba(0,0,0,0.22);
    background-color: #000;
         color: #fff;
}
.mini-profile__link {

    color: #b2b2b2;

}

.myButton:hover {
    
    box-shadow: 0 2.5px 0 0 #fff,0 3.7px 2px rgba(0,0,0,0.22);
    background-color: #fff;
             color: #000;
}
.myButton--cold-blue-transparent,.myButton--cold-blue-transparent:hover {
    border-color: #858585;


}

.meter > span {

    display: block;
    height: 100%;
    border-radius: 20px 10px 10px 20px;
    background-color: #000!important;
    background-image: linear-gradient(70deg, #000, #000)!important;
}

.bookHome.placeholder{
    background-color: #262626;
    border: 0px solid #000;  
}

.homeCol2-container {
    background-color: #262626;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px
}

.nav-i__button{
    color: #858585;
}
.breadcrumbs > .item.active {

    background-color: #3d3d3d;
    color: #fff;
    text-shadow: 0 1px 0 rgba(0,0,0,0.25);

}
h2{
         color: #929292!important; 
}

header{
        color: #929292!important;
    background-color: #262626;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px
}

.levelBox{
    background-color: #262626;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px

}
.mini-profile__stats{
    background-color: #202020;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px

}

.leaderBoardRow{
    background-color: #202020;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px

}

.breadcrumbs > .item{
    background-color: #202020;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px

}

.bookstore-sidebar__main{
    background-color: #202020;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px

}

.book-card__inner,.book-card__price{
    color: #929292!important;
    background-color: #202020;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px

}



.bookTitle,.book-card__title>span{
        color: #929292!important;
}

.top-footer {

    background-color: #0a0a0a;
    padding: 22px 0;
    position: relative;

}

.bubbleUser{
    color: #929292!important;
    background-color: #202020;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px

}

.breadcrumbs > .item::after, .breadcrumbs > .item::before{
    opacity: 0;
}
.board-footer__box{
   color: #929292!important;
        border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px;
    background-color: #202020;
}

.practice-progress{
   color: #929292!important;
        border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px;
    background-color: #202020;

}


.board-info-card{
   color: #929292!important;
        border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px;
    background-color: #202020;

}


a {

    color: #de3232;
    text-decoration: none;

}
.teach-container{
   color: #929292!important;
        border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px;
    background-color: #202020;
}
.levelBox__title,.levelBox__title-text{
    color: #929292!important;
        border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px;
        background-color: #181818;
}

.levelBox__inner,.whiteBodyContainer{
        color: #929292!important;
    background-color: #202020;
    border: 0px solid #000;  
    box-shadow: 0 0 0 0px #000, 0 0px 0px
}}
