css:
@-moz-document domain("musicfromouterspace.com") {
/* Dark Music from Outer Space by At-M */
/* http://userstyles.org/users/62569 */
/* Author: At-M */
/* Contact: atmsupload@gmail.com */
/* Header and Footer, remove stars */
.starbackground, .maintabtable_v2, .headertable_v2 {
    background: #1F1F1F;
    background-image: none;
}
/* Main Navigation Entries*/
.normaltab, .rollovertab, .selectedtab {
    background: #3a3a3a;
    background-image: none;
}
/* Main Navigation Entry Hover*/
.rollovertab {
    background: #2f2f2f;
}
/* Main Navigation Entry Active*/
.selectedtab {
    background: #734AE8;
}
/* Buttonmenu top right*/
.utilitybardiv {
    background: #734AE8;
    border-radius: 60px 00px 0px 0px;
    background-image: none;
}
/* Searchbar top right */
input[type=text] {
    border: 2px solid rgba(0, 0, 0, 0.2);
    margin-top: -3px;
    padding: 5px 5px 5px 3px;
    color: #fff;
    border-radius: 3px;
    background-color: rgba(0, 0, 0, 0.2)!important;
    font-size: 15pt;
    height: 22pt
}
/* Logo top left */
.headertable_v2 > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > img:nth-child(1) {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 505px !important;
    padding-top: 34px !important;
    background: url(https://i.imgur.com/yVbbUlT.png) no-repeat !important;
}
/* Logo top right */
.headertable_v2 > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > img:nth-child(1) {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 290px !important;
    padding-top: 34px !important;
    background: url(https://i.imgur.com/dx88VyK.png) no-repeat !important;
}
/* Offset the content to kinda-center */
.bodydiv {
    padding-left: 5%/* For Screens with 1920x */
}
@media only screen and (min-width: 2000px) {
    .bodydiv {
        padding-left: 15.5%/* For Screens with 2560x */
    }
}
/* Vertical Menu */
#directorycontainer {
    background-color: #3a3a3a;
    overflow-x: hidden;
    width: 300px!important;
    border-color: transparent;
    -webkit-box-shadow: inset 0px 10px 30px -9px rgba(0, 0, 0, 0.75);
    -moz-box-shadow: inset 0px 10px 30px -9px rgba(0, 0, 0, 0.75);
    box-shadow: inset 0px 10px 30px -9px rgba(0, 0, 0, 0.75);
}
/* Vertical Menu Entries */
.prj, .prjhi, .prjon {
    font-size: 12pt;
    margin-left: 0;
    padding: 2px 0px 2px 15px;
    width: 100%;
    color: #d3d3d3;
}
/* Vertical Menu Entries Hover*/
.prjhi {
    background-color: #2f2f2f;
    color: #fff;
}
/* Vertical Menu Entries Active*/
.prjon {
    background-color: #6937b9;
    color: #fff;
}
/* Vertical Menu Sectiontitles */
.sec {
    font-size: 14pt;
    color: #9476ea;
}
/* Vertical Menu Multipagetitles */
.multipagetitle {
    color: #9476ea;
    font-size: 12pt
}
/* Vertical Menu Multipagesections */
.multipagesection {
    margin-left: 0px;
    padding-left: 20px;
}
/* Submenu */
#synthdiy_submenu {
    background-color: #1F1F1F;
}
/* Submenu Entries*/
.ls_lo, .ls_hi, .ls_mo {
    background-color: #3a3a3a;
    border-color: transparent;
    margin-left: 0px;
    margin-right: 0px;
    padding-left: 8px;
    padding-right: 8px;
    padding-top: 5px;
    padding-bottom: 7px;
    font-size: 11pt;
    -webkit-box-shadow: inset 0px 10px 5px 0px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: inset 0px 10px 5px 0px rgba(0, 0, 0, 0.2);
    box-shadow: inset 0px 10px 5px 0px rgba(0, 0, 0, 0.2);
}
/* Submenu Entry Hover*/
.ls_hi {
    background-color: #734AE8;
    -webkit-box-shadow: inset 0px 10px 5px 0px rgba(0, 0, 0, 0);
    -moz-box-shadow: inset 0px 10px 5px 0px rgba(0, 0, 0, 0);
    box-shadow: inset 0px 10px 5px 0px rgba(0, 0, 0, 0);
}
/* Submenu Entry Active*/
.ls_mo {
    background: #2f2f2f;
}
/*[[vertmenutoggle]]*/
}
