css:
@-moz-document domain("ticalc.org") {
.timeline-Widget, body, td[bgcolor="white"], tr[bgcolor="white"], body[bgcolor="white"] {
    background-color: #000;
}
tr[bgcolor="#EEEEEE"], td[bgcolor="#EEEEEE"] {
    background-color: #111;
}
tr[bgcolor="#DDDDDD"], td[bgcolor="#DDDDDD"] {
    background-color: #222;
}
tr[bgcolor="#A0A0A0"] {
    background-color: #555;
}
tr[bgcolor="#FFEECC"] {
    background-color: #431;
}
th[style="cursor: default; background-color: rgb(255, 238, 204);"] {
    background-color: #431 !important;
}
th[style="cursor: pointer; background-color: rgb(255, 204, 102);"] {
    background-color: #320 !important;
}
th[bgcolor="#0066CC"] {
    background-color: #026;
}
strong, font[color="#666666"], .timeline-Header-byline {
    color: #bbb;
}
.timeline-Header-title, .t2, table, pre, p, i, b[size="4"] {
    color: #fff;
}
A[HREF], font[color="#003399"] {
    color: #66f !important;
}
}
