css:
@-moz-document domain("keep.google.com") {


/* Background color set */
body, body.dkl3Ye, .PvRhvb-bN97Pc
{background-color: /*[[background]]*/;}


/* Sidebar text color invert */
.PvRhvb-ibnC6b-V67aGc, .PvRhvb-ibnC6b svg
{
    color: /*[[background]]*/; 
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
.PvRhvb-ibnC6b.gk6SMd, .PvRhvb-ibnC6b:hover {
       background-color: /*[[background]]*/; 
    -webkit-filter: invert(100%);
    filter: invert(100%);  
}


/* Labels border */
.Q0hgme-XPtOyb {border: /*[[labelbordersize]]*/px solid;}


/* Checkbox for adding Labels to notes */
.mQXP-L9AdLc {background-color: darkseagreen; border: 2px solid; border-radius: 10px;}


}
