@-moz-document domain("aulavirtual.ual.es") {
.bannerImage {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url("http://as00.epimg.net/img/comunes/fotos/fichas/equipos/large/1.png") no-repeat;
    background-size: 70px 70px;
    width: 354px;
    height: 354px;
    padding-left: 354px;
}

.topTabs {
    height: 120px
}

.appTabs a {
    padding: 30px 1.2em 1px
}

.appTabs td {
    padding-bottom: 0px !important
}

#topTab, table.appTabs tbody tr td a, div.topTabs-compact table.appTabs tr td a, table.appTabs tbody tr td a:hover, div.topTabs-compact table.appTabs tr td a:hover, table.appTabs tbody tr td a:focus, div.topTabs-compact table.appTabs tr td a:focus {
    color: #ffffff;
}
}
