@namespace url(http://www.w3.org/1999/xhtml);
/* Aendert das Kwick-Aussehen. Von http://www.kwick.de/Pitchshifter */
/* Original von http://www.kwick.de/Pitchshifter (wenigstens diese Zeile bitte stehen lassen...) */
@-moz-document domain("www.kwick.de") {

body {background:#C6E7DF none repeat scroll 0 0 !important; color:#777 !important }


#wrapper {background:#FFF none repeat scroll center center !important; border-right: 3px solid #FFF !important; border-left: 3px solid #FFF !important; width:1012px !important}

.sidebarNick {width: 100px !important}

table {background:#bcffc5 none repeat scroll 0 0 !important}

a:link {color:#000 !important; outline-color:-moz-use-text-color !important; outline-style:none !important; outline-width:medium !important;}

a:visited {color:#000 !important; outline-color:-moz-use-text-color !important; outline-style:none !important; outline-width:medium !important;}

a:hover {color:#000 !important; text-decoration:underline !important}

.time {color:#7b7b7b !important}

.defaultSkin table, .defaultSkin tbody, .defaultSkin a, .defaultSkin img, .defaultSkin tr, .defaultSkin div, .defaultSkin td, .defaultSkin iframe, .defaultSkin span, .defaultSkin *, .defaultSkin .mceText {background-color:#FFF !important}

#header #navigation #status a {color:#FFFBF7 !important;}


/*-------SIDEBAR-----------*/

#sidebar .box {background:#CEFFCE !important; border-right:1px solid #CCCCCC !important; border-left:1px solid #CCCCCC !important; border-bottom:1px solid #CEFFCE !important}

#sidebar .box-bottom {height:4px !important;border-bottom:1px solid #CCCCCC !important; border-right:1px solid #CCCCFF !important; background: #CEFFCE none no-repeat scroll right bottom !important}

#sidebar #buddys td.bItem {background:#CEFFCE none repeat scroll 0 0 !important; border-bottom:1px solid #CCCCFF !important;}

#sidebar #ticker {background:#CEFFCE !important}

#sidebar .tbInfobox {border-right:1px solid #CCCCCC !important;}

#sidebar .box-header, #ticker .box-header {background:#009A31 !important; border-bottom:1px dotted #ADADAD !important; border-left:1px solid #CCC !important; border-top:1px solid #CCCCCC !important; border-right:1px solid #CCCCCC !important; width:173px !important; color:#FFF !important}

#sidebar .box-header a {color:#FFF !important}

#sidebar .box-top {background:#CEFFCE none right top no-repeat !important; border-right:1px solid #CCCCCC !important; border-left:1px solid #CCCCCC !important; border-top:1px solid #CCCCCC !important}

#sidebar table {background:#CEFFCE !important}



/*-----------MODULE-------------*/

.module .header {background:#009A31 none no-repeat right top !important; border-top:1px solid #C4CEDF !important; border-left:1px solid #C4CEDF !important; border-right:1px solid #C4CEDF !important; border-bottom:1px solid #C4CEDF !important; color:#FFF !important}



.module .header h4, .module .header a, .modCom .header a, .modCom .header h4 {background:#009A31 none no-repeat left top !important; color:#FFF !important}

.module .content {background:#CEFFCE none repeat left top !important; border-bottom: 1px solid #C4CEDF !important; border-left: 1px solid #C4CEDF !important; border-right: 1px solid #C4CEDF !important}

.module .hr {border-bottom:1px solid #CCCCCC !important; }


/*--------------MODCOM--------------*/

.modCom .header {background:#009A31 none no-repeat right top !important; border-bottom: 1px solid #C4CEDF !important; border-top: 1px solid #C4CEDF !important; border-right: 1px solid #C4CEDF !important}

.modCom .header h4 {background:#009A31 none no-repeat left top !important; color:#3B5998 !important; border-bottom: 1px solid #C4CEDF !important; border-left: 1px solid #C4CEDF !important;}

.modCom .content {background:#CEFFCE none repeat scroll 0 0 !important; border-bottom: 1px solid #C4CEDF !important}

.modCom .content-footer,.module .content-footer {height:0px !important}

.nolink {color:#6A7171 !important;}



/*----------TABNAV----------*/


/*-------TOPNAV1-------*/


/*------TOPNAV2--------*/

#t2uo {background:#009A31 none repeat-x scroll left top !important; border-bottom:1px solid #CCC !important;  border-left:1px solid #CCC !important; height:27px !important; border-right:1px solid #CCC !important; padding-left:10px !important; padding-top:3px !important}

#topnav2 ul {background:#009A31 none repeat-x scroll left top !important; height: 31px !important; }

#topnav2 ul a {color:#FFF !important; text-decoration: none !important; display: block !important; font-weight: bold !important; line-height: 2em !important; font-size: 11px !important; font-family:"Lucida Grande",Arial,Helvetica,Verdana,sans-serif !important; background:#009A31 none repeat-x scroll left top !important; width: 120px !important; margin:-1px 0 0 !important; padding:4px 5px 5px 10px !important; }

#topnav2 {padding: 0 0 0 0px !important; background:#F498E2 none repeat-x scroll left top !important; height:28px !important; border-right:1px solid #CCC !important; border-bottom: 0px solid #CCC !important}

#topnav2 ul a {border-bottom:1px solid #CCCCCC !important; border-right:1px solid #CCCCCC !important; height:19px !important}

#topnav2 a {color:#EDDEF3 !important}

.delim {display:none !important}

#topnav2 ul li.act a, #topnav2 ul li.act a:hover {color:#D51007 !important; background: #84CF96 none repeat scroll 0 0 !important; height:21px !important}

#topnav2 ul a:hover {color:#D51007 !important; background: #84CF96 none repeat scroll 0 0 !important}

#topnav2 ul li.act {background:#5C75AA none bottom repeat-x !important;}


table.list .alt1, .rowlist.a {background-color:#C6E7CE !important}

table.list .alt2, .rowlist.b {background-color:#C6E7CE !important}

#lay2col-r .colcontent {background:#CEFFCE none no-repeat bottom !important; border-top:1px solid #C4CEDF !important; border-left:1px solid #C4CEDF !important; border-right:1px solid #C4CEDF !important; border-bottom:1px solid #C4CEDF !important}

.lay2col-r .colcontent {background:#CEFFCE none no-repeat bottom !important; border-top:1px solid #C4CEDF !important; border-left:1px solid #C4CEDF !important; border-right:1px solid #C4CEDF !important; border-bottom:1px solid #C4CEDF !important}



.colcontent-h {height:0px !important}



/*--------------MTABLE---------------*/

.mtable td {background:#CEFFCE !important; border-right: 1px solid #C4CEDF !important}

.mtable-right {background:#EEE none right top no-repeat !important; border-bottom: 1px solid #C4CEDF !important; border-right: 1px solid #C4CEDF !important}

.mtable-left {background:#EEE none right top no-repeat !important; border-bottom: 1px solid #C4CEDF !important; border-left: 1px solid #C4CEDF !important; border-right: 0px solid #C4CEDF !important}

.mtable th {background:#84CF96 !important; border-top: 1px solid #C4CEDF !important;}

.mtable .mtc {background:#FFFFFD !important; border-bottom: 0px solid #C4CEDF !important; border-right: 0px solid #C4CEDF !important; border-top: 1px solid #C4CEDF !important; border-left: 0px solid #C4CEDF !important}

.mtable .lb {border-left: 1px solid #C4CEDF !important}

.mtable tr.delimiter td {border-color: #C4CEDF -moz-use-text-color !important; background: #FFF !important}

.Paginator a, .Paginator a:visited, .Paginator .this-page { background: transparent !important; color:#000 !important}

div.puser {background:transparent none center !important}

h1,h2,h3 {color:#D15A1F !important}

.bc,#header #navigation #meta {visibility:hidden !important; line-height:0px !important}

#footer {line-height:22px !important; height:3px !important; padding:0px 0 0 12px !important}



/*--------JDROP------------*/

.jDrop table {background-color:#000000 !important}

.jDrop, .jDrop td.r{background-color:#E5EDF5 !important; border-bottom:0px solid #5C75AA !important; border-top:0px solid #5C75AA !important}

.jDrop td.l {background-color:#5C75AA !important; border-bottom:0px solid #E5EDF5 !important; border-top:0px solid #E5EDF5 !important}

.jDrop tr.seperator td.r, .jDrop tr.seperator td {background:transparent none repeat-x center !important; height:2px !important; background-color:transparent !important}

html>body>div>div>table>tbody>tr>td>div>div>div>table>tbody>tr>td>table>tbody>tr>td>img:first-child {visibility:hidden !important}

}
