css:
@-moz-document regexp(".*derivative.ca/Forum.*") {
body, html {
    background: #252525!important;
    color: #e8e8e8!important;
}
.navbar {
    background-color: #333333!important;
}
a:link {
    color: #777777;
}
a:visited {
    color: #AAAAAA;
}
.post {
    background-color: #444444;
}
.attachbox {
    background-color: #EAEAEA;
}
select {
    background-color: #888888;
}
.content {
    color: #222222;
}
.postprofile {
    color: #777777;
}
.postbody {
    color: #111111;
}
img[src="/images/LogosDerivative.5.jpg"] {
    filter: grayscale(100%) brightness(85.3%) invert(100%);
}
label {
    color: #DDDDDD;
}
.author {
    color: #000000;
}
.author a {
    color: #FFDD22;
}
.postprofile a:link {
    color: #FFDD22;
}
.signature a:link {
    color: #FF22DD;
}
.content a:link {
    color: #000000!important;
}

.content a:visited {
    color: #444444!important;
}

.button2 {
    color: #222222;
}
h2 {
    color: #DDDDDD;
}
h3 {
    color: #FFDD00;
}

span.corners-top {
    filter: brightness(10%)
}
span.corners-top span {
    filter: brightness(10%)
}
span.corners-bottom {
    filter: brightness(10%)
}
span.corners-bottom span {
    filter: brightness(10%)
}
.bg2 {
    background-color: #BBBBBB;
}
.bg1 {
    background-color: #CCCCCC;
}
.bg2 a:link, .bg1 a:link {
    color: #000000;
    font-weight: bold;
    border-bottom-color: #FFDD00;
}

.bg2 a:visited, .bg1 a:visited {
    color: #555555;
    font-weight: bold;
	border-bottom-color: #FFDD00;
}

.bg3 {
    background-color: #333333;
}

#postingbox h3, #postingbox label {
    color: #000000;
}


#tabs li {
    filter: grayscale(100%) brightness(50%);
}

.activetab {
    filter: grayscale(100%) brightness(80%);
}

.icon a:link {
    color: #000000;
}

.icon a:visited{
    color: #555555;
}

.forumbg, .forabg {
    background-image: none;
    background-color: #555555;
}
ul.forums {
    filter: grayscale(100%);
}
.reply-icon {
    filter: grayscale(90%) brightness(80%);
}

.profile-icons a{
    filter: grayscale(90%) brightness(80%);
}

.MenuBorder {
    background-image: none;
}

.button2, .button1{
    filter: brightness(70%);
}

}
