css:
@-moz-document url(https://open.spotify.com/collection/artists) {
  
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans Regular'), local('OpenSans-Regular'), url(https://fonts.gstatic.com/s/opensans/v15/mem8YaGs126MiZpBA-UFVZ0b.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
  }

  @font-face {
    font-family: 'Fugaz One';
    font-style: normal;
    font-weight: 400;
    src: local('Fugaz One'), local('FugazOne-Regular'), url(https://fonts.gstatic.com/s/fugazone/v7/rax_HiWKp9EAITukFsl8Axhf.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
  }

  body, input, .navigator-toolbox, .toolbarbutton-text, .sidebar-title{
      text-shadow: 0px 0px 1px  !important;
      font-family: 'Open Sans',sans-serif !important;
  }

  div[class^="col-"] {
      width: 100%;
  }

  .media-object {
      padding: 0;
  }

  .mo-artist {
      min-width: 100%;
  }

  .mo-info {
      text-align: left;
  }

  .mo-info-name {
      font-size: 1.8em;
  }

  .media-object-hoverable {
      display: flex;
      align-items: center;
      margin-bottom: 1em;
  }

  .cover-art {
      height: 80px!important;
      margin: 0;
      margin-right: 2em;
      overflow: hidden;
      display: block;
      padding: 0;
  }

  .cover-art, .cover-art-image {
      border-radius: 0!important;
  }

  .cover-art-image {
      background-position-y: 33%!important;
  }
  
  .Root__now-playing-bar {
    right: 0;
    top: 0;
    height: 100%;
}
  .now-playing {
    flex-direction: column;
  }
  
  .now-playing-bar {
    display: flex;
    flex-direction: column;
    height: 100%;
  }
  
  .now-playing-bar-container {
    height: 100%;
    min-width: auto;
  }

  .cover-art {
    width: 300px!important;
  }
  
  .Root__now-playing-bar,
  .now-playing .cover-art,
  .now-playing-bar__center,
  .now-playing-bar__left,
  .now-playing__widget-overlay {
    width: 400px!important;
  }
  
  .Root__now-playing-bar .cover-art {
    height: 400px!important;
    margin-bottom: 2em;
  }
  
}
