@-moz-document domain("nextdoor.com") {
/*__________________________________________________________________________________*/

.css-txitvo,
.classified-item-card-container,
.numbered-list-item-section,
.channel-media,
.channel-media-nav,
.classified-item-card-container,
.comment-detail,
.dropdown-menu li,
.final-slide-listing-card-container,
.list-item,
.listing-card,
.listing-card-container,
.neighbor,
.post-container,
.recommendations-top-category-topic-container,
.slick-slide,
.value-comp-preview-listing,
.value-comp-preview-user-listing {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.css-txitvo:hover,
.classified-item-card-container:hover,
.numbered-list-item-section:hover,
.channel-media-nav:hover,
.channel-media:hover,
.classified-item-card-container:hover,
.comment-detail:hover,
.dropdown-menu li:hover,
.final-slide-listing-card-container:hover,
.list-item:hover,
.listing-card-container:hover,
.listing-card:hover,
.neighbor:hover,
.post-container:hover,
.recommendations-top-category-topic-container:hover,
.slick-slide:hover,
.slideshow-thumbnail-container.active,
.value-comp-preview-listing:hover,
.value-comp-preview-user-listing:hover {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

.avatar-image,
.channel-end-of-feed-content-image,
.classified-item-card-photo,
.listing-card-small .listing-card-image,
.pet-card-photo,
.response-modal-body,
.treat-map-legend-header {
	border-radius: 22px!important;
}

.final-slide-listing-image-container img {
	border-top-left-radius:22px!important;
	border-top-right-radius:22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

li,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.post-container:hover span,
li:hover,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}
}

