css:
@-moz-document domain("aurora.iguw.tuwien.ac.at") {
body{
    background: #000;
}
div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, form, fieldset, input, textarea, p, blockquote, th, td, label, #msgInput {
    color: #eee !important; 
    background-color: rgba(32, 32, 32, 0.7) !important;
}

.StickyMessages .staff > .ChatMessageBody, .StickyMessages .staff > .ChatMessageBody *, .staff>.ChatMessageBody {
    background-color: rgb(32, 32, 32) !important;
}

.filterbar, .feed_header, #chat-header{
    background: rgb(52, 180, 80) !important;
    background-image: none !important;
    }

.StickyMessages .ChatMessagePostDate{
    color: #eee !important;
    background: transparent !important;
}

.fadeout {
    background: transparent !important;
}

span {
    color: #eee !important; 
}

a {
    color: #ffdd33 !important;
}

.submitted-content > trix-editor, .finished {
    background: #050505 !important;
    border: 1px solid lightgray !important;
    border-radius: 3px;
}

.submitted-content > trix-editor *, .finished * {
    background: transparent !important;
}

.credits a {
    color: rgba(58, 201, 88, 0.4);
}

.icon {
    filter: invert(100%);
}

.uRhere {
    background: rgba(255, 255, 255, 0.3);
    box-shadow: 0 0 6px rgba(255, 255, 255, 0.7) inset;
    border-bottom: rgba(0, 0, 0, 1);
}

.uRhereB a i {
	color:#30bb00;
	text-shadow:0 0 10px #30bb00;
}

.uRhere a {
    color: #30bb00 !important;
}

.login input[type="checkbox"] + label {
    color: rgba(58, 201, 88, 0.4);
    text-shadow: 0 0 2px rgba(58, 201, 88, 0.7);
}

#error {
    font-size: 1.5em;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    text-shadow: 0 0 5px rgba(255, 0, 0, 1), 0 0 2px rgba(255, 0, 0, 1);
	box-shadow:0 0 20px rgba(255,0,0,0.8) inset;
    background: rgba(0,0,0,0.5);
	border-radius:0.5em;
    cursor: default;
    padding: 0.5em;
    color: rgba(255,128,128, 0.5);
    clear: both;
}

.login input[type="checkbox"]:checked + label {
    color: rgb(58, 201, 88);
    text-shadow: 0 0 1px rgba(58, 201, 88, 1);
}

.login input[type="checkbox"]:checked + label::after {
    content: " \2714";
}

.login .title {
    font-family: 'yanone_kaffeesatzlight';
    color: rgb(58, 201, 88);
    font-size: 2.5em;
    text-shadow: 0 0 6px rgba(0, 0, 0, 0.5);
    margin-bottom: 1em;
}

@media only screen and (max-width: 959px) {
    .login input {
        font: 1.5em Helvetica Neue, Arial, sans-serif;
        background: rgba(0, 0, 0, 0.5);
        color: rgba(58, 201, 88, 1);
        box-shadow: 0 0 20px rgba(58, 201, 88, 0.8) inset;
        width: 100%;
        border: none;
        border-radius: 0.5em;
        padding: 0.5em;
        margin-bottom: 0.5em;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
    }

    .login {
        color: #30bb00;
        width: 100%;
        margin-top: 50px;
        padding: 1em;
        text-align: left;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        background-color: rgba(0, 0, 0, 0.4);
    }
}

@media only screen and (min-width: 960px) {
    .login input {
        font: 1.5em Helvetica Neue, Arial, sans-serif;
        background: rgba(0, 0, 0, 0.5);
        color: rgba(58, 201, 88, 1);
        box-shadow: 0 0 20px rgba(58, 201, 88, 0.8) inset;
        width: 500px;
        border: none;
        border-radius: 0.5em;
        padding: 0.5em;
        margin-bottom: 0.5em;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
    }

    .login {
        color: #30bb00;
        position: absolute;
        width: 600px;
        left: 43%;
        top: 36%;
        margin-left: -200px;
        margin-top: -200px;
        text-align: left;
        padding: 50px;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        background-color: rgba(0, 0, 0, 0.4);
        border-radius: 20px;
    }
}

::-webkit-input-placeholder {
    color: rgba(58, 201, 88, 0.6);
    text-shadow: 0 0 2px rgba(58, 201, 88, 1);
}

::-moz-placeholder {
    color: rgba(58, 201, 88, 0.7);
    text-shadow: 0 0 2px rgba(58, 201, 88, 0.3);
}

/* firefox 19+ */
:-ms-input-placeholder {
    color: rgba(58, 201, 88, 0);
    text-shadow: 0 0 2px rgba(58, 201, 88, 0.3);
}

/* ie */
input:-moz-placeholder {
    color: rgba(58, 201, 88, 0);
    text-shadow: 0 0 2px rgba(58, 201, 88, 0.3);
}

.login_background {
    position: fixed;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
}

.course_selection_background {
    position: fixed;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;

}

.alternate_login {
    text-decoration: none;
    text-transform: uppercase;
    color: rgb(58, 201, 88);
    font-size: 1.5em;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    margin-top: 80px;
    padding-top: 30px;
}

.alternate_login a {
    text-decoration: none;
    color: rgb(58, 201, 88);
    box-shadow: 0 0 20px rgb(58, 201, 88);
    border-radius: 0.5em;
    float: right;
    padding: 0.4em;
}

.alternate_login span {
    padding: 0.4em;
    display: block;
    color: rgba(58, 201, 88, 0.7);
}

.login hr {
    box-shadow: 0 0 5px rgba(58, 201, 88, 0.5), 0 0 2px rgba(58, 201, 88, 0.5);
    border: 1px solid rgba(58, 201, 88, 0.25);
}

.loginButton {
    text-decoration: none;
    text-transform: uppercase;
    color: rgb(58, 201, 88);
    font-size: 1.5em;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    box-shadow: 0 0 20px rgb(58, 201, 88);
    background: rgba(0, 0, 0, 0.5);
    border: none;
    white-space: nowrap;
    border-radius: 0.5em;
    padding: 0.4em;
    margin-top: 0.3em;
    margin-right: 0.1em;
    float: right;
    cursor: pointer;
}

.loginButton:active {
    background-color: rgba(58, 201, 88, 0.8);
    color: white;
}

.remember_me {
    font-size: 1em;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    color: white;
    width: 50%;
    height: 1em;
    float: left;
    margin-top: 1em;
    text-shadow: 0 0 10px rgba(0, 0, 0, 0.4);
}

#checkbox_remember {
    width: auto;
    margin-right: 0.25em;
    margin-bottom: 0;
}

@media only screen and (max-width: 959px) {
    .course_selected {
        font-family: 'yanone_kaffeesatz_rgregular';
        color: white;
        font-size: 32px;
        padding-right: 48px;
        overflow: hidden;
        cursor: pointer;
        display: inline;
        position: absolute;
        bottom: 3px;
    }

    .long_title_text {
        display: none;
    }

    .short_title_text {
        display: inline;
        text-transform: uppercase;
    }
}

@media only screen and (min-width: 960px) {
    .course_selected {
        font-family: 'yanone_kaffeesatz_rgregular';
        color: white;
        font-size: 30px;
        cursor: pointer;
        display: inline;
        position: relative;
        top: 8px;
    }

    .long_title_text {
        display: inline;
    }

    .short_title_text {
        display: none;
    }
}

#course_select {
    z-index: 100000;
    list-style-type: none;
    position: absolute;
    top: 40px;
    left: 150px;
    box-shadow: 0 2px 5px #30bb00;
    padding: 7px;
    border-radius: 4px;
    margin-right: 0;
    margin-left: 0;
    background: rgba(255, 255, 255, 0.95);
    display: none;
}

#course_select li {
    font-family: 'yanone_kaffeesatzthin';
    cursor: pointer;
    padding: 5px;
    color: rgb(110, 184, 99);
    font-size: 20px;
    display: block;
    min-width: 30%;
}

#course_select li.selected:before {
    content: "â€º\A0";
}

.shiftLeft {
    position: relative;
    left: -30px;
    z-index: 4200;
    pointer-events: none;

}

@media all and (max-width: 959px) {
    .pagebottom {
        z-index: 32760;
        position: fixed;
        bottom: -1px;
        height: 48px;
        background: white;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        color: white;
        padding-left: 12px;
        vertical-align: bottom;
        width: 100%;
        border-top: 1px solid #3F6E34;
        box-shadow: 0 0 3px #30bb00;
    }

    .pagebottom > ul {
        list-style: none;
        position: absolute;
        bottom: 8px;
        display: flex;
        flex-direction: row;
        flex-wrap: nowrap;
        justify-content: space-between;
        width: 95%;
    }

    .pagebottom ul li {
        width: 32px;
        font-size: 28px;
        margin-right: 6px;
        margin-left: 6px;
        color: white;
        vertical-align: middle;
        z-index: 32761;
    }

    .pagehead {
        position: fixed;
        z-index: 32760;
        top: -5px;
        background: white;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        height: 48px;
        color: #30bb00;
        padding-left: 12px;
        vertical-align: bottom;
        width: 100%;
        border-bottom: 1px solid #3F6E34;
        box-shadow: 0 0 3px #30bb00;
    }

    .pagehead > ul {
        display: none;
        list-style: none;
        position: absolute;
        bottom: 5px;
    }

    .headline {
        margin-top: 5px;
        margin-left: 0px;
        font-family: 'yanone_kaffeesatzthin';
        font-size: 24px;
    }

    .pagehead ul li {
        font-size: 15px;
        display: inline;
        font-family: 'yanone_kaffeesatz_rgbold';
        margin-right: 0px;
        margin-left: 1px;
    }

    .logout_button {
        text-transform: capitalize !important;
        position: absolute;
        bottom: 3px;
        right: 52px;
        border: none;
        background-color: rgba(0, 0, 0, 0);
        color: #30bb00;
        font-family: 'yanone_kaffeesatz_rgregular';
        cursor: pointer;
        font-size: 32px;
    }

    .user_info {
        cursor: pointer;
        position: absolute;
        top: 0;
        right: 0;
        height: 47px;
        width: 47px;
        opacity: 0.5;
        filter: alpha(opacity=70); /* For IE8 and earlier */
        background-size: 48px 48px;
        background-position: 0 0;
    }

    .icontext {
        display: none;
    }
}

@media all and (min-width: 960px) {
    .pagebottom {
        display: none;
    }

    .pagehead {
        position: relative;
        top: -5px;
        background-clip: border-box;
        background-origin: border-box;
        background: white  0 0;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        min-height: 96px;
        color: #30bb00;
        padding-left: 12px;
        vertical-align: bottom;
        width: 100%;
        border-bottom: 1px solid #3F6E34;
        overflow: hidden;
    }

    .pagehead > ul {
        list-style: none;
        position: absolute;
        bottom: 5px;
    }

    .headline {
        margin-top: 5px;
        margin-left: 10px;
        font-family: 'yanone_kaffeesatzthin';
        font-size: 16px;
    }

    .pagehead ul li {
        font-size: 20px;
        display: inline;
        font-family: 'yanone_kaffeesatz_rgregular';
        margin-right: 4px;
        margin-left: 10px;
        padding: 2px 6px;
    }

    .logout_button {
        text-transform: capitalize !important;
        position: absolute;
        bottom: 5px;
        right: 106px;
        border: none;
        background-color: rgba(0, 0, 0, 0);
        color: #30bb00;
        font-size: 20px;
        font-family: 'yanone_kaffeesatz_rgregular';
        cursor: pointer;
    }

    .user_info {
        cursor: pointer;
        position: absolute;
        top: 0px;
        right: 0px;
        height: 95px;
        width: 95px;
        opacity: 0.5;
        filter: alpha(opacity=70); /* For IE8 and earlier */
        background-size: 96px 96px;
        background-position: 0 0;
    }

    .user_info:hover {
        opacity: 1;
        filter: alpha(opacity=100); /* For IE8 and earlier */
    }

    .unread_notifications {
        text-align: center;
    }

    .unread_notifications > a {
    }
}

.pagehead a, .pagebottom a {
    color: white;
    text-decoration: none;
}

.user_info .user_nick {
    display: none;
    position: absolute;
    width: 96px;
    bottom: -4px;
    left: 0px;
    font-size: 20px;
    font-family: 'yanone_kaffeesatz_rgregular';
    color: white;
    text-shadow: 0 0 2px #30bb00;
    text-align: center;
    padding-bottom: 0.4em;
}

.user_info:hover .user_nick, .user_info:hover .profile_link {
    display: block;
}

.user_info .profile_link {
    display: none;
    position: absolute;
    width: 96px;
    top: 96px;
    left: 0;
    font-size: 11px;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    text-transform: uppercase;
    color: rgb(58, 201, 88);
    text-align: center;
}

#avatar {
    min-height: 96px;
    min-width: 96px;
}

.profile_img {
    width: 96px;
    height: 96px;
    overflow: hidden;
}

.glow {
    border: none;
    color: white;
}

button {
    text-decoration: none;
    text-transform: uppercase;
    color: rgb(58, 201, 88);
    margin-left: 6px;
    font-size: 11px;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    border-radius: 2px;
    background-color: rgba(0, 0, 0, 0);
    border: none;
    white-space: nowrap;
    cursor: pointer;
}

.button:active, button:active {
    background: rgb(58, 201, 88) !important;
    color: white !important;
}

button:hover, .button:hover {
    text-shadow: 0 0 5px rgba(58, 201, 88, 0.3);
}

.XL {
    font-size: 16px !important;
}

.R {
    float: right;
}

.button {
    text-decoration: none;
    text-transform: uppercase;
    color: rgb(58, 201, 88);
    margin-left: 6px;
    font-size: 10px;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    -webkit-appearance: none;
    -moz-appearance: none;
    border-radius: 2px;
    appearance: none;
    background-color: rgba(0, 0, 0, 0);
    border: none;
    cursor: pointer;
    white-space: nowrap;
}

@media only screen and (max-width: 959px) {
    .comment_section {
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        background-color: rgb(242, 255, 242);
        padding: 0.75em;
        margin: 8px 0 8px 0;
        width: 100%;
        border-top: 1px dashed silver;
        border-bottom: 1px dashed silver;
    }
}

@media only screen and (min-width: 959px) {
    .comment_section {
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        background-color: rgb(242, 255, 242);
        padding: 0.75em;
        margin: 8px 0 8px 0;
        width: 100%;
        border-top: 1px dashed silver;
        border-bottom: 1px dashed silver;
    }
}

.nothing_to_see_move_on {
    display: none;
    visibility: hidden;
}

.month {
    width: 60px;
}

.day {
    width: 40px;
    text-align: right;
}

.maybe {
    color: darkgray;
}

.not {
    color: #e8e8e8;
}

.trix-content {
    min-height: 300px;
}


}
