	height: 20px;
    pointer-events: all;
}
div#SearchDropdown > label {
	position: absolute;
    top: 0;
    right: 0;
	margin-top: 0;
	margin-right: 10px;
}
div#SearchDropdown > label > svg {
	position: absolute;
    top: 0;
    right: 0;
	fill: rgb(54, 118, 183);
    transform: scaleY(0.68) scaleX(-0.68);
}

div#SearchDropdown > form > input {
	border-color: rgb(104, 104, 104);
