/* ==UserStyle==
@name           Loadingalternativet 2.0-beta
@namespace      github.com/openstyles/stylus
@version        2.0.0-b
@description    Ett tema för Loading.se
@author         Kenny
==/UserStyle== */
@-moz-document url-prefix("https://loading.se") {

/*Generellt*/
.App {
    background: #000000;
    color: #cccccc;
}

.custom-loadingkalendern-note {
    background: #fd2860;
}

a:link {
    color: #3399ff
}

a:visited {
    color: #ffffff
}

/*Framsidan*/
.List-container {
    background: #000000;
}

.Row-forum-container {
    background: #262626;
    margin-bottom: 0px;
}

.Row-forum-container:hover {
    background: #000000;
    transition: all .15s ease;
}


.Home-link-wrapper {
    background-color: #262626
}

.Home-link-wrapper:hover {
    background: #000000;
}

/*Forumvyn*/
body {
    background: #000000;
}

.Games-page,
.Other-page,
.profile-page {
    max-width: none;
    margin: 0 auto;
}

.Page-list-header {
    padding: 0 20px;
}

.Page-list {
    margin: 0 auto;
    max-width: none;
}

.Games-info {
    color: #cccccc;
    margin: 9px 40px;
}

.Row-white-container {
    background: #262626;
    color: #cccccc;
    padding: 4px;
}

.Row-links-container:hover .Row-white-container {
    background: #000;
    transition: all .15s ease;
}

.Row-forum-container {
    padding: 0px 0 0;
    margin-bottom: 2px;
}

.Row-bubble-container {
    background: #262626;
    color: #cccccc;
    margin: 0px 1px 1px 0;
}

.Row-links-container {
    border-radius: 0px;
    margin: 5px 15px;
}

.Row-links-container a:first-child {
    padding: 0px 0 0px 5px;
}

.Row-latest-written {
    right: 120px;
    bottom: 20px;
}

.Row-links-container:hover .Row-bubble-container {
    background: #000000;
    transition: all .15s ease;
}

.Row-title {
    color: #cccccc;
}

.Page-list-submit-post {
    max-width: none;
}

.Page-list-submit-post .editor-user {
    max-width: none;
    background-color: #262626;
}

/*Inne i trådar*/
.Boop-container {
    background: #000000;
    color: #cccccc;
}

.PostItem-container {
    background: #000000;
    color: #cccccc;
    padding: 0 22px 10px;
}

.PostItem-body {
    background: #000000;
    margin-left: 70px;
    padding: 20px 10px 10px 50px;
}

.PostItem-date {
    padding: 1px;
}

.PostItem-text {
    background: #262626;
    color: #e6e6e6;
}

.PostItem-text p img {
    max-width: 100%;
    max-height: 100%;
}

.PostItem-editorial {
    max-width: 100%;
    text-align: left;
}

.Post-container {
    max-width: 100%;
}

.PostList-boxlink {
    background: #262626;
}

.PostList-boxlink-active {
    background: #fd2860;
}

.PostItem-portrait-container img {
    width: 100px;
    height: 100px;
    border-radius: 3px;
}

.PostItem-regular blockquote {
    background-color: #444444;
}

/* Posteditorn */
.editor-toolbar a {
    color: #e6e6e6 !important;
}

.CodeMirror {
    background: #444444;
    color: #eeeeee;
}

/* Redax-sidan */
.About-text {
    background: #262626;
    color: #e6e6e6;
}

/* Login och registrering */
.Signup-input {
    background: #444444;
    color: #eeeeee;
}

/* Profilsida */
.profile-user-info {
    background-color: #444444;
}
}
