css:
@-moz-document url-prefix("https://wow.boomlearning.com/student") {
.bar.bar-positive {
    border-color: #b81d1d;
    border-top-color: rgb(184, 29, 29);
    border-right-color: rgb(184, 29, 29);
    border-bottom-color: rgb(184, 29, 29);
    border-left-color: rgb(184, 29, 29);
    background-color: #dd2b2b;
    background-image: linear-gradient(0deg,#b81d1d,#b81d1d 50%,transparent 0);
    color: #fff;
}

.button.button-calm {
    border-color: #dc0303;
    border-top-color: rgb(220, 3, 3);
    border-right-color: rgb(220, 3, 3);
    border-bottom-color: rgb(220, 3, 3);
    border-left-color: rgb(220, 3, 3);
    background-color: #fc1616;
    color: #fff;
}

.button.button-calm:hover {
    color: #fff;
    background-color: #dc0303;
    text-decoration: none;
    text-decoration-line: none;
    text-decoration-style: initial;
    text-decoration-color: initial;
}
.tabs-striped.tabs-positive .tabs {
    background-color: #dd2b2b;
}

.accountBioInfo:before {
    content: "";
    display: block;
    width: 100%;
    height: 120px;
    background-color: #dd2b2b;
    position: absolute;
    left: 0;
    top: 0;
    z-index: -1;
}
div[class*=item-badges-card].current, div[class*=item-badges-deck].current {
    background: rgba(255, 0, 0, .1);
    background-image: initial;
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat-x: initial;
    background-repeat-y: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: rgba(255, 0, 0, .1);
}

.tabs-top.tabs-positive.tabs-striped .tab-item.activated, .tabs-top.tabs-positive.tabs-striped .tab-item.active, .tabs-top.tabs-positive.tabs-striped .tab-item.tab-item-active {
    border: 0;
    border-top-color: initial;
    border-top-style: initial;
    border-top-width: 0px;
    border-right-color: initial;
    border-right-style: initial;
    border-right-width: 0px;
    border-bottom-color: initial;
    border-bottom-style: initial;
    border-bottom-width: 0px;
    border-left-color: initial;
    border-left-style: initial;
    border-left-width: 0px;
    border-image-source: initial;
    border-image-slice: initial;
    border-image-width: initial;
    border-image-outset: initial;
    border-image-repeat: initial;
    padding-top: 4px;
    height: 51px;
    background-color: #e86262;
    font-weight: 700;
    box-shadow: 0 1px 5px rgba(0,0,0,.3);
}

.bar.bar-positive {
    border-color: #b81d1d;
    border-top-color: rgb(184, 29, 29);
    border-right-color: rgb(184, 29, 29);
    border-bottom-color: rgb(184, 29, 29);
    border-left-color: rgb(184, 29, 29);
    background-color: #dd2b2b;
    background-image: linear-gradient(0deg,#b81d1d,#b81d1d 50%,transparent 0);
    color: #fff;
}

.tabs-striped.tabs-positive .tabs {
    background-color: #dd2b2b;
}

.gemCaption {
    color: #b00;
    font-size: 12px;
    position: absolute;
    top: 62px;
    left: 0;
    right: 0;
    height: 100px;
    z-index: 2;
    text-decoration: none;
    text-decoration-line: none;
    text-decoration-style: initial;
    text-decoration-color: initial;
    text-align: center;
    line-height: 8px;
}
}
