css:
@-moz-document url-prefix(https://jira) {
  /* Colors */
  :root
  {
      --DASHBOARD-BC: #1d1e20;
      --BG-BODY: #252A35;
      --BC-BODY: #848484;
      --ATEXTCOLOR: #4B97F8;
      --I-BG: #2C3037;
      --I-C: #EBEBEB;
      --EM-C: #BEBEBE;
      --AUI-BC: #3C414C;
      --GHX-BC: v434953;
      --S-BC: #6A25AA;
      --DIALOG-C: #888888;
      --B-C: #585A62;
      --JI-C: #14892c;
      --JSS-C: #668fba;
      --JIS-C: #c6ae65;
      --AUI-SUBTLE: #ffe9a8;
      --AUI-SUBTLE-BC: #f6c342;
      --CQ-C: #57a64a;
      --CK-C: #559cd6;
  }

  /* General */
  body {
      background-color: var(--BG-BODY) !important;
  }

  html,
  body,
  body * {
      border-color:  var(--BG-BODY) !important;
  }

  a {
      color: var(--ATEXTCOLOR) !important;
  }

  input {
      background: var(--I-BG) !important;
      color: var(--I-C) !important;
  }

  em {
      color: var(--EM-C) !important;
  }

  /* Track */
  ::-webkit-scrollbar-track {
      background: var(--EM-C) !important;
  }

  /* Handle */
  ::-webkit-scrollbar-thumb {
      background: var(--AUI-BC) !important;
  }

  /* Handle on hover */
  ::-webkit-scrollbar-thumb:hover {
      background: var(--AUI-BC) !important;
  }

  ::selection {
      background: var(--S-BC) !important;
  }

  ::-moz-selection {
      background: var(--S-BC) !important;
  }

  .aui-header {
      border-color: var(--BC-BODY) !important;
      border-bottom: 1px solid;
  }

  .aui-sidebar .aui-sidebar-wrapper {
      border-color: var(--BC-BODY) !important;
      border-right: 1px solid;
  }

  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group:focus,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group:focus,
  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group:hover,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group:hover,
  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group.active,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group.active {
      background: transparent !important;
  }

  .action-description {
      color: var(--EM-C) !important;
  }

  .issue-drop-zone__button {
      color: var(--ATEXTCOLOR) !important;
  }

  form.aui div.description {
      color: var(--EM-C) !important;
  }

  .jira-dialog .dialog-menu-group .dialog-menu-item.selected {
      color: var(--DIALOG-C) !important;
  }

  .jira-dialog .dialog-menu-group .dialog-menu-item {
      color: var(--ATEXTCOLOR) !important;
  }

  .aui-select2-container.select2-container .select2-choice,
  .aui-select2-container.select2-container a.select2-choice,
  .aui-select2-container.select2-container .select2-choice:visited {
      background: var(--BG-BODY) !important;
      color: var(--I-C) !important;
      text-shadow: none !important;
      border-color: var(--AUI-BC) !important;
  }

  .ct-toolbar {
      background: var(--BG-BODY) !important;
  }

  .jira-multi-select > .representation .value-item {
      -moz-border-radius: 0 !important;
      border-radius: 0 !important;
      background: var(--BG-BODY) !important;
      color: var(--ATEXTCOLOR) !important;
  }

  .editable-field .overlay-icon {
      background: var(--I-BG) !important;
  }

  .ghx-column-headers .ghx-release.ghx-disabled {
      color: var(--DIALOG-C) !important;
  }

  .ghx-controls-filters dd a {
      color: var(--I-C) !important;
  }

  .ghx-controls-filters dd a.ghx-active {
      color: var(--I-C) !important;
      background: var(--I-BG) !important;
  }

  .ghx-classification-item:hover,
  .ghx-classification-item.ghx-selected {
      background-color: var(--GHX-BC) !important;
  }

  .ghx-classification-item > .ghx-inner {
      border: none;
  }

  #dashboard,
  .gadget .dashboard-item-content {
      background-color: var(--BG-BODY) !important;
  }

  #footer-logo {
      background-color: var(--BG-BODY) !important;
  }

  #issue-content {
      background-color: var(--BG-BODY) !important;
  }

  #issuetable,
  .issue-table,
  #issuetable > thead .active,
  .issue-table > thead .active,
  #issuetable > tbody > tr,
  .issue-table > tbody > tr,
  #issuetable > tfoot > tr,
  .issue-table > tfoot > tr,
  .issuerow {
      background-color: var(--BG-BODY) !important;
  }

  .activity-item-description .user-content {
      color: var(--EM-C) !important;
  }

  .activity-item-description blockquote {
      background-color: var(--I-BG) !important;
      color: var(--B-C) !important;
  }

  .aui-item {
      color: var(--I-C) !important;
  }

  .ajs-layer {
      background-color: var(--I-BG) !important;
  }

  .applinks-auth-request {
      background-color: var(--I-BG) !important;
  }

  .aui-badge {
      background-color: var(--ATEXTCOLOR) !important;
      color: var(--I-C) !important;
  }
  aui-badge, .aui-badge {
      color: var(--I-C) !important;
  }

  .ghx-issue-compact .ghx-end .ghx-statistic-badge {
      border: 1px solid;
  }

  .aui-button.aui-button-primary {
      background-color: var(--ATEXTCOLOR) !important;
      color: var(--I-C) !important;
  }

  .aui-button.ghx-dropdown-trigger:after,
  .aui-buttons .aui-button.aui-button-primary.ghx-dropdown-trigger:after {
      border-top-color: var(--I-C) !important;
  }

  .aui-dropdown2.aui-style-default {
      background: var(--BG-BODY) !important;
  }

  .aui-dropdown2.aui-style-default:hover .aui-dropdown2-checkbox,
  .aui-dropdown2.aui-style-default:hover .aui-dropdown2-radio,
  .aui-dropdown2.aui-style-default:hover a {
      background: var(--BG-BODY);
  }

  .aui-header .aui-dropdown2-trigger {
      background-color: var(--BG-BODY) !important;
      color: var(--I-C) !important;
  }

  .aui-dropdown2.aui-style-default > strong,
  .aui-dropdown2.aui-style-default .aui-dropdown2-section > strong,
  .aui-dropdown2-heading {
      color: var(--I-C) !important;
  }

  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group.aui-sidebar-group-actions:hover:after,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group.aui-sidebar-group-actions:hover:after,
  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group.aui-sidebar-group-actions:focus:after,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group.aui-sidebar-group-actions:focus:after,
  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group.aui-sidebar-group-actions.active:after,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group.aui-sidebar-group-actions.active:after {
      background-color: var(--BG-BODY) !important;
      border-color: var(--I-C) !important;
  }

  .aui-header,
  .aui-sidebar,
  .aui-sidebar-wrapper {
      background-color: var(--BG-BODY) !important;
      color: var(--EM-C) !important;
  }

  .aui-icon-small:before,
  .aui-icon-large:before {
      color: var(--I-C) !important;
  }

  .aui-label {
      color: var(--I-C) !important;
  }

  .aui-dropdown2.aui-style-default [role="menuitem"],
  .aui-dropdown2.aui-style-default [role="menuitemcheckbox"],
  .aui-dropdown2.aui-style-default [role="menuitemradio"],
  .aui-dropdown2.aui-style-default [role="radio"],
  .aui-dropdown2.aui-style-default [role="checkbox"],
  .aui-dropdown2.aui-style-default a {
      color: var(--I-C) !important;
  }

  .aui-dropdown2.aui-style-default:hover .aui-dropdown2-checkbox:not(.aui-dropdown2-disabled):hover,
  .aui-dropdown2.aui-style-default:hover .aui-dropdown2-radio:not(.aui-dropdown2-disabled):hover,
  .aui-dropdown2.aui-style-default:hover a:not(.aui-dropdown2-disabled):hover {
      text-decoration: underline !important;
      color: var(--I-C) !important;
  }

  .aui-lozenge-current.aui-lozenge-subtle {
      border-color: var(--AUI-SUBTLE) !important;
      color: var(--AUI-SUBTLE-BC) !important;
  }

  .aui-lozenge.aui-lozenge-subtle {
      background-color: var(--I-BG) !important;
  }

  .aui-lozenge.jira-issue-status-lozenge-blue-gray {
      background-color: transparent !important;
      border-color: var(--JSS-C) !important;
      color: var(--JSS-C) !important;
  }

  .aui-lozenge.jira-issue-status-lozenge-green {
      background-color: transparent !important;
      border-color: var(--JI-C) !important;
      color: var(--JI-C) !important;
  }

  .aui-lozenge.jira-issue-status-lozenge-yellow {
      background-color: transparent !important;
      border-color: var(--JIS-C) !important;
      color: var(--JIS-C) !important;
  }

  .aui-message.error,
  .aui-message-error {
      background: var(--I-BG) !important;
      background-color: var(--I-BG) !important;
      color: var(--EM-C) !important;
  }

  .aui-message.hint,
  .aui-message-hint,
  .aui-message.info,
  .aui-message-info {
      background-color: var(--I-BG) !important;
      color: var(--EM-C) !important;
  }

  .aui-message.warning,
  .aui-message-warning {
      background: var(--I-BG) !important;
      background-color: var(--I-BG) !important;
      color: var(--EM-C) !important;
  }

  .aui-sidebar-submenu .aui-navgroup-horizontal .aui-nav > li a:focus,
  .aui-sidebar-submenu .aui-navgroup-vertical .aui-nav > li > a:focus,
  .aui-sidebar-submenu .aui-nav-vertical > li > a:focus,
  .aui-sidebar-submenu .aui-navgroup-horizontal .aui-nav > li a:hover,
  .aui-sidebar-submenu .aui-navgroup-vertical .aui-nav > li > a:hover,
  .aui-sidebar-submenu .aui-nav-vertical > li > a:hover {
      background: transparent !important;
  }

  .aui-navgroup-horizontal .aui-nav > li a:focus,
  .aui-navgroup-vertical .aui-nav > li > a:focus,
  .aui-nav-vertical > li > a:focus,
  .aui-navgroup-horizontal .aui-nav > li a:hover,
  .aui-navgroup-vertical .aui-nav > li > a:hover,
  .aui-nav-vertical > li > a:hover {
      background: var(--BG-BODY) !important;
  }

  .aui-page-panel {
      background-color: var(--BG-BODY) !important;
  }

  .aui-sidebar .aui-nav > li > .aui-nav-item > .aui-icon,
  .aui-sidebar-submenu .aui-nav > li > .aui-nav-item > .aui-icon {
      color: var(--I-C) !important;
  }

  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group.aui-sidebar-group-actions:after,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group.aui-sidebar-group-actions:after {
      background-color: var(--BG-BODY) !important;
      border: 1px solid var(--AUI-BC) !important;
  }

  .aui-sidebar[aria-expanded=false] .aui-navgroup-vertical .aui-nav .aui-nav-child-selected a,
  .aui-sidebar-submenu[aria-expanded=false] .aui-navgroup-vertical .aui-nav .aui-nav-child-selected a,
  .aui-sidebar .aui-navgroup-vertical .aui-nav .aui-nav-selected a,
  .aui-sidebar-submenu .aui-navgroup-vertical .aui-nav .aui-nav-selected a {
      background-color: var(--AUI-BC) !important;
  }

  .aui-theme-default .aui-button.aui-button-subtle {
      color: var(--EM-C) !important;
  }

  .aui-theme-default .gf-button-cell a.gf-button,
  .gf-button {
      background-color: var(--I-BG) !important;
      background-image: none !important;
  }

  .aui-toolbar .toolbar-group .toolbar-trigger,
  .aui-toolbar .aui-dd-parent a.aui-dd-trigger {
      background-color: var(--I-BG) !important;
      text-shadow: none !important;
  }

  .blender {
      background-image: none !important;
  }

  .details-layout .split-view .list-results-panel .list-panel .issue-list > li.focused {
      background-color: var(--I-BG) !important;
  }

  .editable-field .save-options {
      background-color: var(--I-BG) !important;
  }

  .editable-field form.aui .text,
  .editable-field form.aui .password,
  .editable-field form.aui .textarea,
  .editable-field form.aui .select,
  .editable-field form.aui .multi-select {
      box-shadow: none !important;
  }

  .editable-field:hover,
  .editable-field.active {
      background-color: var(--BG-BODY) !important;
  }

  .gadget.color1 .dashboard-item-header,
  .gadget-colors .color1 > a {
      background-color: var(--I-BG) !important;
  }

  .ghx-backlog {
      background-color: var(--BG-BODY) !important;
  }

  .ghx-backlog-container .ghx-backlog-header {
      border: none !important;
  }

  .ghx-backlog-container,
  .stuck {
      background-color: var(--BG-BODY) !important;
  }

  .ghx-backlog-header .ghx-name {
      color: var(--EM-C) !important;
  }

  .ghx-column-headers .ghx-column {
      border-bottom: 5px solid var(--I-BG) !important;
  }

  .ghx-columns .ghx-column {
      background: var(--I-BG) !important;
  }

  .ghx-column-headers .ghx-column {
      background: var(--BG-BODY) !important;
  }

  .ghx-days b {
      -webkit-filter: invert(100%) !important;
      filter: invert(100%) !important;
  }

  .ghx-detail-contents {
      background: var(--BG-BODY) !important;
  }

  .ghx-detail-contents .ghx-detail-view-blanket {
      background: var(--BG-BODY) !important;
  }

  .ghx-detail-issue {
      color: var(--I-C) !important;
  }

  .ghx-detail-nav-item b {
      background: none !important;
      color: inherit !important;
  }

  .ghx-detail-nav-item.ghx-selected {
      background-color: var(--I-BG) !important;
  }

  .ghx-detail-nav-item.ghx-selected b {
      background: none !important;
  }

  .ghx-detail-view.gh-editable-detail-view #commentmodule .tabwrap .sortwrap .issue-activity-sort-link {
      background-color: var(--I-BG) !important;
  }

  .ghx-detail-view.gh-editable-detail-view .ghx-detail-issue .ghx-fa {
      color: var(--EM-C) !important;
  }

  .ghx-drag-in-progress .ghx-backlog-container.ghx-open .ghx-no-issues .ghx-helper,
  .ghx-drag-in-progress .ghx-backlog-container.ghx-kanban-backlog .ghx-no-issues .ghx-helper {
      background: var(--BG-BODY) !important;
  }

  .ghx-drag-in-progress .ghx-backlog-container.ghx-open.ghx-target-hover .ghx-no-issues .ghx-helper,
  .ghx-drag-in-progress .ghx-backlog-container.ghx-kanban-backlog.ghx-target-hover .ghx-no-issues .ghx-helper,
  .ghx-drag-in-progress .ghx-backlog-container.ghx-open.ghx-target-hover .ghx-plan-dropzone.ghx-dropzone-active,
  .ghx-drag-in-progress .ghx-backlog-container.ghx-kanban-backlog.ghx-target-hover .ghx-plan-dropzone.ghx-dropzone-active {
      background: var(--BG-BODY) !important;
  }

  .ghx-extra-field {
      color: var(--EM-C) !important;
  }

  .ghx-iconfont.aui-icon-small:before,
  .ghx-iconfont.aui-icon-large:before {
      color: var(--I-C) !important;
  }

  .ghx-inner {
      color: var(--I-C) !important;
  }

  .ghx-issue {
      background-color: var(--BG-BODY) !important;
  }

  .ghx-issue-compact {
      background-color: var(--I-BG) !important;
      color: var(--EM-C) !important;
  }

  .ghx-issue-compact .ghx-end {
      background-color: var(--I-BG) !important;
      color: var(--EM-C) !important;
  }

  .ghx-marker {
      background-color: var(--BG-BODY) !important;
  }

  .ghx-plan-dropzone.ghx-dropzone-active {
      background: repeating-linear-gradient( 45deg,
      var(--BG-BODY),
      var(--BG-BODY) 10px,
      var(--I-BG) 10px,
      var(--I-BG) 20px) !important;
  }

  .ghx-swimlane-header .ghx-bandaid {
      background-color: var(--I-BG) !important;
      box-shadow: none !important;
  }

  .ghx-swimlane-header.ghx-selected {
      background-color: var(--I-BG) !important;
  }

  .ghx-swimlane-header:after {
      background-color: var(--I-BG) !important;
      box-shadow: none !important;
  }

  .ghx-zone-cell {
      background: repeating-linear-gradient( 45deg,
      var(--BG-BODY),
      var(--BG-BODY) 10px,
      var(--I-BG) 10px,
      var(--I-BG) 20px) !important;
  }

  .iic-widget,
  .iic-widget__form {
      background-color: var(--BG-BODY) !important;
  }

  .iic-widget__summary {
      color: var(--I-C) !important;
  }

  .issue-body-content .module > .mod-header .ops {
      background-color: var(--I-BG) !important;
  }

  .issue-body-content .module > .mod-header > h1,
  .issue-body-content .module > .mod-header > h2,
  .issue-body-content .module > .mod-header > h3,
  .issue-body-content .module > .mod-header > h4,
  .issue-body-content .module > .mod-header > h5,
  .issue-body-content .module > .mod-header > h6 {
      background-color: var(--BG-BODY) !important;
  }

  .issue-data-block {
      background-color: var(--I-BG) !important;
  }

  .issue-header .issue-header-content > .command-bar {
      background-color: var(--BG-BODY) !important;
  }

  .issue-view > .issue-container .issue-header {
      background-color: var(--BG-BODY) !important;
  }

  .item-attachments .attachment-content {
      background-color: var(--I-BG) !important;
  }

  .jira-dialog {
      background-color: var(--BG-BODY) !important;
  }

  .jira-dialog .buttons-container {
      background: var(--BG-BODY) !important;
  }

  .jira-dialog .buttons-container .hint-container {
      color: var(--B-C) !important;
  }

  .jira-dialog .jira-dialog-content {
      background: var(--BG-BODY) !important;
  }

  .jira-dialog .jira-dialog-heading {
      background: var(--BG-BODY) !important;
  }

  .jira-multi-select > .icon.drop-menu {
      background: var(--I-BG) !important;
  }

  .jira-multi-select > .icon.drop-menu:after {
      color: var(--I-C) !important;
  }

  .links-list dd {
      background-color: var(--I-BG) !important;
  }

  .loading {
      background-color: var(--BG-BODY) !important;
  }

  .navigator-search {
      background-color: var(--BG-BODY) !important;
  }

  .no-results {
      background: var(--I-BG) !important;
      background-color: var(--I-BG) !important;
      color: var(--EM-C) !important;
  }

  .ops li a.drop-menu.active,
  .ops li a.drop-menu.active:hover,
  .ops li a.drop-menu:hover {
      background-color: var(--I-BG) !important;
  }

  .page-type-split .navigator-body {
      background-color: var(--BG-BODY) !important;
  }

  .page-type-split .navigator-group {
      background-color: var(--BG-BODY) !important;
  }

  .preformatted,
  .code {
      background: var(--I-BG) !important;
  }

  .aui-list li.disabled a:link,
  .aui-list li.disabled a:visited,
  .aui-list li.disabled a:focus,
  .aui-list li.disabled a:hover,
  .aui-list li.disabled.active a:link,
  .aui-list li.disabled.active a:visited,
  .aui-list li.disabled.active a:focus,
  .aui-list li.disabled.active a:hover,
  .aui-list li a.disabled:link,
  .aui-list li a.disabled:visited,
  .aui-list li a.disabled:focus,
  .aui-list li a.disabled:hover,
  .aui-list li.active a.disabled:link,
  .aui-list li.active a.disabled:visited,
  .aui-list li.active a.disabled:focus,
  .aui-list li.active a.disabled:hover {
      background-color: transparent !important;
  }

  .property-list .item .name,
  .item-details dl > dt {
      color: var(--I-C) !important;
  }

  .saved-search-selector {
      background-color: var(--BG-BODY) !important;
  }

  .gf-testing-label {
      color: var(--DIALOG-C) !important;
  }

  .split-view #stalker,
  .split-view .issue-header {
      background-color: var(--BG-BODY) !important;
  }

  .tabwrap > .tabs.horizontal > li {
      background-color: var(--BG-BODY) !important;
  }

  .tabwrap > .tabs.horizontal > li.active > a,
  .tabwrap > .tabs.horizontal > li.active > strong {
      background-color: var(--BG-BODY) !important;
  }

  .trigger-label {
      color: var(--I-C) !important;
  }

  .tabwrap > ul.tabs > li a,
  .tabwrap > ul.tabs > li a:link,
  .tabwrap > ul.tabs > li a:visited {
      background-color: var(--BG-BODY) !important;
  }

  .wiki-edit-toolbar {
      background-color: var(--I-BG) !important;
  }

  .aui-nav-heading,
  .aui-navgroup-vertical .aui-nav-heading {
      color: var(--I-C) !important;
  }

  a.aui-button {
      background: var(--I-BG) !important;
      color: var(--I-C) !important;
      border-color: var(--AUI-BC) !important;
