css:
@-moz-document domain("free3d.com") {
/**** Main Page ****/

body {
  color: #aaa;
}

html, button, input, select, textarea {
  color: #aaa;
}

.over {
  background-color: #282828;
}

/* Topmost bar - 'Free 3D models', 'Premium 3D models' */
.top-bar .nav-links .nav-link {
  color: #ccc;
}

/* Top list of categories */
.h-short-container .short-block img {
  filter: invert(84%);
}

.h-short-container .short-block {
  color: #aaa;
}

.h-short-container .short-block > div .label {
  color: #999;
}

/** Products, Notifications, Mails > Dropdown **/

.outside_message_cont > .relative {
  background-color: #282828;
}

.alert {
  background-color: #282828;
  border-left-color: #282828;
}

.outside_message_cont .top_triangle {
  border-bottom-color: #282828;
}

/* Profile */
.account_avatar {
  color: #ccc;
  background-color: #333;
}

.top-bar .account-hamburger {
  color: #aaa;
}

/** Profile > Dropdown **/

.outside_panel_cont .top_triangle {
  border-bottom-color: #282828;
}

.outside_panel_cont > .relative {
  background-color: #282828;
}

.outside_panel_cont a {
  color: #aaa;
}

.panel-menu > ul > li > a:hover {
  background-color: #333;
  color: #1c86c3; /* #1c5fc3; */
}

.panel-menu > ul > li.green > a {
  background-color: #222;
}

/* 'Logout' */
.panel-menu > ul > li:last-child > a {
  background-color: #222;
}

.panel-menu > ul > li:last-child:hover > a {
  background-color: #333;
}

/** Left-side Headers **/

.h-categs .h-categ .copy-block .categ-title a {
  color: #aaa;
}

.h-categs .h-categ .copy-block .copy {
  color: #999;
}

/** Item **/

.search-result__title .link {
  color: #aaa;
}

.search-result__format {
  color: #aaa;
}

/** 'Selections' section **/

.h-title-container, .h-fWidth-cont {
  background-color: #333 !important;
}

.h-title-container h3 {
  color: #aaa;
}

.h-title-container .subtitle {
  color: #aaa;
}

.selection_entry .sel_title {
  color: #ccc;
}

/** 'Best Sellers' section **/

.premium .search-result__title .link {
  color: #aaa;
}

/* * floating '?' disk button ** /
.uv-popover-loading {
  background-color: #282828;
} */

/* Footer */

footer {
  color: #aaa;
}

.footer-top .awesome-logo, .footer-top .middle-links .navigate, .footer-top .middle-links .search {
  color: #ccc;
}

.footer-top .middle-links li a {
  color: #aaa;
}

/*** Overlay on clicking item ***/

.single-page-preview__content {
  background-color: #282828;
}

.product-main-info {
  background-color: #282828aa;
}

/* Close button */
.single-page-preview__close {
  background-color: #282828;
}

/* 'Personal Use License' */
.product-main-details__license-container {
  border-top-color: #666;
}

.product-main-info .product-meta-public {
  border-top-color: #666;
}

/* Bookmark/Heart button */
.btn-bookmark-product, .btn-bookmark-product-temp {
  color: #999;
  background-color: #333;
  border-color: #666;
}

.btn-action {
  box-shadow: 0 6px 6px -4px rgba(100,100,100,.3);
}

/* Image */
.product-slider-container .large-image-slider .slick-list {
  background-color: #282828;
}

/* 'Specifications', 'Formats & Files', 'Description' */
.product-page-box-title {
  border-bottom-color: #666;
}

/* Specifications alternate Row */
.product-specs-list .product-spec.even {
  background-color: #333;
}

/* Left options */
.similar-products-container {
  background-color: #282828;
}

.similar-products-container .search-result__title a.link {
  color: #aaa;
}

/* 'Add your comment' */
.product-comment__add-form--title {
  border-bottom-color: #666;
}

.btn-stock.black {
  background-color: #222;
  color: #aaa;
}

/* 'Reviews' */
.basic-notice {
  background-color: #333;
  color: #aaa;
}

/* Comments */
.product-comment--body {
  background-color: #333;
}

.product-comment__meta {
  border-top-color: #666;
}

.product-comment__add-reply {
  border-left-color: #666;
  border-right-color: #666;
}

.product-comment--body::before {
  border-right-color: #333;
}

input:not([type="submit"]):not([type="file"]), textarea {
  border-color: #666;
}


/**** /dashboard/bookmarks Page ****/

body {
  background-color: #282828;
}

.pan-body-block {
  background-color: #282828;
  box-shadow: 0 1px 1px #333;
}

.model-entry .det .ent_title {
  color: #aaa;
}

.model-entry .det .price.free {
  color: #1c5fc3;
}

/**** /3d-models Page ****/

/* 'N' Results message on top */
.search-results__header {
  color: #aaa;
}

.search-filters-container .vert-hr {
  background-color: #666;
}

.search-filters-container a {
  color: #aaa;
}

.search-filters-container a:hover {
  color: #ccc;
}

.search-filters-container a.selected {
  color: #1c86c3;
}

/* 'Why try premium?' box */

.premium-selling-points-wrapper {
  background-color: #333;
}

.product-main-details__product-license {
  color: #aaa;
}

/* Page No.s */
.search-results-paging__links li .paging-link.inactive {
  color: #aaa;
  background-color: #222;
  border-color: #666;
}

.search-results-paging__links li .paging-link {
  color: #aaa;
  border-color: #666;
}

/**** /add page ****/

.add-container .number {
  background-color: #333;
  color: #aaa;
}

.add-drop-spot {
  background-color: #333;
  border-color: #666;
}

.add-block {
  background-color: #333;
}

.add-block .title {
  color: #aaa;
}

.btn-stock {
  background-color: #282828;
}

.alert.alert-info {
  background-color: #282828;
}

.custom-checkbox input[type="checkbox"]:checked + label span.text {
  color: #aaa;
}

/**** /dashboard/products/1 - My 3D Models Page ****/

/* Draft, In Review, Private */
.legend .type {
  background-color: #333;
  box-shadow: 0 1px 3px #666;
}

/* 'Important stuff to know' box */
.helpnote {
  background-color: #333;
}

.helpnote .helppad ul {
  color: #aaa;
}

.helpnote .helppad {
  border-left-color: #666;
}


/**** /user/<username> Page ****/

.usr-info-cont {
  background-color: #333;
}

.usr-info .block {
  background-color: #282828;
  box-shadow: 0 1px 1px #666;
}

.usr-info .bio {
  color: #aaa;
}

.usr-info .bio .icon {
  color: #999;
}

/* 'Send Message' box */
.btn-line {
  background-color: #333;
}

}
