css:
@-moz-document domain(docs.djangoproject.com) {
body {
    color: #ccc;
    background-color: #444;
}
[role="main"] {
    background-color: #333;
}
[role="main"] .section {
    border-bottom-color: #282828;
}
.mdzr-boxshadow .sidebar-right [role="main"] {
    box-shadow: 0px 0px 0 0 #333, 0px 0px 0 0 #333, -460px 0 0 0 #333, -1000px 0 0 0 #333, 0px 600px 0 0px #333, -460px 600px 0 0px #333;
}

.note, .admonition, .help-block, #doc-versions li.current, #doc-languages li.current, #doc-versions li a, #doc-languages li a {
    background-color: #303830;
    border-color: #343;
}
.note.warning, .admonition.warning, .help-block.warning {
    background-color: #404030;
    border-color: #554;
}
.versionadded, .versionchanged, .deprecated, .versionmodified {
    border-color: #454;
}
#docs-content a.reference:active, #docs-content a.reference:focus, #docs-content a.reference:hover {
    color: #fcc;
}
#docs-content a.reference {
    color: #f77;
}
tt, code, kbd, pre, samp, [role="main"] h1, h2, h3, h4, a, #doc-versions li, #doc-languages li {
    color: #7c7;
}
a:visited, #doc-versions li:visited, #doc-languages li:visited, .list-links a:visited, .search-links a:visited {
    color: #5b5;
}
#docs-content a.reference:active, #docs-content a.reference:focus, #docs-content a.reference:hover {
    background-color: #555;
}
table.docutils td, table.docutils th {
    border-bottom-color: #282828;
}
.snippet-filename, .code-block-caption {
    background-color: #303830;
    border-color: #343;
    color: #7c7;
}
form input[type="search"] {
    background-color: #333;
}
form input[type="search"], form input[type="text"], form input[type="email"], form input[type="password"], form input[type="number"], form input[type="url"], form textarea {
    border-color: #555;
}
form input[type="search"]:active, form input[type="search"]:focus, form input[type="text"]:active, form input[type="text"]:focus, form input[type="email"]:active, form input[type="email"]:focus, form input[type="password"]:active, form input[type="password"]:focus, form input[type="number"]:active, form input[type="number"]:focus, form input[type="url"]:active, form input[type="url"]:focus, form textarea:active, form textarea:focus {
    border-color: #177551;
}
.browse-horizontal {
    border-top-color: #282828;
    border-bottom-color: #282828;
}
.nav-pagination {
    border-top-color: #282828;
}
[role="complementary"] .list-links dt, [role="complementary"] .search-links dt, [role="complementary"] .list-links li, [role="complementary"] .search-links li {
    border-top-color: #383838;
}
[role="complementary"] h2, .layout-secondary h2 {
    border-bottom-color: #383838;
}
[role="contentinfo"], .copy-banner {
    background-color: #195F44;
}
[role="contentinfo"] h2 {
    color: #ccc;
}
pre.literal-block, .literal-block {
    background-color: #272822;
    border-color: #282828;
    color: #fff;
}
.console-block > label {
    color: #777;
}
.console-block > input:checked + label {
    border-color: #222;
    border-bottom-color: #222;
    border-top-color: #853;
    color: #ccc;
}




.highlight {
    background-color: #272822;
    border-color: #282828;
}
.highlight .n, .highlight pre {    color: #fff; }
/*.highlight .nn {    color: #9ED72A; }*/
.highlight .nn {    color: #fff; }
.highlight .kn {    color: #f92672; }
.highlight .nc {    color: #9ED72A; }
.highlight .s1 {    color: #e6db74; }
.highlight .s2 {    color: #e6db74; }
.highlight .mi {    color: #ae81ff; }
.highlight .ow {    color: #f92672; }
.highlight .k {    color: #f92672; }
.highlight .nf {    color: #9ED72A; }
.highlight .bp {    color: #ae81ff; }
.highlight .p {    color: #fff; }
.highlight .o {    color: #f92672; }
.highlight .c1 {    color: #75715e; }
.highlight .si {    color: #ae81ff; }
.highlight .nb {    color: #67d8ef; }
.highlight .kc {    color: #ae81ff; }
.highlight .gp {    color: #f92672; }
.highlight .gt {    color: #5998a6; }
.highlight .gr {    color: #fd971f; }
.highlight .nv {    color: #67d8ef; }
.highlight .nt {    color: #f92672; }
.highlight .cp {    color: #fff; }
.highlight .na {    color: #97b757; }
.highlight .s {    color: #bc9262; }
.highlight .mf {    color: #67d8ef; }
.highlight .m {    color: #ae81ff; }
.highlight .ss {    color: #e6db74; }
.highlight .nd {    color: #67d8ef; }
.highlight .ne {
    font-weight: normal;
    font-style: italic;
    color: #67d8ef;
}
.highlight .sd {    color: #75715e; }

}
