css:
@-moz-document domain("techcrunch.com") {
/*general darkification*/
/*dark-dark*/
html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, .module-header .middle-container, .module-header .bottom-container, .featured-post-row, .article-content div, .site-footer, .comments-header, .comments-sidebar {
color: #d5d5d5 !important;
background: #292929;
}
textarea, pre {
background: #292929 !important;
}

/*text*/
/*white stuff*/
a, .upcoming-events__item__date, span {
color: #d5d5d5 !important;
}
.form-field__input--checkbox{
    border: 2px solid #d5d5d5;
}
/*green stuff*/
.crunchbase-link {
    color: #06e988 !important;
}
/*transparency for (previously) highlighted text*/
.desktop-nav__body, .menu.navigation__main-menu, .menu__item, .menu.navigation__trending-topics, .article__title, .article__subtitle, .event-title, .article-content a, .mobile-header-nav  {
    background: transparent !important;
}

/*All the small things*/
/*close button*/
.close-button::before, .close-button .icon-wrap {
    background: #000;
    background-color: #000;
}
.close-button.completed .icon-wrap {
    background: #0aaa66;
    background-color: #0aaa66;
    color: #000;
}
/*embed changer*/
/*embed iframes cant be broken because they are sandboxed so they have been changed as to not be a glaring white spot in the middle of the screen.*/
.wp-ebedded-content {
    display: block !important;
}
.embed.breakout P iframe{
    display: none;
}
/*adblocking stuffs*/
.ad-unit__billboard.ad-unit--fixed, .ad-unit__super.ad-unit--fixed, .ad-unit__ad.ad-unit__rightrail, .ad-unit, .ad-unit__ad.ad-unit__native, ad-unit__ad, .ad-unit__billboard, .ad-unit__super {
    display: none !important;
}
/*load more gradient*/
.load-more:hover, .load-more.loading{
    background: linear-gradient(79deg,#003400,#124529 50%,#003f25) !important;
}
.load-more:hover {
    background: #141414;
}
/*photo stacks*/
.photo-stack__text {
    background-color: #0aaa66 !important;
    color: #000 !important;
}
/*extra crunch preview end*/
.extra-crunch-offer-container::before {
    background-image: linear-gradient(180deg,hsla(0,0%,100%,0),#fed95224);
}
}
