css:
@-moz-document domain("elixir-lang.org") {
@font-face{
    font-family: 'Fira Code';
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Regular.eot');
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Regular.eot') format('embedded-opentype'),
         url('https://unpkg.com/firacode/distr/woff2/FiraCode-Regular.woff2') format('woff2'),
         url('https://unpkg.com/firacode/distr/woff/FiraCode-Regular.woff') format('woff'),
         url('https://unpkg.com/firacode/distr/ttf/FiraCode-Regular.ttf') format('truetype');
    font-weight: 400;
    font-style: normal;
}

:root {
    --background-primary: #282C34;
    --background-secondary: #3E4452;
    --background-tertiary: #2C323C;
    --background-quaternary: #2C323C;
    --foreground-primary: #ABB2BF;
    --foreground-secondary: #5B6577;
    --foreground-tertiary: #687289;
}

#container {
    background-color: var(--background-primary);
    color: var(--foreground-primary);
}

h1, h2, h3, h4, h5, h6,
.widget.news h3 a,
.sidebar .widget-title {
    color: var(--foreground-tertiary);
}

a, a:visited,
.entry-title a,
body.home div.menu li.home a,
p,
.byline,
.byline a {
    color: var(--foreground-primary);
}

a:hover,
.entry-title a:hover,
#menu-primary ul li a:hover,
.byline a:hover {
    color: var(--foreground-secondary);
}

.sidebar .widget {
    color: var(--foreground-secondary);
}

.cta-copy .cta-title,
.cta-copy .cta-subtitle,
.mini-docu-copy {
    color: var(--foreground-primary);
}

.mini-docu-copy:hover {
    color: var(--foreground-tertiary);
}

.mini-docu-cta {
    border-color: var(--foreground-primary);
}

a.cta-button,
.pagination a.page-numbers,
.comment-navigation a.page-numbers {
    background: none;
    background-color: var(--background-tertiary);
    border-color: transparent;
    box-shadow: none;
    color: var(--foreground-primary);
    transition: background-color 200ms ease;
}

a.cta-button:hover,
.pagination a:hover,
.comment-navigation a:hover {
    background: none;
    background-color: var(--background-secondary);
    border-color: transparent;
    color: var(--foreground-primary);
    transition: background-color 200ms ease;
}

.widget .search-form input[type="text"] {
    background-color: var(--background-secondary);
    border-color: transparent;
    color: var(--foreground-primary);
}

.jekyll-toc-header span.jekyll-toc-icon {
    color: var(--foreground-secondary);
}

blockquote {
    border-left-color: var(--foreground-primary);
    color: var(--foreground-primary);
}

.highlighter-rouge {
    background-color: var(--background-secondary);
    color: var(--foreground-primary);
    border-color: transparent;
    font-family: "Fira Code";
}

.highlight,
pre,
code {
    background-color: var(--background-quaternary);
    color: var(--foreground-primary);
    border-color: transparent;
    font-family: "Fira Code";
}

.highlight .k,
.highlight .nb {
    color: #C678DD;
}

.highlight .no {
    color: #E5C07B;
}

.highlight .ss,
.highlight .gp,
.highlight .nv {
    color: #56B6C2;
}

.highlight .sd,
.highlight .s2 {
    color: #98C379;
}

.highlight .m,
.highlight .mi {
    color: #D19A66;
}

.highlight .c1 {
    color: #5C6370;
}

}
