css:
@-moz-document url-prefix("https://zimadmin.site.org") {
/* Decorative elements/Subheads */
/*Gradient*/
.skin_layout, .DWT_control {
    background: -moz-linear-gradient(top,#260027, #530055) !important;
}
/*load screen reflection*/
.decor1 {
    background: -moz-linear-gradient(top,#260027d1, #53005500) !important;
}
/*Flat*/
#skin_td_bottom, .AdminTreeItem-Count, .rootOverviewHeader, .LoginScreen .contentBox, .SplashScreen .contentBox, .ErrorScreen .contentBox, .overviewHeader  {
    background: #260027;
    border-color: #530055;
}

/* Links and stuff */
.ZaHomeSetupTitle, .ZaHomeLinkItemLabel, a, a:hover, a:link, a:visited {
    color: #9e4aec;
}

/* Background and text general */
/*light dark*/
.OverviewTreePanel, .homeRootOverviewHeader, #ztabv__HOMEV_zatabcase, .ZaTaskTitleNameHeader, .DwtToolbarButton, #skin_container_main_footer, #skin_container_tree_footer, #skin_container_tool_footer, .grid_xform_table, .SearchTBSearchField .ZButtonBorder, .DwtMsgDialog, .DwtDialogButtonBar, .DwtMenuTable, .DwtMenuTable TD, .ActionMenu, .oselect, .oselect_check_choice, .oselect_check_choice_disabled, .ZTabView, select, #ztabv__MTX_EDIT_xform_tabbar___container, .ZTabBorder, .ZTabBorder *, .ZaHelpDropdownSearch, .ZaHelpDropdownFirstItem, .ZaHelpDropdownItem, .ZaHelpDropdownLastItem, .LoginScreen {
    background-color: #2f2f2f !important;
    color: #d5d5d5 !important;
    background-image: none;
}
/*Dark dark*/
.ZaTaskListContent, .ZaHomeSetupPanel, .ZaCtrAppTreeHeader, .ZaCurrentAppBar, .TaskHeaderPanel, .DwtListView-Column, .AdminTreeItem-selected, .DwtDialogTitle, .DwtDialogButtonBar .ZButtonBorder, .AdminTreeItem-actioned, .DwtListView-Rows, .gridGroupHeader, textarea, input, .XFormCase, .GrouperLabel, .ZaOverride, .ZaOverride *, .xform_button, .ZButtonBorder, .DwtListView-Rows-Empty, .DwtAlertWarn, .oselect_check_choice_selected, .LinkButton, .ChartSettings, .ZSelected .ZTabBorder, .ZSelected .ZTabBorder *, .SmallRadioGrouperLabel, .DLSource, .mtaprogressbar, .DLTarget, .DwtTabView {
    background-color: #292929 !important;
    color: #d5d5d5 !important;
    background-image: none !important;
}

/* Specific Text fixes */
.ZTreeItemTextCell, .xform_output, .xform_label, span, .ZToolbarButtonTable .ZWidgetTitle, .DwtListHeaderItem-label, .DwtDialogButtonBar .ZButtonTable .ZWidgetTitle, .NoResults, .ZButtonTable .ZWidgetTitle {
    color: #d5d5d5 !important;
}
/*app tree arrow fix*/
.ZWidgetTitle {
    color: #000;
}

/* Rows */
/*Even*/
.RowEven, .RowEven * {
    background-color: #2f2f2f !important;
    color: #d5d5d5 !important;
    border-color: #2f2f2f;
}
/*Odd*/
.RowOdd, .RowOdd *, .Row-selected-actioned, .Row-selected, .Row-focused, .DwtAlertInfo, .RowOdd TD {
    background-color: #292929 !important;
    color: #d5d5d5;
    border-color: #292929 !important;
}
}
