css:
@-moz-document domain("ssherder.com") {
/*back:#171717 hover:#242424 focus:#2f2f2f border:#4c4c4c*/
#main-content{
    background:black;
}
#sidebar{
    background:#0c0c0c;
}
body{
    color: #a5a5a5;
}
#footer{
    background:#232323;
    color:#a9a9a9;
    border-top:2px solid #4a4a4a;
    margin-left: -15px;
    margin-right: -15px;
}
.table thead th{
    border-bottom:2px solid #4a4a4a;
}
.dropdown-menu{
    background:#171717;
}
.dropdown-item{
    color:#8a8a8a;
    background:#171717;
}
.dropdown-item:hover{
    color:#8a8a8a;
    background:#242424;
}
.dropdown-divider{
    border-top:1px solid #636363;
}
.form-control{
    background:#171717;
    color:#ced4da;
    border-color:#4c4c4c;
}
.btn-secondary{
    background:#171717;
    color:#b5b5b5;
    border-color:#4c4c4c
}
#filter-name .form-control{
    color:white;
}
.btn-secondary:not([disabled]):not(.disabled):active, .btn-secondary:not([disabled]):not(.disabled).active, .show>.btn-secondary.dropdown-toggle{
    background-color: #2f2f2f;
    color:#d2d2d2;
    border-color:#4c4c4c;
}
.btn-secondary:hover{
    background:#242424;
    color:#d2d2d2;
    border-color:#4c4c4c;
}
.form-control:focus{
    background:#242424;
    color:#ced4da;
    border-color:#4c4c4c;
}
.active-filters>div{
    background-color:#004228;
    color:#00f391;
    border:1px solid #00f391;
}

/*Character info*/
.card{
    background:#1b1b1b;
    /*background:black;*/
    border: 1px solid black;
}
.progress{
    background-color:#2f2f2f;
    /*border:1px solid black;*/
}
/*div.col-xs-12.col-sm-4.col-lg-12, div.col-xs-12.col-sm-8.col-lg-12, div.col-sm-12.col-md-7, div.col-sm-12.col-md-5, div.col-lg-12.col-xl-7, div.col-lg-6.col-xl-5, div.col-lg-6.col-xl-12, div.col-md-12.col-lg-7, div.col-md-12.col-lg-5{
    background:#1b1b1b !important;*/
    /*padding:20px;*/
    /*border:solid 1px black;
    border-radius:10px;
}padding: 1.25rem;*/

.card-body{
    padding:0;
}

div.col-xs-12.col-sm-4.col-lg-12/*Image*/, div.col-sm-12.col-md-7/*Stats 1*/, div.col-lg-12.col-xl-7/*Skills*/, div.col-lg-6.col-xl-12/*Reverse*/, div.col-md-12.col-lg-7/*Evolution*/, div.col-xs-12.col-sm-8.col-lg-12/*Info*/, div.col-sm-12.col-md-5/*Stats 2*/, div.col-lg-6.col-xl-5/*Chains*/, div.col-md-12.col-lg-5/*Story*/{
    border: 1px solid #000;
    border-radius:10px;
    padding: 1.25rem;
    background-color:#1b1b1b;
}

.mt-3, .my-3{
    margin-top:0 !important;
}



.table th, .table td{
    border-top: 1px solid #4c4c4c;
}

/*Sidebar*/
#sidebar-content .btn:hover{
    background-color:#242424;
}
.btn-link, .btn-link:hover{
    color:#8a8a8a;
}
#sidebar-content .btn.active{
    background-color:#2f2f2f;
}
/*Chains*/
.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link{
    background-color:#2f2f2f;
    border-color:#4c4c4c;
    color:#ced4da;
}
.nav-tabs{
    border-bottom: 1px solid #4c4c4c;
}
.nav-tabs .nav-link:focus, .nav-tabs .nav-link:hover{
    border-color:#4c4c4c;
}
/*Margin*/
#sidebar{
    margin-left: -15px;
}
#main-content{
    margin-right: -15px;
}
.row{
    margin-right:0;
    margin-left:0;
    background-color:black;
}
/*Stones*/
.card-group .card+.card, .card-group .card:first-child, .card-group .card:not(:first-child):not(:last-child):not(:only-child){
    padding: 10px;
    border-radius:10px;
} 
}
