@-moz-document regexp("^https?://www\\.tuicool\\.com/articles/(\\w+)?"), regexp("^https?://www\\.tuicool\\.com/wx/(\\w+)?") {
/* 详细页
		1. Ã¦ÂÂÃ§Â«Â Ã¨Â¯Â¦Ã§Â»ÂÃ©Â¡Âµ
 */

/* 导航条 */

@media all {
	
	body,
	div.navbar-fixed-top,
	div.navbar-fixed-top div.container,
	div.navbar-fixed-top div.container a.brand,
	div.article-header,
	div.article_detail_bg {
		background-color: black !important;
		background-image: none !important;
		color: LimeGreen;
	}

	a {
		color:#ccc;
	}
	
	img {
		background-color:white;								
	}
	
}

/* Ã©Â¡ÂµÃ§ÂÂ */

div.article_meta span.from a,
div.article_meta span#dup-cnt a,
div.navbar-fixed-top div.container ul.nav.navbar li a {
	color: gray;
}

/* 文章正文 */

@media all{
	div.article_body > div > *:not(pre),
	div.article_body > div > *:not(pre) * { /* 全文（不包括代码引用）字体大小 */
	  font-size: 1.15em;
  }
	
	div.article_detail_bg div.article_body pre {
		border: 1px solid green;
		background-color: black;
		color: LimeGreen;
	}

	div.article_detail_bg div.article_body pre span[style="color: #000000;"]{
		color: LimeGreen !important;
	}

	div.article_detail_bg div.article_body pre span.tag,
	div.article_detail_bg div.article_body pre span.tag span.title {
		color: RoyalBlue;
	}

	div.article_detail_bg div.article_body pre span.css span.rule,
	div.article_detail_bg div.article_body pre span.css span.rules {
		color: SlateGray;
	}

	div.article_detail_bg div.article_body pre.prettyprint.css span.rules span.rule{
		display: block;
	}

	div.article_detail_bg div.article_body pre.prettyprint.css span.rules span.rule:not(:last-of-type){
		margin-left:2em;
	}
	
	div.article_detail_bg div.article_body pre.prettyprint code[data-language="javascript"],
	div.article_detail_bg div.article_body pre.prettyprint code[data-language="javascript"] span{
		text-shadow: none !important;
	}
}

/* Ã¤Â»Â£Ã§Â ÂÃ©ÂÂÃ¨ÂÂ² */

@media all{
	
	div.article_detail_bg div.article_body code {
		background-color: DarkCyan;
		color: WhiteSmoke !important;
	}

	div.article_detail_bg div.article_body pre code {
		background-color: black;
		color: WhiteSmoke !important;
	}

	div.article_detail_bg div.article_body pre span.keyword{
		color: BlueViolet;
		font-size: 1.1em;
	}

	div.article_detail_bg div.article_body pre span.comment{
		color: LawnGreen;
	}

	div.article_detail_bg div.article_body pre span.variable,
	div.article_detail_bg div.article_body pre span.attribute,
	div.article_detail_bg div.article_body pre span.tag,
	div.article_detail_bg div.article_body pre span.class{
		color: #00B7B7;
	}

	div.article_detail_bg div.article_body pre span.rules span.rule span.value,
	div.article_detail_bg div.article_body pre span.rules span.rule span.number{
		color: LimeGreen;
	}
	
	div.article_detail_bg div.article_body pre span.request,
	div.article_detail_bg div.article_body pre span.status{
		color: whitesmoke;
	}
	span.hljs-selector-tag,
	span.hljs-attribute,
	span.hljs-keyword {
	  color: rgb(0, 59, 255);
	}
	span.hljs-selector-class,
	span.hljs-string,
	span.hljs-number {
	  color: #f31b1b;
	}	
	span.hljs-string span.hljs-subst{
		color: #bababa;
	}
	span.hljs-name{
		color: rgba(245, 245, 245, 0.76);
	}
	span.hljs-built_in {
		color: #0099ff;
	}	
	span.hljs-title {
		color: #bb0909;
	}
}

@media (max-width: 767px) {
	
	div#header.navbar-fixed-top{
		display:none;									
	}
	
	div.return {
		display: none !important;
	}
	
}
}

