@-moz-document domain("freecodecamp.org") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.tags-row
{
	text-transform: capitalize!important;
}

.list-controls .combo-box .combo-box-header,
.monaco-editor .view-overlays .current-line,
.monaco-scrollable-element,
.moving-avatar, .static-avatar,
.nav-pills>li>a,
.topic-map section,
.topic-map,

:focus,

div.menu-links-header .menu-links-row li a.active
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

code[class*=language-], pre[class*=language-] {
    white-space: pre-line;
}

/* Fonts 
__________________________________________________________________________________________________________________________________*/


selector
{
	font-size: 3rem!important;
}

.mtk8,
.post-full-content pre code,

code[class*=language-],
pre[class*=language-]
{
	font-size: 2.85rem!important;
}

.category-list tbody .category h3,
.monaco-menu .monaco-action-bar.vertical .keybinding,
.monaco-menu .monaco-action-bar.vertical .submenu-indicator,
.monaco-menu .monaco-action-bar.vertical .action-label,
.monaco-editor .suggest-widget .monaco-list .monaco-list-row,

h4, h5, h6, td, th
{
	font-size: 2.52rem!important;
}

.badge-wrapper,
.categories-list .category .category-description,
.category-list .topics .unit,
.category-list .topics .value,
.mtk8,
.post-full-content pre code,
.topic-list .main-link a.title,
.topic-list .num,
.topic-map h4,

code[class*=language-],
pre[class*=language-],
p
{
	font-size: 1.52rem!important;
}

code[class*=language-],
pre[class*=language-]
{
    line-height: 1.85rem!important;
}

p
{
	line-height: 1.8rem!important;
}

/* Remove Items 
__________________________________________________________________________________________________________________________________*/

.ad-layout .sidebar,
.d-modal.create-account .login-form:after,
.d-modal.create-account .login-form:before,
.google-adsense .google-adsense-content,
.google-adsense .google-adsense-label h2
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
__________________________________________________________________________________________________________________________________*/

.topic-list .link-bottom-line {
    margin-left: 2rem;
    margin-top: .5rem;
}

.timeline-container {
    margin-left: 84%;
}

.timeline-container .topic-timeline {
    margin-left: 1em;
}

#whos-online.collapsed a {
    margin-right: 0.1rem;
}

#list-area .show-more.has-topics {
    top: -17px!important;
}

.nav-pills>li {
    margin-right: 9.5em!important;
}

/* Margins 
__________________________________________________________________________________________________________________________________*/

.post-card,
.row,
.user-content-wrapper .user-secondary-navigation,
.user-stream .item,
.user-stream .user-stream-item,
.wrap
{
	margin: 1.2rem!important;
}

/* Paddings 
__________________________________________________________________________________________________________________________________*/

.row
{
	padding: 1.2rem!important;
}

.post-full-header
 {
	margin: 3px 12px!important;
}

.post-card {
    margin: 0 34px 50px!important;
}

/* Heights 
__________________________________________________________________________________________________________________________________*/

.monaco-list-rows {
    height: 100%!important;
}

/* Widths 
__________________________________________________________________________________________________________________________________*/

.inner,
.post-content,
.post-full-header,
.small-action,
.wrap,

div.menu-links-header a.user-activity-link span.d-label
 {
	max-width: none!important;
}

div.menu-links-header a.user-activity-link span.d-label {
    width: auto!important;
    overflow: visible!important;
    text-overflow: inherit!important;
}

.outer {
    padding: 0 1vw!important;
}

}

@-moz-document domain("freecodecamp.org") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	

.container,
.footer-container,
.inner
 {
	width: 98%!important;
}

.topic-body
 {
	width: 84%!important;
}

	
.category-list .topics {
    width: 9rem!important;
}

	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

.container,
.footer-container,
.inner
 {
	width: 98%!important;
}

.topic-body
 {
	width: 84%!important;
}
	
.category-list .topics {
    width: 9rem!important;
}
	
}
}

@-moz-document domain("freecodecamp.org") {
/* Font Settings */

.category-list .category-name,

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
__________________________________________________________________________________________________________________________________*/

#current-user a:focus:before,
#current-user a:hover:before,
#search-button:focus:before,
#search-button:hover:before,
#toggle-hamburger-menu:focus:before,
#toggle-hamburger-menu:hover:before,

.active #search-button:before,
.active #toggle-hamburger-menu:before,
.active#current-user a:before,
.alert.alert-info,
.drop-down-mode .d-header-icons .active .icon,
.drop-down-mode .d-header-icons .active .icon:before,
.exposed-button-nav:hover,
.monaco-editor .current-line ~ .line-numbers,
.monaco-menu .monaco-action-bar.vertical .action-menu-item:hover .action-label,
.tags-row>.tag:hover h3,
.topic-map .map .number,

a[href]:hover span,
a[href]:hover,
button.map-title:hover h4,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label,
nav.post-controls button:hover,
p code
 {
	color: #ff0!important;
}

/* White 
__________________________________________________________________________________________________________________________________*/

#description,

.alert-success,
.alert.alert-error,
.badge-notification.clicks,
.badge-wrapper.box span.badge-category,
.btn,
.categories-list .category .category-description,
.donation-modal b,
.donation-modal p,
.kg-bookmark-description,
.monaco-icon-label>.monaco-icon-label-description-container>.label-name span,
.monaco-list.list_id_3 .monaco-list-row.focused .type-label,
.monaco-menu .action-label,
.monaco-menu .monaco-action-bar.vertical .keybinding, 
.monaco-menu .monaco-action-bar.vertical .submenu-indicator,
.mtk8,
.select-kit.combo-box.category-drop .select-kit-row .category-desc,
.select-kit.combo-box.category-drop .select-kit-row .topic-count,
.select-kit.dropdown-select-box .dropdown-select-box-header,
.select-kit.dropdown-select-box.period-chooser .period-chooser-header h2.selected-name .date-section,
.test-output,
.timeline-container .topic-timeline .timeline-replies,
.topic-list .topic-excerpt,
.user-menu .quick-access-panel li span:first-child,

blockquote,
button.map-title,
details,
li,
p,
summary,
td
 {
	color: #fff!important;
}

/* Khaki 
__________________________________________________________________________________________________________________________________*/

.footer-container .col-header,
.kg-bookmark-title,
.mtk15,
.badge-wrapper.box span,

code,
h1,
h2,
strong,
table th
 {
	color: #F0E890!important;
}

/* Burlywood 
__________________________________________________________________________________________________________________________________*/

.completion-block-name,
.latest-topic-list .table-heading,
.post-full-content em, .post-full-content strong,
.top-topic-list .table-heading,

dfn,
h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
__________________________________________________________________________________________________________________________________*/

#whos-online,

.kg-bookmark-metadata,

aside.quote .title,
h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
__________________________________________________________________________________________________________________________________*/

.select-kit.dropdown-select-box.period-chooser .period-chooser-header h2.selected-name .top-date-string,
.timeline-container .topic-timeline .now-date,
.timeline-container .topic-timeline .timeline-ago,
.timeline-container .topic-timeline .start-date,

span.relative-date,
time
 {
	color: #07beff!important;
}

/* Green 
__________________________________________________________________________________________________________________________________*/


selector
 {
	color: #07ff24!important;
}

/* Red 
__________________________________________________________________________________________________________________________________*/


selector
 {
	color: #ff0707!important;
}

/*
__________________________________________________________________________________________________________________________________*/

.donate-input-element,
.list-controls .combo-box .combo-box-header,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
__________________________________________________________________________________________________________________________________*/


::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
__________________________________________________________________________________________________________________________________*/

code[class*=language-], pre[class*=language-] {
    text-shadow: none;
}

.mtk10,
.mtk21,
.output-text,
.post-full-content figcaption,
.token.punctuation,
code
 {
    color: #fff!important;
}

.monaco-editor .suggest-widget .monaco-list .monaco-list-row .monaco-highlighted-label .highlight {
    color: #bf9f00!important;
}

.mtk23 {
    color: #aef1f1;
}

.mtk4,
.token.class-name,
.token.function
{
    color: #ff7994;
}

.mtk6,
.language-css .token.string, .style .token.string, .token.entity, .token.operator, .token.url {
    color: #ffc076;
}

.token.cdata, .token.comment, .token.doctype, .token.prolog {
    color: #b1b1b1;
}

.mtk1,
.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #af0!important;
}

.mtk7 {
    color: #d8ff00;
}

.hljs-string, .hljs-tag .hljs-string, .tex .hljs-formula {
    color: #ffd747;
}

.hljs-class .hljs-title, .haskell .hljs-type, .vhdl .hljs-literal, .tex .hljs-command {
    color: #8aa8ff;
}

.token.atrule, .token.attr-value, .token.keyword,
.hljs-keyword, .javascript .hljs-title, .nginx .hljs-title, .hljs-subst, .hljs-request, .hljs-status
 {
    color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted
 {
    color: #ff6cbe!important;
}

.mtk11 {
    color: #ebebeb;
}


polygon {
    fill: #ff0000!important;
}

.btn-flat.close .d-icon {
    color: #fd0202;
}


.PlaybackOverlay {
    --bg-color: #000815;
    --button-color: #006400;
    --muted-button-color: rgba(255, 255, 255, 0.6);
    --elapsed-color: white;
    --track-color: rgba(255, 255, 255, 0.25);
    --label-color: #eee;
    background: #000;
    color: #fbfbfb;
}

.SpaceView .SidebarView {
    --panel-hover-bg: rgba(62, 70, 79, 0.2);
    --panel-on-bg: rgba(7, 8, 8, 0.91);
    background: #000000;
}
}

@-moz-document domain("freecodecamp.org") {
/* Background Settings */

#main,
#main-outlet,

.light-palette,
.nimbus-is-editor,
.site-wrapper,

body,
html
{
	background: #000!important;
}

selector
{
	background-image: none!important;
}

.reflex-container>.reflex-element,
.monaco-editor .margin,
.monaco-editor-background
 {
	background: #000!important;
}

.read-next-card {
	background-image: none;
}

.monaco-editor, .monaco-editor-background, .monaco-editor .inputarea.ime-input {
    background-color: #fffffe!important;
}

.monaco-editor .cursor {
    background-color: #ff0;
}

/* Background 
__________________________________________________________________________________________________________________________________*/


.site-nav,
.site-footer,
.universal-nav
{
	background: #060505!important;
	background-color: #060505!important;
}

.d-header,
.under-header-content
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
__________________________________________________________________________________________________________________________________*/

.btn.btn-active,.btn.btn-hover,
.btn.btn-social.btn-hover,.btn.btn-social:hover,
.btn:active,.btn:focus,.btn:hover,
.default-layout,
.fcc-modal,
.intro-toc .list-group-item:hover,
.monaco-menu .monaco-action-bar.vertical .action-menu-item,
.nav li:hover,
.page-template .site-main,.page-template.site-main, .post-template.site-main,
.post-full-content,.post-full-image,
.post-template .site-main,
.read-next-card,.read-next-card:before,
.select-kit.dropdown-select-box .dropdown-select-box-header.btn-active,.select-kit.dropdown-select-box .dropdown-select-box-header.btn-hover,.select-kit.dropdown-select-box .dropdown-select-box-header.btn-social.btn-hover,.select-kit.dropdown-select-box .dropdown-select-box-header.btn-social:hover,.select-kit.dropdown-select-box .dropdown-select-box-header:active,.select-kit.dropdown-select-box .dropdown-select-box-header:focus,.select-kit.dropdown-select-box .dropdown-select-box-header:hover,
.table-striped>tbody>tr:nth-child(odd)>td,.table-striped>tbody>tr:nth-child(odd)>th,.table-striped>tbody>tr:nth-of-type(odd),
.tags-row>.tag,.tags-row>.tag:hover,
.topic-map,
.user-menu .quick-access-panel li,

[class*="-bg"],

a.mention,a:focus,a:hover,
aside.quote .title,
blockquote,
nav.post-controls .double-button button:hover,nav.post-controls button:hover
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
__________________________________________________________________________________________________________________________________*/

/* Blue 
__________________________________________________________________________________________________________________________________*/

#readMoreBtn,

.btn-primary,
.exposed-button-nav,
.user-menu .quick-access-panel .show-all a,

a.cta-button
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

#readMoreBtn::before,

.btn-primary::before,
.exposed-button-nav::before,
.user-menu .quick-access-panel .show-all a::before,

a.cta-button::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

#readMoreBtn:hover::before,

.btn-primary:hover::before,
.exposed-button-nav:hover::before,
.user-menu .quick-access-panel .show-all a:hover::before,

a.cta-button:hover::before
 {
  opacity: 1!important;
}

/* Red 
__________________________________________________________________________________________________________________________________*/

.btn-danger
{	
	background: radial-gradient(circle, #d90008a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-danger::before
{
	background: radial-gradient(circle, #000 0%, #d90008 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.btn-danger:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.btn-cta,
.discourse-no-touch .btn,
.list-controls .btn,
.topic-map .buttons .btn,

[data-test-label="donate-button"]
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-cta::before,
.discourse-no-touch .btn::before,
.list-controls .btn::before,
.topic-map .buttons .btn::before,

[data-test-label="donate-button"]::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.btn-cta:hover::before,
.discourse-no-touch .btn:hover::before,
.list-controls .btn:hover::before,
.topic-map .buttons .btn:hover::before,

[data-test-label="donate-button"]:hover::before
 {
  opacity: 1!important;
}

/* Plain Black 
__________________________________________________________________________________________________________________________________*/

.inner,
.monaco-editor .monaco-editor-hover,
.row,
.user-content,
.user-content-wrapper .user-secondary-navigation,
.user-main .about.collapsed-info .details
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
__________________________________________________________________________________________________________________________________*/

#simple-container,

.select-kit.is-expanded .select-kit-body,
.iw-cm-menu,
.kg-bookmark-card,
.menu-panel,
.modal-content,
.modal-inner-container,
.moderator .regular>.cooked,
.monaco-editor .suggest-widget>.tree,
.monaco-menu .monaco-action-bar.vertical,
.post-card,
.user-stream .item,
.user-stream .user-stream-item
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#simple-container:hover,

.select-kit.is-expanded .select-kit-body:hover,
.inner:hover,
.iw-cm-menu:hover,
.kg-bookmark-card:hover,
.menu-panel:hover,
.modal-content:hover,
.modal-inner-container:hover,
.moderator .regular>.cooked:hover,
.monaco-editor .monaco-editor-hover:hover,
.monaco-editor .suggest-widget>.tree:hover,
.monaco-menu .monaco-action-bar.vertical:hover,
.post-card:hover,
.row:hover,
.user-content-wrapper .user-secondary-navigation:hover,
.user-content:hover,
.user-main .about.collapsed-info .details:hover,
.user-stream .item,
.user-stream .user-stream-item:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
__________________________________________________________________________________________________________________________________*/

.alert.alert-error,
.alert.alert-info,
.reset-modal-header,
.test-result:nth-child(odd)
 {
	background: linear-gradient(to bottom, #ba0000 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	padding: 3px 8px!important;
}

/* Blue Gradient 
__________________________________________________________________________________________________________________________________*/

.badge-notification.clicks,
.d-header-icons .unread-notifications,
.progress-bar-background,

a.banner,
aside.quote
 {
	background: linear-gradient(to bottom, #3b4c85 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	padding: 3px 8px!important;
}

/* Green Gradient 
__________________________________________________________________________________________________________________________________*/

.alert-success,
.d-header-icons .header-dropdown-toggle.active .icon,
.nav-pills>li a.active
 {
	background: linear-gradient(to bottom, #3b8545 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	 padding: 3px 8px!important;
}

.progress-bar-percent
 {
	background: linear-gradient(to bottom, #3b8545 0%, #000 100%)!important;
	border-top-left-radius: 22px!important;
	border-bottom-left-radius: 22px!important;
}

/* Code 
__________________________________________________________________________________________________________________________________*/

code,
p code,
pre code,
pre,
strong code
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

/* Highlight Background 
__________________________________________________________________________________________________________________________________*/

.monaco-editor .suggest-widget .monaco-list .monaco-list-row:hover,
.monaco-editor .view-overlays .current-line,
.monaco-menu .monaco-action-bar.vertical .action-menu-item:hover,
.select-kit .select-kit-row.is-highlighted,

button.map-title:hover,
tbody tr:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

.monaco-list.list_id_3 .monaco-list-row.focused {
	background: linear-gradient(to right, #006400, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
__________________________________________________________________________________________________________________________________*/

selector
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

selector
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#0e0e0e 70%)!important;
	padding: 13px 26px!important;
}

selector
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}

}

