css:
@-moz-document url-prefix("https://www.masterani.me/anime/watch/") {
div.row:nth-of-type(2),
div.row:nth-of-type(4),
.cover,
div.item > p { display: none !important; }

#watch > .ui.grid > .anime-video .options > .item .ui.dropdown,
.ui.embed { border-radius: 5px !important; }

.ui.dropdown.master.button .master.menu { border-radius: 5px !important; margin-top: 5px !important; }

.ui.grid.container { width: 70% !important; }
}

@-moz-document url("https://www.masterani.me/my-anime") {
div.column.wide.sixteen:nth-of-type(1),
div.column.wide.sixteen:nth-of-type(2) > .header.master.ui,
.ui.my-anime.list .item .content .time { display: none !important; }

.ui.my-anime.list .item { border-radius: 5px !important; }

.ui.button.master,
.ui.button.master.icon:hover,
.ui.button.master:hover { background-color: rgba(0, 0, 0, 0); border: 0px solid rgba(0, 0, 0, 0) !important; }

.ui.button:hover { box-shadow: none !important; }
}

@-moz-document url("https://www.masterani.me/anime/schedule") {
.header.master.ui { display: none !important; }

.ui.poster.list .item .content .overlay,
.list.poster.horizontal.ui > .item > .content { border-top-right-radius: 5px !important; border-top-left-radius: 5px! important; }
}

@-moz-document url("https://www.masterani.me/anime"), url-prefix("https://www.masterani.me/anime?search=") {
.item > .title { display: none !important; }

.ui.lazy.loaded .image { border-top-right-radius: 5px !important; border-top-left-radius: 5px! important; }

.ui.dropdown.master.button .master.menu { border-radius: 5px !important; margin-top: 5px !important; }

#explore #filters > .item { border-radius: 5px !important; }

.ui.anime.results .anime { width: calc(100%/8) }
}

@-moz-document url("https://www.masterani.me/") {
div.column.wide.sixteen:nth-of-type(3),
.row.width.equal.column.two.stackable,
div.column.wide.sixteen:nth-of-type(1) > .header.master.ui,
.ui.thumbnails > .thumbnail > .holder > .overlay > .cover,
.centered.menu.pagination.simple.master.ui { display: none !important; }

.ui.grid.container { width: 80% !important; }

.ui.thumbnails > .thumbnail > .holder > img,
.ui.thumbnails > .thumbnail > .holder > .overlay.darken { border-top-right-radius: 5px !important; border-top-left-radius: 5px !important; }

.ui.thumbnails > .thumbnail > .holder > .overlay { width: 105% !important; height: 105% !important; }
}

@-moz-document url-prefix("https://www.masterani.me/anime/info/") {
.button.basic.ui,
.rating.ui,
.options,
.ui.thumbnails > .thumbnail > .holder > .overlay > .time { display: none !important; }

.ui.tag.horizontal.list .item { border-radius: 5px !important; }

#info #main #details .cover > img { border-top-right-radius: 5px !important; border-top-left-radius: 5px !important; }

.ui.thumbnails > .thumbnail.blur > .holder > img { filter: blur(3px) !important; }
}

@-moz-document url-prefix("https://www.masterani.me/") {
footer,
.visible.transition.left.menu > a.item:nth-of-type(2),
.visible.transition.left.menu > a.item:nth-of-type(3),
a.icon.item:nth-of-type(2) { display: none !important; }

#info #head { height: 250px !important; }

#navigation .ui.inverted.menu .item.search .menu .results .result img { border-top-right-radius: 5px !important; border-top-left-radius: 5px !important; }
}

@-moz-document url-prefix("https://www.masterani.me/u/") {
.ui.lazy.loaded .image { border-top-right-radius: 5px !important; }

.background { height: 200px !important; }

.stat { display: none !important; }

#profile > #about > .row.menu > .column .avatar,
#profile > #about > .row.menu > .column .avatar img { border-radius: 100% !important; }
}
