css:
@-moz-document url-prefix("https://gitlab."), domain("gitlab.com"), domain("framagit.org"), domain("git.vpgrp.io") {
:root {
    --main-background: #222;
    --lighter-background-1: #333;
    --lighter-background-2: #3c3c3c;

    --main-text-color: #e4e4e4;
    --main-header-color: #b7946e;
    --lighter-text-color-1: #e7e7e7;
    --lighter-text-color-2: #eee;
    --lighter-text-color-3: #f5f5f5;

    --darker-text-color-1: #ddd;
    --darker-text-color-2: #d4d4d4;
    --darker-text-color-3: #cacaca;
    --darker-text-color-4: #c7c7c7;
    --darker-text-color-5: #bababa;

    --a-color: #ff8b25;
    --a-hover-color: #ff9823;

    --light-divider: #8a8a8a;
    --main-border: #878faa00;
    --btn-border: #0000;
    --btn-background: #0000;
    --btn-disabled-background: #777;
    --btn-disabled-color: #5c5c5c;
}

body,
.user-callout.promotion-callout .bordered-box {
    background: var(--main-background);
    color: var(--main-text-color);
}
.dropdown-menu li a:not(.help-link),
.dropdown-menu li button:not(.btn),
.dropdown-menu li .menu-item,
.dropdown-menu-nav li a:not(.help-link),
.dropdown-menu-nav li button:not(.btn),
.dropdown-menu-nav li .menu-item {
    color: var(--main-text-color);
}

body.ui-dark .navbar-gitlab .nav > li.active > a,
body.ui-dark .navbar-gitlab .nav > li.dropdown.show > a {
    background: var(--lighter-background-2);
    color: #eee;
}

body.ui-dark .navbar-gitlab .nav > li.active > a,
body.ui-dark .navbar-gitlab .nav > li.dropdown.show > a:hover,
#cycle-analytics .stage-event-item .item-title.issue-title a,
#cycle-analytics .stage-event-item .item-title.commit-title a,
#cycle-analytics .stage-event-item .item-title.merge-merquest-title a,
#cycle-analytics .stage-event-item .issue-link,
#cycle-analytics .stage-event-item .commit-author-link,
#cycle-analytics .stage-event-item .issue-author-link,
#cycle-analytics .empty-stage h4,
#cycle-analytics .no-access-stage h4 {
    color: #eee;
}
#cycle-analytics .stage-event-item .issue-date,
#cycle-analytics .stage-event-item .build-date,
#cycle-analytics .stage-event-item .total-time {
    color: #aaa;
}
#cycle-analytics .stage-event-item .total-time span {
    color: #888;
}

body.ui-dark .navbar-gitlab .nav > li.active > a:hover svg,
body.ui-dark .navbar-gitlab .participants-more .nav > li.active > .btn-link:hover svg,
.participants-more body.ui-dark .navbar-gitlab .nav > li.active > .btn-link:hover svg,
body.ui-dark .navbar-gitlab .user-list-more .nav > li.active > .btn-link:hover svg,
.user-list-more body.ui-dark .navbar-gitlab .nav > li.active > .btn-link:hover svg,
body.ui-dark .navbar-gitlab .nav > li.dropdown.show > a:hover svg,
body.ui-dark .navbar-gitlab .participants-more .nav > li.dropdown.show > .btn-link:hover svg,
.participants-more body.ui-dark .navbar-gitlab .nav > li.dropdown.show > .btn-link:hover svg,
body.ui-dark .navbar-gitlab .user-list-more .nav > li.dropdown.show > .btn-link:hover svg,
.user-list-more body.ui-dark .navbar-gitlab .nav > li.dropdown.show > .btn-link:hover svg {
    fill: #eee;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: var(--main-text-color);
}

.btn {
    background-color: var(--lighter-background-1);
    border-color: var(--btn-border);
    color: var(--main-text-color);
}

.btn:hover,
.btn:active,
.btn:focus {
    background-color: var(--btn-background);
    border-color: var(--btn-border);
    color: var(--main-text-color);
}

.btn:hover:active,
.btn:focus:active {
    background-color: var(--btn-background);
    border-color: var(--btn-border);
    box-shadow: inset 0 0 10px 10px rgba(0, 0, 0, .2);
    color: var(--main-text-color);
}

hr {
    border-color: var(--main-border);
}

a {
    color: var(--a-color);
}

a:hover,
a:active,
a:focus {
    color: var(--a-hover-color);
}

.avatar:not([href]):hover {
    border-color: var(--main-border);
}

pre {
    background-color: #24272a;
    color: #e2e2e2;
}

/* Inline code */
code {
    background-color: #24272a;
    color: #e2e2e2;
}

.gfm-color_chip {
    background-color: #414654;
    background-image: linear-gradient(135deg, #414654 25%, transparent 0%, transparent 75%, #414654 0%), linear-gradient(135deg, #414654 25%, transparent 0%, transparent 75%, #414654 0%);
}

/* ===== Nav bar ===== */
.navbar-gitlab {
    border-bottom-color: var(--btn-border);
}

body.ui-indigo .navbar-gitlab {
    background-color: #3e4352;
}

body.ui-indigo .navbar-gitlab .navbar-sub-nav {
    color: #eee;
}

body.ui_indigo .navbar-gitlab .nav > li.active > a,
body.ui_indigo .navbar-gitlab .nav > li.dropdown.open > a {
    background-color: #bda38f;
}

body.ui-indigo .navbar-gitlab .container-fluid .navbar-toggler {
    border-left-color: #6a665f;
}

.frequent-items-dropdown-container .frequent-items-dropdown-sidebar {
    border-color: var(--main-border);
}

.frequent-items-dropdown-container .section-header,
.frequent-items-dropdown-container .frequent-items-list-container li.section-empty {
    color: var(--darker-text-color-3);
}

.frequent-items-list-item-container .frequent-items-item-namespace {
    color: var(--darker-text-color-5);
}

.navbar-gitlab li.dropdown-bold-header {
    color: #efefef;
}

.projects-list-item-container .project-namespace {
    color: #a3a3a3;
}

.header-user.show .dropdown-menu {
    color: var(--main-text-color);
}

body.ui-indigo .search.search-active form {
    background-color: #99928a;
}

.search .search-input-wrap .dropdown-header {
    color: var(--main-text-color);
}

.dropdown-content-faded-mask::after {
    background: linear-gradient(to top, var(--main-background) 45%, rgba(56, 60, 74, 0.4));
}

.search.search-active form .search-input {
    color: var(--lighter-text-color-3);
}

/* ===== Sidebar ===== */
.nav-sidebar {
    background-color: var(--lighter-background-1);
    box-shadow: inset -2px 0 0 #292c37, 2px 1px 3px rgba(0, 0, 0, 0.1);
}

.context-header a {
    color: #eeeeee;
}

.context-header:hover,
.context-header a:hover,
.context-header .participants-more .btn-link:hover,
.participants-more .context-header .btn-link:hover,
.context-header .user-list-more .btn-link:hover,
.user-list-more .context-header .btn-link:hover {
    color: #eeeeee;
}

body.ui_indigo .nav-sidebar li.active {
    box-shadow: inset 4px 0 0 #4096ff;
}

body.ui_indigo .nav-sidebar li.active > a {
    color: #eeeeee;
}

.nav-sidebar li a {
    color: #d3d3d3;
}

.sidebar-sub-level-items > li a:hover,
.sidebar-sub-level-items > li .participants-more .btn-link:hover,
.participants-more .sidebar-sub-level-items > li .btn-link:hover,
.sidebar-sub-level-items > li .user-list-more .btn-link:hover,
.user-list-more .sidebar-sub-level-items > li .btn-link:hover,
.sidebar-sub-level-items > li a:focus {
    color: #eeeeee;
}

.sidebar-top-level-items > li > a:hover,
.participants-more .sidebar-top-level-items > li > .btn-link:hover,
.user-list-more .sidebar-top-level-items > li > .btn-link:hover {
    color: #eeeeee;
}

.nav-icon-container svg {
    fill: #bebebe !important;
}

.nav-icon-container svg:hover {
    fill: #eee !important;
}

.toggle-sidebar-button,
.close-nav-button {
    background-color: var(--lighter-background-1);
    border-color: #4b4844;
    color: var(--main-text-color);
}

.toggle-sidebar-button:hover,
.close-nav-button:hover {
    background-color: #4b4844;
    color: #eeeeee;
}

.nav-sidebar:not(.sidebar-collapsed-desktop) {
    box-shadow: inset -2px 0 0 #3c3936, 2px 1px 3px rgba(0, 0, 0, 0.1);
}

.avatar {
    background-color: var(--main-border);
}

.avatar-circle,
.avatar,
.avatar-container {
    border-color: var(--main-border);
}

.context-header .avatar-container {
    background-color: var(--main-border);
}

/* Number of issues, merge requests etc. */
.sidebar-top-level-items > li .badge.badge-pill,
.sidebar-top-level-items > li .shortcut-mappings .key,
.shortcut-mappings .sidebar-top-level-items > li .key {
    color: #d3d3d3;
}

body.ui-indigo .sidebar-top-level-items > li.active .badge.badge-pill,
body.ui-indigo .sidebar-top-level-items > li.active .shortcut-mappings .key,
.shortcut-mappings body.ui-indigo .sidebar-top-level-items > li.active .key {
    color: #eee;
}

.issuable-row {
    background: var(--lighter-background-1);
    color: #eee;
}

.milestone-content .issuable-row span a {
    color: #ccc;
}

/* ===== Sidebar (GitLab.com, EE 11.0) ===== */
body.ui-indigo .nav-sidebar li.active {
    box-shadow: inset 4px 0 0 #4096ff;
}

body.ui-indigo .nav-sidebar li.active > a {
    color: #eeeeee;
}

.toggle-sidebar-button .collapse-text,
.toggle-sidebar-button .icon-angle-double-left,
.toggle-sidebar-button .icon-angle-double-right {
    color: var(--main-text-color);
}

/* Hover menu */
.sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items {
    background-color: var(--lighter-background-1);
    border-color: var(--btn-border);
}

.sidebar-top-level-items .fly-out-list li {
    background-color: var(--lighter-background-1);
}

.fly-out-top-item {
    background-color: #514d4b;
}

.sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items a:hover,
.sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items .participants-more .btn-link:hover,
.participants-more .sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items .btn-link:hover,
.sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items .user-list-more .btn-link:hover,
.user-list-more .sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items .btn-link:hover,
.sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items a:focus {
    background-color: #55524f;
    color: #eee;
}

.sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items a {
    color: var(--main-text-color);
}

.dropdown-divider,
.divider {
    border-color: #5f5448;
}

/* ===== Project page, showing README ===== */
/* Between top nav bar and file nav */
.breadcrumbs-links {
    color: #a3a3a3;
}

.toggle-mobile-nav + .breadcrumbs-links {
    border-color: var(--light-divider);
}

.breadcrumbs-sub-title a {
    color: #dadada;
}

.breadcrumbs-links .avatar-tile {
    border-color: var(--main-border);
}

.project-home-panel .project-metadata {
    color: #bababa;
}

.project-repo-buttons .btn,
.group-buttons .btn {
    background-color: var(--lighter-background-1);
    border-color: var(--btn-border);
    color: var(--main-text-color);
}

.project-repo-buttons .btn:hover,
.project-repo-buttons .btn:focus,
.group-buttons .btn:hover,
.group-buttons .btn:focus {
    background-color: var(--btn-background);
    border-color: var(--btn-border);
    color: var(--main-text-color);
}

.project-repo-buttons .count-buttons .count-badge-count,
.group-buttons .count-buttons .count-badge-count {
    background-color: var(--main-background);
}

.project-repo-buttons .count-buttons .count-badge-count,
.project-repo-buttons .count-buttons .count-badge-button,
.group-buttons .count-buttons .count-badge-count,
.group-buttons .count-buttons .count-badge-button {
    border-color: var(--btn-border);
}

.project-repo-buttons .count-buttons .count,
.project-repo-buttons .count-buttons .count-badge-button,
.group-buttons .count-buttons .count,
.group-buttons .count-buttons .count-badge-button {
    color: var(--main-text-color);
}

.dropdown-menu-inner-title {
    color: #cbcbcb;
}

.dropdown-menu-inner-content {
    color: #bababa;
}

.git-clone-holder .form-control,
.git-clone-holder .search form,
.search .git-clone-holder form {
    background-color: var(--main-background);
    border-color: var(--btn-border);
    color: var(--main-text-color);
}

.project-stats .nav .stat-text,
.project-stats .nav .stat-link {
    color: #bababa;
}

.project-stats .nav .stat-link:hover,
.project-stats .nav .stat-link:focus {
    color: #eeeeee;
}

.btn-missing,
.project-stats .nav .btn-missing {
    color: var(--main-text-color);
    border-color: var(--btn-border);
}

.btn-missing:hover,
.project-stats .nav .btn-missing:hover,
.btn-missing:active,
.project-stats .nav .btn-missing:active,
.btn-missing:focus,
.project-stats .nav .btn-missing:focus {
    background-color: var(--btn-background);
    color: var(--main-text-color);
}

.project-repo-buttons .btn .fa,
.group-buttons .btn .fa {
    color: var(--main-text-color);
}

.project-repo-buttons .btn svg,
.group-buttons .btn svg {
    fill: var(--main-text-color);
}

.breadcrumbs-container {
    border-color: var(--light-divider);
}

.project-home-panel,
.group-home-panel {
    border-color: var(--light-divider);
}

.project-stats {
    border-color: var(--light-divider);
}

.banner-callout.banner-non-empty-state {
    border-color: var(--light-divider);
}

.banner-callout .banner-close .dismiss-icon {
    color: var(--main-text-color);
}

/* Tree nav */
.dropdown-toggle,
.dropdown-menu-toggle {
    background-color: var(--main-background);
    border-color: var(--btn-border);
    color: var(--main-text-color);
}

.dropdown-toggle:hover,
.dropdown-menu-toggle:hover,
.dropdown-toggle:active,
.dropdown-menu-toggle:active,
.dropdown-toggle:focus,
.dropdown-menu-toggle:focus {
    border-color: #4096ff;
}

.open .dropdown-toggle,
.open .dropdown-menu-toggle,
.open .dropdown-menu-toggle {
    border-color: #4096ff;
}

.dropdown-menu,
.dropdown-menu-nav {
    background-color: var(--main-background);
    border-color: var(--btn-border);
}

.dropdown-menu .dropdown-header,
.dropdown-menu-nav .dropdown-header {
    color: var(--main-text-color);
}

.dropdown-input-field,
.default-dropdown-input {
    background-color: #413a31;
    color: var(--main-text-color);
}

.dropdown-menu-selectable li a.is-indeterminate,
.dropdown-menu-selectable li a.is-active,
.dropdown-menu-selectable li button.is-indeterminate,
.dropdown-menu-selectable li button.is-active {
    color: var(--main-text-color);
}

.dropdown-menu li a:not(.disable-hover):hover,
.dropdown-menu li .participants-more .btn-link:not(.disable-hover):hover,
.participants-more .dropdown-menu li .btn-link:not(.disable-hover):hover,
.dropdown-menu li .user-list-more .btn-link:not(.disable-hover):hover,
.user-list-more .dropdown-menu li .btn-link:not(.disable-hover):hover,
.dropdown-menu li a:active,
.dropdown-menu li a:focus,
.dropdown-menu li a.is-focused,
.dropdown-menu li button:not(.disable-hover):hover,
.dropdown-menu li button:active,
.dropdown-menu li button:focus,
.dropdown-menu li button.is-focused,
.dropdown-menu li .menu-item:not(.disable-hover):hover,
.dropdown-menu li .menu-item:active,
.dropdown-menu li .menu-item:focus,
.dropdown-menu li .menu-item.is-focused,
.dropdown-menu-nav li a:not(.disable-hover):hover,
.dropdown-menu-nav li .participants-more .btn-link:not(.disable-hover):hover,
.participants-more .dropdown-menu-nav li .btn-link:not(.disable-hover):hover,
.dropdown-menu-nav li .user-list-more .btn-link:not(.disable-hover):hover,
.user-list-more .dropdown-menu-nav li .btn-link:not(.disable-hover):hover,
.dropdown-menu-nav li a:active,
.dropdown-menu-nav li a:focus,
.dropdown-menu-nav li a.is-focused,
.dropdown-menu-nav li button:not(.disable-hover):hover,
.dropdown-menu-nav li button:active,
.dropdown-menu-nav li button:focus,
.dropdown-menu-nav li button.is-focused,
.dropdown-menu-nav li .menu-item:not(.disable-hover):hover,
.dropdown-menu-nav li .menu-item:active,
.dropdown-menu-nav li .menu-item:focus,
.dropdown-menu-nav li .menu-item.is-focused {
    background: var(--btn-background) !important;
    color: var(--main-text-color) !important;
}

.breadcrumb.repo-breadcrumb a {
    color: var(--lighter-text-color-1);
}

/* Last commit info */
.info-well {
    background-color: var(--lighter-background-1);
    border-color: #352d28;
    color: var(--main-text-color);
}

.commit a,
.commit button,
.generic_commit_status a,
.generic_commit_status button {
    color: #eeeeee;
}

.commit-sha-group .label-monospace {
    background-color: var(--lighter-background-1);
    border-color: var(--btn-border);
    color: var(--main-text-color);
}

.commit-sha-group .label,
.commit-sha-group .shortcut-mappings .key,
.shortcut-mappings .commit-sha-group .key,
.commit-sha-group .btn {
    border-color: var(--btn-border);
}

/* File navigation */
.table th,
.file-holder table th,
.wiki table th,
.md table th,
ul.notes > li .note-body .note-text table th,
.table td,
.file-holder table td,
.wiki table td,
.md table td,
ul.notes > li .note-body .note-text table td {
    border-color: var(--light-divider);
}

table.table tr th,
.file-holder table tr th,
.wiki table tr th,
.md table tr th,
.ide-file-templates,
ul.notes > li .note-body .note-text table tr th {
    background-color: var(--main-header-color);
}

.tree-holder .tree-table tr {
    border-color: var(--light-divider);
}

.tree-holder .tree-table tr:hover:not(.tree-truncated-warning) td {
    background-color: #433a31;
    border-color: #a2816b;
}

table.table tr td,
.file-holder table tr td,
.wiki table tr td,
.md table tr td,
ul.notes > li .note-body .note-text table tr td {
    border-color: var(--light-divider);
}

.tree-holder .tree-item .tree-item-file-name i,
.tree-holder .tree-item .tree-item-file-name a {
    color: #eeeeee;
}

.tree-holder .tree-commit .tree-commit-link {
    color: #c6c6c6;
}

.tree-holder .tree-time-ago {
    color: #c6c6c6;
}

/* Content of README */
.file-holder {
    border-color: var(--main-border);
}

.file-holder .file-title {
    background-color: var(--main-header-color);
    border-color: var(--main-border);
}

.file-holder .file-title a:not(.btn) {
    color: #f2f2f2;
}

.file-holder .file-content {
    background-color: var(--main-background);
}

.wiki {
    color: var(--main-text-color);
}

.wiki h1,
.wiki h2,
.wiki h3 {
    border-color: var(--main-border);
    color: #f5f5f5;
}

.wiki p {
    color: var(--main-text-color);
}

.wiki a {
    color: var(--a-color);
}

.wiki table {
    color: var(--main-text-color);
}

.table-bordered > thead > tr > th,
.wiki table > thead > tr > th,
.md table > thead > tr > th,
ul.notes > li .note-body .note-text table > thead > tr > th,
.table-bordered > thead > tr > td,
.wiki table > thead > tr > td,
.md table > thead > tr > td,
ul.notes > li .note-body .note-text table > thead > tr > td,
.table-bordered > tbody > tr > th,
.wiki table > tbody > tr > th,
.md table > tbody > tr > th,
ul.notes > li .note-body .note-text table > tbody > tr > th,
.table-bordered > tbody > tr > td,
.wiki table > tbody > tr > td,
.md table > tbody > tr > td,
ul.notes > li .note-body .note-text table > tbody > tr > td,
.table-bordered > tfoot > tr > th,
.wiki table > tfoot > tr > th,
.md table > tfoot > tr > th,
ul.notes > li .note-body .note-text table > tfoot > tr > th,
.table-bordered > tfoot > tr > td,
.wiki table > tfoot > tr > td,
.md table > tfoot > tr > td,
ul.notes > li .note-body .note-text table > tfoot > tr > td {
    border-color: #3f3731;
}

.table-bordered,
.wiki table,
.md table,
ul.notes > li .note-body .note-text table {
    border-color: #3f3731;
}

/* ===== Find file ===== */
.table-striped tbody tr:nth-of-type(2n+1) {
    background-color: #353947;
}

.tree-holder .tree-table tr.selected td {
    background-color: #333744;
    border-color: #0e437f;
}

/* ===== New issue ===== */
.form-control,
.search form {
    background-color: transparent;
    color: var(--main-text-color);
}

.input-group-prepend .btn,
.input-group-prepend .project-buttons .stat-text,
.project-buttons .input-group-prepend .stat-text,
.input-group-append .btn,
.input-group-append .project-buttons .stat-text,
.project-buttons .input-group-append .stat-text {
    border: 1px #eee solid;
}

.btn,
.project-buttons .stat-text {
    border: 1px #504c4c solid;
}

.search form:hover,
.file-finder-input:hover,
.issuable-search-form:hover,
.search-text-input:hover,
.form-control:hover,
.filtered-search-box:hover,
.search form:hover {
    border-color: #ffa340;
}

.help-block {
    color: #c7c7c7;
}

a:hover,
.participants-more .btn-link:hover,
.user-list-more .btn-link:hover,
a:focus {
    color: #ffa340;
}

/* Description */
.markdown-area {
    background-color: var(--lighter-background-1);
    padding: 20px;
    margin: 10px;
    border: none;
    border-radius: 10px;
    width: calc(100% - 20px);
}

.common-note-form .md-area {
    border-color: #352f28;
    background: #282828;
}

.md {
    color: var(--main-text-color);
}

.md p {
    color: var(--main-text-color);
}

.md h1,
.md h2,
.md h3,
.md h4,
.md h5,
.md h6 {
    border-color: var(--main-border);
    color: #f5f5f5;
}

.note-textarea {
    color: var(--main-text-color);
}

.nav-links {
    border-color: #352f28;
}

.nav-links li a {
