    color: #fff;
}

body.ui_indigo .nav-links li.active a {
    border-color: #4096ff;
}

.comment-toolbar {
    border-color: #352e28;
}

.gfm-project_member.current-user {
    background-color: #4a3b2c;
}

/* Dropdown toggle buttons (assignee, milestone etc.) */
.dropdown-toggle-text.is-default {
    color: #cbcbcb;
}

.dropdown-menu li a,
.dropdown-menu li button,
.dropdown-menu li .menu-item,
.dropdown-menu-nav li a,
.dropdown-menu-nav li button,
.dropdown-menu-nav li .menu-item {
