}

/* Footer */
.row-content-block {
    background-color: var(--lighter-background-1);
    border-color: #352e28;
}

.row-content-block.content-component-block {
    padding: 11px 0;
    background-color: var(--main-background);
}

/* ===== List of issues ===== */
.badge {
    color: #bfbfbf;
}

.nav-links li.active a .badge {
