/* ===== Issue details ===== */
/* Right sidebar */
.right-sidebar {
    background-color: var(--lighter-background-1);
    border-color: var(--btn-border);
}

.right-sidebar .block {
    border-color: #282b35;
}

.right-sidebar .title {
