}

.right-sidebar.right-sidebar-expanded .gutter-toggle {
    border-color: #282b35;
}

.right-sidebar .block .gutter-toggle:hover {
    color: #fff;
}

.right-sidebar.right-sidebar-expanded .no-value,
.right-sidebar.right-sidebar-expanded .btn-secondary-hover-link {
