    background-color: var(--light-divider);
}

/* Main content */
.main {
    background-color: var(--main-background);
}

.doc-nav > ul {
    background-color: var(--lighter-background-1);
}

h1 {
    border-color: rgba(255, 134, 64, .6);
}

table td,
table th {
    border-color: #3f3731;
}

.wiki table tbody {
    background-color: var(--main-background);
}

blockquote {
    background-color: #53473c;
}

.alert-info {
    background-color: #163558;
    border-color: #245890;
}

.alert-warning {
    background-color: #7e4100;
    border-color: #a35a00;
}

.alert-success {
    background-color: #083a1b;
    border-color: #0e6c31;
}

.main .edit-on {
    border-color: var(--light-divider);
}

.comments {
    border-color: var(--light-divider);
}

h2 code,
h3 code,
h4 code,
h5 code,
h6 code {
    background-color: #424242;
    color: #e2e2e2;
}

p code,
li code,
table code,
body.ui-dark .search.search-active form {
    background-color: #424242;
    color: #e2e2e2;
}

a:hover,
.participants-more .btn-link:hover,
.user-list-more .btn-link:hover,
a:focus {
    color: var(--a-hover-color);
}
.wiki:not(.use-csslab),
.wiki:not(.use-csslab) p,
.wiki:not(.use-csslab) h1,
.wiki:not(.use-csslab) h2,
.wiki:not(.use-csslab) h3,
.wiki:not(.use-csslab) h4,
.wiki:not(.use-csslab) h5,
.wiki:not(.use-csslab) h6,
.md:not(.use-csslab) p,
.md:not(.use-csslab) h1,
.md:not(.use-csslab) h2,
.md:not(.use-csslab) h3,
.md:not(.use-csslab) h4,
.md:not(.use-csslab) h5,
.md:not(.use-csslab) h6,
.md:not(.use-csslab) li,
.md:not(.use-csslab) ul,
.project-stats .nav .stat-link .project-stat-value,
.project-buttons .nav .stat-link .project-stat-value {
    color: #e2e2e2;
}
.bordered-box {
    border: none;
    border-radius: 0;
}
.gfm-project_member,
.event-item .event-user-info .author_name a {
    background-color: #d7771e;
    color: #eaeaea !important;
    padding: 2px 5px;
    border-radius: 5px;
}
.tree-holder .tree-table tr:last-of-type,
.tree-holder .tree-table tr th,
.tree-holder .tree-table tr td {
    border-color: var(--lighter-background-2);
}
.block:hover {
    background: var(--lighter-background-2)!important;
}
.tokenized-input,
.pipeline-tab-content {
    background-color: transparent;
}
.dropdown-menu li a:hover,
.dropdown-menu li button:hover,
.dropdown-menu li .menu-item:hover,
.dropdown-menu-nav li a:hover,
.dropdown-menu-nav li button:hover,
.dropdown-menu-nav li .menu-item:hover {
    background: var(--lighter-background-2)!important;
}
#cycle-analytics .stage-nav .stage-nav-item {
    background: var(--lighter-background-1);
    border: none;
}
#cycle-analytics .stage-nav:hover .stage-nav-item:hover {
    background: var(--main-background);
    border: none;
}
#cycle-analytics .stage-events {
    overflow: auto;
}
.network-graph svg {
    background: var(--lighter-background-1);
}
.network-graph svg text {
    fill: var(--main-text-color);
}
.project-network .controls,
.project-network .network-graph {
    background: var(--main-background);
}
.diff-file .file-title::before,
.diff-file .file-title-flex-parent::before {
    content: '';
    position: absolute;
    top: -1px;
    left: -5px;
    width: 10px;
    height: calc(100% + 1px);
    background: var(--a-color);
    pointer-events: none;
    border-radius: 5px;
}
.bg-white {
    background: #444 !important;
}
.notes .system-note .timeline-icon,
.notes .discussion-filter-note .timeline-icon {
    background: #222;
}

.notes .system-note .timeline-icon svg,
.notes .discussion-filter-note .timeline-icon svg {
    fill: #eee;
}
}
