css:
@-moz-document domain("sueddeutsche.de") {

body,
html,
#wrapperbelt {
    color: #ccc;
    background-color: #3c3f41;
}
body[data-iqdselector*="videoartikel"],
body[data-iqdselector*="video_artikel"] {
    background-color: #3c3f41;
}
#wrapper,
.sz-wrapper,
.flyout {
    background-color: #2b2b2b !important;
    border: 1px solid #555555;
}

.sz-wrapper {
    margin-top: 20px;
}
.sz-content,
.css-g6ve13 {
    border: none;
}
.mainnav,
.mainnav.sticky {
    border-bottom: 1px solid #555555;
}
#sitecontent > *,
#sitecontent,
.css-jh0uza,
.css-g6ve13,
.sz-header,
.mainnav,
.teaser-item,
.sz-button--minus.active,
.sz-button--minus:active,
.sz-button--minus:focus,
.sz-button--minus,
.sz-button--plus.active,
.sz-button--plus:active,
.sz-button--plus:focus,
.sz-button--plus,
#wetter1 .table_forecast1,
.basebox.include,
.basebox.sz-module.embed.sz-module--separator,
.homepage .related .basebox.embed,
.tile,
figure,
.article-info-inline,
main
{
    background-color: #2b2b2b !important;
}

.escapism,
.escapism-content,
.sz-page__sidebar .sz-module,
.newsscannermodule--social .newsscannermodule__content-wrapper {
    background-color: #313335;
}
.escapism,
.sz-module--separator-line,
.sz-teaser--separator-line,
.interactiveeditorial .accordion,
.flyout-textlinks-container {
    border-top: 1px solid #555555;
}
.tile,
.sz-page__sidebar .sz-module {
    border: 1px solid #555555;
}
.interactiveeditorial .szpluspages {
    border-bottom: 1px solid #555555;
}
.teaserable-layout {
    border-top: 1px solid #555555;
    border-bottom: 1px solid #555555;
}

.flyout-teaser:not(:last-child) {
    border-right: 1px solid #555555;
}

#header-logo-img,
.ligature,
svg,
.interactiveeditorial .szpluspages a::after,
.sz-department-teaser-area-headline--color-das-rezept a img {
    color: #bb9b2a !important;
    filter: brightness(2) hue-rotate(80deg) contrast(130%);
}

.mainnav .houseofhomepage,
.search .search-label {
    /*filter: brightness(1.3) hue-rotate(180deg) contrast(100%) saturate(80);*/
}
.nav-item-arrow {
    filter: brightness(0.3) hue-rotate(180deg) contrast(100%) saturate(80);
}

.article-sidebar-actions {
    filter: brightness(0.7) contrast(140%);
}

.flexible-teaser,
.sz-panorama-banderole,
.sz-article-ad,
homepage_wrapper .interactiveeditorial::before,
.interactiveeditorial::before,
.homepage .related .basebox.embed,
a[href="https://adventskalender.sueddeutsche.de"],
iframe,
#sz-newspilot,
#regioressortaufmacher,
div[data-id="sz.7.92006"],
.asset-infobox,
.mainnav .newsnotification-counter,
.end-of-article-joboffers,
.basebox.job-offer-module.job-offer-right-column,
.sz-teaser__image-container--l::before,
.basebox.sz-module.embed.sz-module--separator-line,
.ad,
.ads,
.basebox.sz-module.embed.sz-module--separator,
.css-2tvoun-aside
{
    display: none !important;
}

h3,
.sz-article__header,
.article > .header h1,
.article > .header h2,
.more-on-the-subject-headline,
.newsscanner-end-of-article-header,
.interactiveeditorial .content .description .headline,
.flyout-teaser-title,
.mainnav .nav-item:hover .nav-item-link,
.sz-page__header-text,
.datacenter-navigation #swipe-active,
.newsscannermodule__title {
    color: #bb9b2a;
}
.sz-teaser__overline,
.sz-article__byline,
.department-teaser,
.tile-teaser-title,
.article > .header time,
.article > .header strong,
.css-1qh16s7,
.css-129jgu2,
.more-on-the-subject-overline,
.interactiveeditorial .content .ressort,
.article-paging-list-item-active::before,
.flyout-teaser-overline,
.newsscannermodule__overline,
.newsscannermodule__source {
    color: #6a8759 !important;
}

.sz-teaser-label-plus {
    background-color: #ccc;
    padding: 4px;
    margin-bottom: -4px;
}

a,
.sz-header,
.more-on-the-subject-title,
.datacenter-navigation .swiper-slide,
.article.application .body a,
.article .body > p a,
.gallery .body .caption > p,
.gallery .body .caption .text > p,
.btk .body .caption > p,
.btk .body .caption .text > p {
    color: #84aeab;
}

.sz-teaser-label-opinion {
    background-color: #84aeab;
    color: #2b2b2b;
}

.department-teaser a,
.table_forecast1,
#wetter1 .table_forecast1,
.interactiveeditorial .content .description,
.sz-teaser__summary,
.sz-article__body,
.sz-article__intro {
    color: #ccc;
}

.header-products-item--active .nav-item-link,
.header-products-item:hover .nav-item-link {
    color: rgb(136, 136, 136);
}
.sz-teaser__author {
    color: #888;
}
.sz-button--white:focus,
.sz-button--white:hover,
.sz-button--white,
.sz-button--round.active,
.sz-button--round:active,
.sz-button--round:focus,
.sz-button--round,
.sz-button--round:hover,
.article-paging-nav-btn {
    background-color: #6a8759;
    color: #2b2b2b !important;
}

.homepage #sitecontent.mainpage {
    margin-left: 150px;
}
#sitecontent .interactiveeditorial,
.sz-teaser__image--l {
    margin-left: -150px;
}

#wrapper .sz-teaser__image--l-lead-story,
.sz-teaser__image--l-pos-1,
.sz-teaser__breaking-news--lead-story--pos-1 {
    margin-left: -210px;
}

.sz-teaser__breaking-news--lead-story--pos-1 {
    background-color: #bb9b2a;
    color: #2b2b2b;
}

.sz-teaser__breaking-news--lead-story--pos-1 h3.sz-teaser__title {
    color: #2b2b2b;
}

.sz-department-teaser-area-headline::after {
    background-color: #bb9b2a;
}

.sz-link--arrowed,
[data-class="abd-arrowedlinklike-button"],
[data-class="layer-szpluscontent__arrow-link"],
.interactiveeditorial .szpluspages a,
.sz-page__header {
    border-bottom: 2px solid #6a8759
}

div.place7,
div.place8,
div.place9,
div.place10,
div.place11,
div.place12,
div.place13,
div.place14,
div.place15 {
    background-color: #fff;
}
.article-sidebar-action--text-mailTo {
    color: #888;
    border-bottom: 1px solid #888;
}
.article > .body > ul li,
.article .liveticker > ul li,
.teaser .oneliner li {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAKCAIAAADzWwNnAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4gwPFSk5C2WbBgAAABRJREFUCNdjbDMzY0ACTAyoYKD5AJyMAQYsOXJNAAAAAElFTkSuQmCC');
}

.sz-teaser-label-exklusiv {
    background-color: #863636;
}
.sz-teaser-label-exklusiv {
    color: #2b2b2b;
}

#article-app-container article {
    color: #ccc;
}

#article-app-container header {
    color: #bb9b2a;
}

#article-app-container header time {
    color: #6a8759;
}

#article-app-container header h2 span:first-child {
    color: #6a8759;
}


@media only screen and (max-width: 500px) {
    .homepage #sitecontent.mainpage {
        margin-left: 5px;
    }
    #sitecontent .interactiveeditorial {
        margin-left: 5px;
    }

    .sz-teaser__image--l-lead-story,
    .sz-teaser__image--l-pos-1,
    .sz-teaser__breaking-news--lead-story--pos-1 {
        margin-left: 5px;
    }
}
}
