css:
@-moz-document domain('blog.fefe.de')
{
html {
    background-color: #3c3f41;
    color: #84aeab;
    font-family: Mono;
    font-size: 1em;
}


/*
* {
    font-size: 13px;
}
*/

body {
    max-width: 720px;
    margin: 0 auto;
    background-color: #2b2b2b;
    padding: 25px;
    border: 1px solid #555555;
    margin-top: 10px;
    line-height: 1.7;
    word-break: break-word;
}

a {
    color: #cc7832;
}

h2 a {
    color: #bb9b2a !important;
}


h3 {
    color: #6a8759;
}

ul {
    list-style: none;
    color:#ccc;
}

li:before {
    content: "– ";
    /* Insert content that looks like bullets */
    margin-left: -1em;
    color: #84aeab;
    /* Or a color you prefer */
    font-weight: bold;
}

@media only screen and (max-width: 500px) {
  body {
    padding:10px;
  }
  ul {
    padding-left: 25px;
  }
}
}
