css:
@-moz-document domain("nexusmods.com") {
    ul.modtabs {
        display: flex;
        margin-bottom: -20px;
        width: 100px;
    }
    ul.modtabs li span.alert {
        display: inline-flex;
        position: relative;
        padding: 0px;
        top: 0px;
        margin: 0px;
        margin-bottom: 6px;
        background-color: inherit;
        font-size: 9px;
    }
    .tabs {
        background-color: #2d2d2d;
    }
    .mobile-tabs {
        display: none;
    }
}
