css:
@-moz-document domain("gameflare.com") {
body, 
a.navbar-brand,
ul.nav li a,
#footer, 
#navbar, 
#sidebar, 
#owl-related-games,
.comment-content,
.games-cells .small-box-games .game-cell, 
.game-details,
.game-owl,
.main, 
.owl-controls.clickable,
.sub-comment .reply,
.user-activity-box .comment-box,
.user-activity-box .comment-box:nth-child(even)
{ 
    background-color: #1c1c1c
}

.pagination > li > a,
.pagination > li > span
{
    background-color: #2c2c2c; border-color: #333
}

.pagination > .disabled > span,
.pagination > .disabled > span:hover,
.pagination > .disabled > span:focus,
.pagination > .disabled > a,
.pagination > .disabled > a:hover,
.pagination > .disabled >a:focus
{
    background-color: #2c2c2c; border-color: #333; color: #666
}
.user-activity-box .comment-box .text,
.user-activity-box .comment-box:nth-child(even) .text   { background-color: #2c2c2c; border-color: #343434; border-radius: 5px }

.btn-default                            { background: #ddd linear-gradient(0deg, #aaa, #eee); border-color: #ccc }
.comment-avatar>img                     { border-radius: 5px }

@media (min-width: 768px)
{
    .navbar-nav>li>div>a:hover, .navbar-nav>li>div>a:focus, .navbar-nav>li>div .active, .navbar-nav>li>h1>a:hover, .navbar-nav>li>h1>a:focus, .navbar-nav>li>h1 .active 
    {
        background-color: #2c2c2c; border-bottom: 1px solid #3c3c3c
    }
}

.table-hover > tbody > tr:hover,
#forum .forum_menu,
#forum .forum_menu li a,
#forum .forum_menu li:hover a, 
#forum .forum_menu li:last-child a      { background-color: inherit; border: 0 }
#forum th, #forum td                    { border-color: #444 }
#forum .forum_menu li a::after          { border-left-color: #444 }
}
