css:
@-moz-document url-prefix("http://chrome-extension://") {
body, .CodeMirror, button, .CodeMirror-gutters, #help-popup {
    background: #000!important;
}
* {
    color: #fff!important;
}
.CodeMirror-activeline-background, .toggle_ball, .toggle_stick {
    background: #006fff!important;
}
.CodeMirror {
    border: solid #fff 1px!important;
}
input, select, option {
    color: #000!important;
}
#installed::after {
    display: none!important;
}
#help-popup .close-icon {
    background: linear-gradient(-45deg, transparent 5px, red 5px, red 6px, transparent 6.5px) no-repeat, linear-gradient(45deg, transparent 5px, red 5px, red 6px, transparent 6.5px) no-repeat;
}
}
