@-moz-document domain("wolfquest.org") {
/* Customizable online indicator/banner. Remove this section if undesired.
	Future feature: some values may not work until the next style revision has been implemented.
	Choose ONE flavor by changing the URL to any of the six banners provided:
	Capitalised = https://i.imgur.com/Bu7cTSQ.gif (dark) | https://i.imgur.com/BYzw8Td.gif (light)
	Smallcaps = https://i.imgur.com/MKF8yYR.gif (dark) | https://i.imgur.com/CQ6z74o.gif (light)
	Themed = https://i.imgur.com/wtgfAFz.gif (dark) | https://i.imgur.com/CZByHvl.gif (light)
*/

.online, .imageset.icon_user_online {
	background-image: url("https://i.imgur.com/wtgfAFz.gif") !important;
}
}

@-moz-document domain("wolfquest.org") {
/* Classic span enforcement. 
	Remove this section if undesired. */

.text-strong {
	font-weight: bold;
	font-family: verdana;
	font-size: 11px;
	color: white !important;
}

.text-italics {
	font-style: italic;
	font-family: verdana;
	font-size: 11px;
	color: white !important;
}

.postbody span {
	font-family: verdana;
	font-size: 11px;
	color: white;
        font-weight: normal;
}
}

